INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(39656, 'deDE', 'Wolfsrudel im Visier', 'Feuert die handliche Kleintierkanone in der Nähe von Wolfsrudeln ab, damit sie auseinanderlaufen. Dadurch lassen sich die Anführer isolieren.', 'Es ist an der Zeit, das Rudel anzugreifen! Ich habe die Kanone schon vorgeladen. Jetzt müsst Ihr nur noch zielen und schießen.\n\nDie Kleintiere dürften die Explosion überleben und davonlaufen. Die Heuler werden von den Kleintieren abgelenkt, und ihr habt dann freies Schussfeld auf die Anführer des Rudels.\n\nWeidmannsheil!', '', '', '', '', '', '', 22522),
(40000, 'deDE', 'Wahre Jäger', 'Stellt Euer Jägertalent unter Beweis, indem Ihr Knochenschnabelfalken auf der Sonnenfrostlichtung erlegt.', 'Ich weiß nicht, warum Hemet mit Razik auf die Jagd geht. Seine Gerätschaften sind im besten Falle unzuverlässig!\n\nWahre Jäger brauchen keine solchen Spielereien für die Jagd. Sie verlassen sich auf Instinkt, Fertigkeiten und Agilität.\n\nSagt, $n, habt Ihr die Fähigkeiten eines wahren Jägers? Dann beweist es.\n\nErlegt ein paar Knochenschnabelfalken, die im nahegelegenen Becken ihre Kreise ziehen.', '', '', '', '', '', '', 22522),
(39670, 'deDE', 'Kleintierstreuschuss', 'Fangt Hasen auf der Sonnenfrostlichtung.', 'Die Heuler dieser Hügel ziehen in Rudeln umher. Drei gegen einen ist kein Kampf, den ich anfangen würde.\n\nGlücklicherweise sind diese Wölfe meinen Ingenieursfertigkeiten nicht gewachsen! Ich habe gerade dem Prototyp einer neuen Vorrichtung den letzten Schliff verliehen, die diese Rudel auseinandertreiben wird. Alles, was ich jetzt brauche, ist Munition...\n\nFangt mir ein paar Hasen auf der Lichtung südlich von hier und ich werde Euch gestatten, $gder Erste:die Erste; zu sein, $gder:die; die Kanone ausprobieren darf.', '', '', '', '', '', '', 22522),
(39781, 'deDE', 'Neltharions Hort', 'Beschafft den Hammer von Khaz''goroth zurück.', 'Ich werde meinen Leuten befehlen, einen Weg durch diese Trümmer zu ebnen und Dargruls Truppen direkt anzugreifen.$b$bWir werden eine List anwenden.$b$BNavarrogg hat einen vielversprechenden Plan vorgeschlagen. Er kennt einen Seiteneingang, der direkt zu Dargrul führen könnte.$b$bSammelt all Eure Verbündeten zusammen und greift die Feinde durch den Seiteneingang an. Stellt den Hammer von Khaz''goroth sicher, bevor dieser Schurke den Berg einstürzen lässt.$b$bWir werden für Ablenkung sorgen, damit Ihr ihn überraschen könnt.', '', 'Er trägt den gestohlenen Hammer von Khaz''goroth.', 'Dargrul der Unterkönig', '', '', '', 22522),
(39780, 'deDE', 'Der Unterkönig', 'Greift Dargrul den Unterkönig an.', 'Mayla ist dem Unterkönig bereits bis in dessen Höhle nachgejagt. Wir sagen unseren Kindern immer, dass sie das keinesfalls tun dürfen.$b$bLeichtsinniges Kind, aber hey, wer will schon ewig leben! Schließen wir uns ihr im Kampf an.', '', '', '', '', '', '', 22522),
(40594, 'deDE', 'Gerechtigkeit von oben', 'Bombardiert die Streitkräfte des Tiefgesteinsrisses.', 'Tötet jetzt, trauert später!$b$bHier, Champion. Fliegt auf einem meiner Kriegsadler und nutzt die Kräfte, die Euch unsere vereinten Stämme gewährt haben, um Vernichtung auf die Drogbar herabregnen zu lassen.$b$bStopfen wir den neuen Eingang zu ihrem Hort mit ihren eigenen Leichen!', '', '', '', '', '', '', 22522),
(42512, 'deDE', 'Verteidigung des Hochbergs', 'Besiegt Dargrul.', 'Das muss genügen. Dargrul ist eingetroffen und wir benötigen jeden verfügbaren Tauglichen an der Frontlinie. Geht Ihr voran, ich werde dichtauf folgen.', '', '', '', '', '', '', 22522),
(42088, 'deDE', 'Evakuierung der Schneemähnen', 'Evakuiert Rotschopf Himmelshaar, Tarvim Tageswandler und den Alten Nefu.', 'Es hört einfach nicht auf!$b$bJetzt berichten mir meine Späher, dass einige Drogbar sich an unserer Flanke vorbeigeschlichen haben und das Dorf der Schneemähnen angreifen.$b$bGeht und helft bei der Evakuierung. Wir werden hier standhalten und versuchen, Euch mehr Zeit zu verschaffen.', '', '', '', '', '', '', 22522),
(39862, 'deDE', 'Der Belagerungsbrul', 'Tötet Belagerungsbrul Olgrul.', 'Würmer mögen weder Kämpfe noch die Oberfläche. Sie begeben sich nur dorthin, wohin ihr Meister es ihnen befiehlt.$b$bWenn man sich also im Krieg befindet, sollte man zuerst den Meister töten.$b$bBelagerungsbrul Olgrul ist der Wurmmeister der Machtsteindrogbar, die uns vom Norden her angreifen.$b$bTötet ihn.', '', 'Der Machtsteinanführer', 'Belagerungsbrul Olgrul', '', '', '', 22522),
(39777, 'deDE', 'Verschafft uns Zeit', 'Tötet $1oa Machtsteindrogbar.', 'Wir brauchen Zeit, um das Dorf der Schneemähnen zu evakuieren. Könnt Ihr den Machtsteindrogbar entgegentreten und ihre Anzahl drastisch reduzieren?$b$bIch werde meine Heiler anweisen, jeden, der nicht kampffähig ist, in Sicherheit zu bringen.', '', '', '', '', '', '', 22522),
(39776, 'deDE', 'Kampfwürmer', 'Tötet Naxa, Arxas und Xist.', 'Das Dorf der Schneemähnen hinter uns wurde vom Ausmaß dieses Angriffs völlig überrumpelt. Ich werde versuchen, dem Angriff der Drogbar hier standzuhalten, um den Zivilisten genug Zeit für die Evakuierung zu verschaffen.$b$bDie Drogbar benutzen Würmer, die ihnen einen Weg zur Oberfläche graben, damit sie dann ihre Truppen in den Einsatz schicken können.$b$bIch übertrage Euch das Kommando über unsere Streitkräfte. Setzt sie ein, um diese Würmer zu töten, und verhindert so, dass der Feind Verstärkung erhält.', '', '', '', '', '', '', 22522),
(38915, 'deDE', 'Die Schlacht um die Schneeblinde Hochebene', 'Reist zur Schneeblinden Hochebene.', 'Ihr kommt gerade zur rechten Zeit. Unsere Streitkräfte in der Schneeblinden Hochebene melden, dass sie den Unterkönig an der Oberfläche gesehen haben.$b$bDarüber hinaus ist er wohl nur leicht bewacht und trägt den Hammer bei sich. Dank Eurer Bemühungen können wir ihm nun im Kampf entgegentreten.$b$bTrefft Euch mit uns an der Schneeblinden Hochebene und lasst uns Dargrul zusammen bekämpfen.', '', '', '', '', '', 'Sprecht mit Mayla Hochberg auf der Schneeblinden Hochebene.', 22522),
(39438, 'deDE', 'Guhruhlruhlruh', 'Bezwingt Guhruhlruhlruh.', 'Üblicherweise sind die Murlocs uns gegenüber eher scheu, aber irgendein größerer von ihnen hat sie in unsere Höhle geführt und jetzt werden sie übermütig.\n\nTötet ihren Anführer. Ich glaube, sein Name lautet "Guhruhlruhlruh"... oder so ähnlich.', '', 'Ruhrul gulul gruhhln! Lulhulghnnn!', 'Guhruhlruhlruh', '', '', '', 22522),
(39437, 'deDE', 'Tief in der Höhle', 'Tötet $1oa Murlocs in der Dunkelsteingrotte.', 'Diese Murlocs werden uns lästig. Sie huschen durch diese Höhlen, als würden sie ihnen gehören. Sie haben sogar ein paar unserer wertvollsten Relikte gestohlen und sie mit ihren fischigen Flossen betatscht.\n\nTötet ein paar, damit ihnen klar wird, dass sie hier nicht das Sagen haben.', '', '', '', '', '', '', 22522),
(39439, 'deDE', 'Dunkelsteinrelikte', 'Bringt die Ketten des Widerstandes, das Rad der Seelenstärke und die Kursrute zurück.', 'Den Murlocs ist es gelungen, einige unserer heiligen Relikte zu stehlen und sie in die tieferen Bereiche der Höhle zu verschleppen.\n\nGlaubt Ihr, dass Ihr sie aufspüren könnt, während wir uns aufwärmen?', '', '', '', '', '', '', 22522),
(39440, 'deDE', 'Stemmst du, Brul?', 'Tragt die Hanteln zur Statue von Muckibrul und kehrt zu Damrul dem Schtarken zurück.', 'Habt Ihr Muckis? Wenn ja, dann könnt Ihr diese Hanteln zur Statue von Muckibrul und wieder zurück tragen. Wenn Ihr das schafft, reden wir.', '', '', '', '', '', '', 22522),
(39456, 'deDE', 'Unerwartete Verbündete', 'Sprecht mit Mayla Hochberg in Donnertotem.', 'Ihr habt mein Vertrauen erlangt. Nicht alle Drogbar sind so gewalttätig wie die Wilden, die Dargrul der Unterkönig anführt, genau wie nicht alle Tauren so blutrünstig sind wie die Bluttotems.\n\nIhr könnt Mayla Hochberg berichten, dass sie unsere volle Unterstützung genießt. Die Höhle, die wir unser Heim nennen, steht Euch ebenfalls offen, $n.', '', '', '', '', '', '', 22522),
(40229, 'deDE', 'Ein Schritt in die Dunkelheit', 'Sprecht mit Navarrogg an der Dunkelsteingrotte.', 'Nachdem die Gefahr für mein Volk gebannt ist, möchte ich Euch meiner Sippe vorstellen.\n\nTrefft mich an der Dunkelsteingrotte. Dann werde ich Euch zeigen, wie wir leben.', '', '', '', '', '', '', 22522),
(39426, 'deDE', 'Blutschuld', 'Bezwingt Torok Bluttotem an Toroks Fels.', 'Ihr habt Euch als ein besserer Verbündeter erwiesen, als es die Bluttotems je waren. Durch meinen Fokuskristall sind meine Wunden verheilt, doch ich fürchte, für Splitt war es bereits zu spät.\n\nTorok und seine Bluttotems werden für das leiden, was sie uns angetan haben. Lasst uns ihnen einen Besuch abstatten, bevor sie einen Angriff gegen die Höhle meines Volks starten können.', '', '', 'Torok Bluttotem', '', '', '', 22522),
(42373, 'deDE', 'Rache für die Dunkelsteine', '', '', '', '', '', '', '', '', 22522),
(39425, 'deDE', 'Dunkelsteinkristall', 'Findet Navarroggs Dunkelsteinfokus.', 'Ohne meinen Kristallfokus sind meine Kräfte nur schwach. Die, die mich gefangen nahmen, haben ihn mir abgenommen, und ich weiß, wo er sich jetzt befindet.\n\nIch bin mir sicher, dass einer der Teufelstotems ihn hat. Wenn Ihr ihn finden solltet, bringt ihn bitte zu mir zurück.', '', '', '', '', '', '', 22522),
(39588, 'deDE', 'Sie werden mit Blut bezahlen', 'Tötet $1oa Tauren der Teufelstotems.', 'Die Tauren gehen zu weit. Ich bin nicht aus ihrem Außenposten geflohen, da ich befürchtete, sie würden die Höhle angreifen und meinen Stamm töten.\n\nEs sieht so aus, als hätten sie das die ganze Zeit geplant. Tötet einige der Teufelstotems. Dünnt die Herde aus.', '', '', '', '', '', '', 22522),
(39391, 'deDE', 'Des Drogbars bester Freund', 'Findet Navarroggs Haustiere.', 'Ich ziehe Kreaturen der Erde heran, um mich selbst und mein Volk zu beschützen. Als ich gefangen genommen wurde, befahl ich ihnen, zu Haus zu bleiben. Doch sie sind oft ungehorsam und haben wahrscheinlich versucht, mir zu folgen.\n\nWenn das der Fall ist, fürchte ich, könnten die Bluttotems sie eingefangen haben – oder schlimmeres. Ich wäre Euch sehr dankbar, wenn Ihr sie finden könntet.', '', '', '', '', '', '', 22522),
(39381, 'deDE', 'Den Stein ins Trollen bringen', 'Sprecht mit Navarrogg, um ihn an einem sicheren Ort zu treffen.', 'Wir sollten diesen Ort verlassen, so lang wir noch Gelegenheit dazu haben. Die Bluttotems versammeln sich bei ihrem Dorf im Norden und ich glaube, dass sie beabsichtigen, meinen Stamm auszurotten.\n\nLasst mich wissen, wann Ihr zum Aufbruch bereit seid, und ich werde uns schnell an einen sicheren Ort bringen.', '', '', '', '', '', '', 22522),
(39860, 'deDE', 'Ritus des Blutes', 'Schließt den Ritus des Blutes ab.', 'Oakin Eisenstier bürgt für Euch. Er ist brauchbar, für eine Flussmähne. Ihr habt auch einen Beweis Eurer eigenen Stärke mitgebracht, auch wenn er etwas dürftig ist.\n\nBevor wir Euch freies Geleit durch unser Territorium gestatten, müsst Ihr den Ritus des Blutes abschließen, den Ihr bereits begonnen habt. Wenn Ihr erfolgreich seid – was ich bezweifle –, dann sprecht wieder mit mir.\n\nBetretet die Tiefen der Höhle. Bezwingt jeden Gegner, der sich Euch entgegenstellen mag, und kehrt dann zu mir zurück.', '', '', '', '', '', '', 22522),
(39455, 'deDE', 'Die Höhle der Blutprüfung', 'Platziert den Leichnam der Hexe des Waldes auf dem Altar und bezwingt einen Kriegstreiber im Einzelkampf.', 'Alle Bluttotems und ihre geehrten Gäste müssen eine Prüfung des Blutes bestehen, die in der Höhle unter diesem Außenposten stattfindet.\n\nIch hoffe, dass ihre Einwilligung, Euch daran teilnehmen zu lassen, ein gutes Zeichen ist. Ihr werdet einen Beweis Eurer Taten erbringen, einen Kriegstreiber im Einzelkampf bezwingen und dann ein Opfer an einem Altar tief in der Höhle erbringen müssen.', '', '', '', '', '', '', 22522),
(39374, 'deDE', 'Eine Audienz bei Torok', 'Sprecht mit Torok Bluttotem.', 'Ich sollte Euch warnen, dass Torok sehr schnell verärgert ist und Fremde nicht ausstehen kann. Seid auf der Hut.\n\nWir werden eine Audienz in seinem Zelt am Klippenrand ersuchen. Als Abgesandter der Flussmähnen hier befinde ich mich in relativer Sicherheit, doch Euch gegenüber haben sie keinerlei solche Versprechungen gemacht.', '', '', '', '', '', '', 22522),
(39873, 'deDE', 'Mir ist nicht wohl bei der Sache', 'Untersucht Navarroggs finstere Vermutungen.', 'Ihr habt guten Grund, mir nicht zu vertrauen. Meine von Dargrul angeführten Cousins wurden von Machtgelüsten in den Wahnsinn getrieben.\n\nAber ich möchte, dass Ihr ein paar Nachforschungen anstellt. Ich habe das Gefühl, dass Hochberg ein schreckliches Unheil droht und Torok und sein Bluttotemstamm dafür verantwortlich sind.\n\nWenn Ihr mir nicht glaubt, dann überzeugt Euch an der Nordseite des Klippenrands selbst.', '', '', '', '', '', '', 22522),
(39373, 'deDE', 'Federn rupfen', 'Sammelt $1oa Greisenfedern von Hexenwaldgreisinnen im Hexenwald.', 'Die Drogbar, aus deren Gewalt Ihr mich befreit habt, waren ungewöhnlich aggressiv. Der Stamm aus den Höhlen hier in der Nähe scheint vom Rest getrennt zu leben, aber das hielt die Bluttotems nicht davon ab, eine präventive Offensive zu starten und einige von ihnen gefangen zu nehmen.\n\nDurch diese Entscheidung blieb der Wald jedoch unbewacht und stand für den Einfall der Harpyien offen. Eure Hilfe beim Dezimieren dieser Hexen würde von Torok nicht unbeachtet bleiben. Bringt einige Federn zurück, um ihren Tod zu beweisen.', '', '', '', '', '', '', 22522),
(39372, 'deDE', 'Hexe des Waldes', 'Bezwingt die Hexe des Waldes.', 'Ihr habt mich vor Kurzem an der Flusswindung gerettet. Das ist der Grund, warum ich Eure Rolle als Abgesandter bei diesem Stamm unterstützte, und auch der einzige Grund, warum Ihr jetzt noch am Leben seid. Ich betrachte damit meine Schuld Euch gegenüber als beglichen.\n\nIhr müsst Euch dem Häuptling der Bluttotems gegenüber beweisen, bevor er mit Euch sprechen wird.\n\nIn diesem Wald befindet sich die Hexe des Waldes, eine Harpyienmutter, die sich erst kürzlich in unserem Territorium niedergelassen hat. Wenn Ihr in der Lage seid, sie zu bezwingen, und ihren Leichnam den Bluttotems präsentiert, gestatten sie Euch vielleicht eine Audienz bei Torok.', '', '', 'Hexe des Waldes', '', '', '', 22522),
(40230, 'deDE', 'Ein Klau-kühnes Unterfangen', 'Verschmelzt Eure Gedanken mit denen von Murky und besiegt Klaukühn.', 'Klaukühn, der Anführer der Höhlenmoosmakrura, will das Höhlenmoosdorf überfallen, um sich den Nachwuchs als Mahlzeit einzuverleiben.\n\nDas dürfen wir nicht zulassen. Murky und seine Kaulquappen werden ihre Stärke beweisen und ihn vernichten!\n\nDas ist eine Mission für Murlocs.\n\nVerschmelzt Eure Gedanken mit denen von Murky in seinem Ei und zeigt ihm, wie es gemacht wird!', '', 'Der kühnste Makrura der Küstengewässer.', 'Klaukühn', '', '', '', 22522),
(40102, 'deDE', 'Murlocs: Die nächste Generation', 'Verschmelzt Eure Gedanken mit denen von Murky und tötet $2oa Höhlenmoosklacker.', 'Jetzt ist es soweit! Murky und seine Anhänger benötigen einen geheimen Ort, an dem sie ohne Wissen der älteren Murlocs trainieren können, um zukünftig das Kommando zu übernehmen.\n\nEs gibt da eine schöne Höhle nicht weit von hier... Das Problem sind nur die vielen Makrura, die dort hausen.\n\nDie Höhle wäre eine gute Basis für die Kaulquappen. Murky wird ihnen zeigen, wo der Murloc die Locken hat!\n\nVerschmelzt Eure Gedanken mit denen von Murky in seinem Ei und zeigt ihm, wie es gemacht wird!', '', '', '', '', '', '', 22522),
(40049, 'deDE', 'Schleimzeit', 'Füttert Murky mit $1oa Bündeln Schleimgras, indem Ihr durch sie hindurchschwimmt.', 'Murky wächst schnell heran und muss tüchtig essen! Und was gibt es Besseres als Schleimgras für einen jungen Murloc?\n\nIn den Murlocgewässern hier findet Ihr jede Menge davon! Ihr müsst nur hindurchschwimmen und Murky damit füttern!', '', '', '', '', '', '', 22522),
(40047, 'deDE', 'Kugelrund fressen', 'Sammelt $1oa Portionen Kugelfischlaich ein.', 'Murky ist mein absoluter Liebling. Um aber eine Veränderung in der hiesigen Murlocpopulation zu bewirken, benötigt er besondere Fähigkeiten.\n\nIn diesen Gewässern gibt es eine Menge Kugelfische und ich glaube, dass sie ein gutes Mittel für ihn darstellen werden.\n\nTötet ein paar von ihnen und sammelt ihre Eier für Murky ein.', '', '', '', '', '', '', 22522),
(40045, 'deDE', 'Sozialisierung der Kaulquappen', 'Sprecht mit $1oa Kaulquappen der Sumpfsteine.', 'Die Murlocs liegen mir viel zu sehr am Herzen, als dass ich Euch auftragen würde, alle erwachsenen Murlocs einfach umzubringen.\n\nStattdessen sollten wir die nächste Generation dazu bringen, etwas friedlicher zu sein. Murky ist dafür das beste Beispiel.\n\nBringt ihn dazu, mit den Murlockaulquappen zu sprechen und sie zu unseren Verbündeten zu machen.', '', '', '', '', '', '', 22522),
(40244, 'deDE', 'Ein verdächtiges Kostüm', 'Sprecht mit Tamara und fliegt zur Schiffbruchgrotte.', 'Als wir auf Jagd an der Küste waren, ist uns ein komischer Typ im Kostüm über den Weg gelaufen. Er sagte, er wolle sich so unter die Murlocs mischen... aber ich glaube, er hat nicht mehr alle Tassen im Schrank.\n\nAls wir uns auf den Weg zurück ins Lager gemacht haben, ist jemand zurückgeblieben, um ihn im Auge zu behalten. Könntet Ihr dort mal nachsehen?\n\nTamara da drüben kann Euch hinbringen.', '', '', '', '', '', '', 22522),
(39386, 'deDE', 'Ein Prototyp muss her', 'Sucht Razik Gazbolz am Eisfichtengrat auf.', 'Wenn Addie ein ständiges Mitglied der Jagdgruppe werden soll, dann braucht sie spezielle Ausrüstung für jemanden ihrer Größe.\n\nMein Munitionsexperte sollte ein paar Vorschläge haben, welche Waffen am besten für einen Gnom geeignet sind.\n\nSchaut bei seinem Lager vorbei und sagt ihm, dass ich bald einen Prototyp benötige.', '', '', '', '', '', '', 22522),
(39773, 'deDE', 'Das Schicksal des Seelenbewahrers', 'Findet Seelenbewahrer Uriah im Schnodderschnauzenbau.', 'Seelenbewahrer Uriah wurde beim Angriff der Kobolde verletzt, bestand aber darauf, noch tiefer in die Höhle vorzudringen, um die gestohlenen Seelensplitter zu bergen. Er befahl mir, mit unseren verbliebenen Vorräten hier zu warten, bis er zurückkehrt, aber ich befürchte das Schlimmste.\n\nBitte findet Uriah. Er ist der Einzige, der weiß, wie man die Dämonen festsetzen kann.', '', '', '', '', '', '', 22522),
(39764, 'deDE', 'Glänzend aber tödlich', 'Sammelt Seelenkammern von Kobolden im Schnodderschnauzenbau.', 'Seelenbewahrer Uriah und ich haben Dämonengefangene zu Allari in Azsuna gebracht, als unsere Karawane von Kobolden angegriffen wurde.\n\nDieses hirnlose Gesindel ist mit den Seelenkammern verschwunden, in denen sich unsere dämonischen Gefangenen befanden.\n\nDie Kristalle glänzen zwar hübsch, aber sie sind auch tödlich. Wenn sie auch nur im Geringsten beschädigt werden, könnten die darin gefangenen Dämonen entkommen.\n\nBegebt Euch in die Höhle, die sich in der Nähe befindet, und bergt die Seelenkammern, bevor es zu spät ist.', '', '', '', '', '', '', 22522),
(43062, 'deDE', 'Weiterführende Ausbildung', 'Verwendet Eure Fähigkeiten ''Knisternder Jadeblitz'', ''Rollen'' und entweder ''Blackout-Tritt'' oder ''Blackout-Schlag'' jeweils einhundert Mal.', 'Ihr mögt $gder Großmeister:die Großmeisterin; sein, aber nur durch Disziplin erhält man die Meisterschaft aufrecht.$B$BUm perfekte Harmonie zu erreichen, müsst Ihr Eure Ausbildung fortsetzen.$B$BIch würde Eure Form gerne bei praktischen Übungen sehen, $n. Geht und benutzt Eure Fähigkeiten ''Knisternder Jadeblitz'', ''Rollen'' und entweder ''Blackout-Tritt'' oder ''Blackout-Schlag''. Die Disziplin führt uns zu wahrer Größe.', '', '', '', '', '', '', 22522),
(39178, 'deDE', 'Elch außer Rand und Band', 'Beschafft das Geweih des verfluchten uralten Geistes Morashu.', 'Ein durchgedrehter Elch, der das Becken terrorisiert, ist das Gesprächsthema in der Jagdhütte.\n\nIch selbst habe das Tier noch nie gesehen, aber man sagt, dass sein Geweih vor Magie leuchtet.\n\nAnders als die weniger fähigen Jäger, die ihm bisher nachgestellt haben, werdet Ihr erfolgreich sein. Bringt mir diese einzigartige Trophäe!', '', '', 'Morashu', '', '', '', 22522),
(40228, 'deDE', 'Ausspähen', 'Tötet Grimmschwinge, nachdem Ihr seine Position mittels einer Leuchtpistole angezeigt habt.', 'Ihr habt bewiesen, dass selbst eine Gnomin mit einem Gewehr umgehen kann – eine beeindruckende Leistung. Aber um Addies Fähigkeiten endgültig unter Beweis zu stellen, muss sie noch ein wahrhaft imposantes Tier erlegen.\n\nGerüchten zufolge soll ein gefräßiger Roc seine Kreise über dem Becken ziehen. Man sagt, er sei durch Harpyienmagie verflucht, aber noch hat ihn keiner aus der Nähe zu Gesicht bekommen.\n\nNehmt diese Leuchtpistole und feuert sie ab, wenn Ihr den Roc gefunden habt. Sobald wir das Signal sehen, wird unsere Jagdgesellschaft Euch zur Hilfe eilen, um diese Bestie zu erlegen.', '', '', 'Grimmschwinge', '', '', '', 22522),
(39867, 'deDE', 'Ich bin doch nicht blind!', 'Erbeutet eine Jagdtrophäe von dem Löwen, den Ellias im Kieferfelsbecken gesehen haben will.', 'Ich hab in meinem Leben schon viele exotische Tiere gejagt. Aber so ein Löwe, wie ich ihn auf meiner Erkundung des Beckens gesehen habe, ist mir noch nie begegnet.\n\nDie bernsteinfarbenen Augen der Bestie haben sich in meine gebohrt und mich geradezu zu ihm hingezogen. Ich war dem Blick des Raubtiers hilflos ausgeliefert. Erst als der Löwe lossprang, löste sich dieser Bann.\n\nIch habe Hemet von dieser magischen Raubkatze erzählt, aber er hat mich nur ausgelacht. Ich lüge nicht, $n! Dieser Löwe existiert.\n\nWenn Ihr die Raubkatze finden und töten könnt und mir eine Trophäe mitbringt, werde ich Euch großzügig entlohnen.', '', '', 'Rashar', '', '', '', 22522),
(39123, 'deDE', 'Löwenpirsch', 'Jagt Kieferfelspirscher im Kieferfelsbecken.', 'Die Hügel sind voller Löwen, die ihre Beute aus der Ferne verfolgen. Die Großkatzen springen alles an, was ihnen ins Auge fällt. $gEin:Eine:r; $R wie Ihr bildet da keine Ausnahme.\n\nDie meisten würden solch einem furchterregenden Raubtier zum Opfer fallen, aber aus meiner Jagdgesellschaft würde jeder mit diesen Biestern fertigwerden.\n\nBeweist, dass Ihr auch das Zeug dazu habt, indem ihr einige dieser Pirscher zur Strecke bringt.', '', '', '', '', '', '', 22522),
(39124, 'deDE', 'Elchjagd', 'Jagt Kieferfelsurhörner im Kieferfelsbecken.', 'Auf dem Hochberg leben Wildtiere, die man nirgendwo anders auf der Welt jagen kann. Sagt mir, $n, ist Euch je zuvor ein Elch unter die Augen gekommen?\n\nDie umherziehenden Urhörner mögen wie sanfte Riesen aussehen, aber nur ein Narr würde ihre Stärke unterschätzen.\n\nSie müssten sogar für jemanden, der so erfahren ist, wie Ihr es seid, eine ziemliche Herausforderung darstellen.', '', '', '', '', '', '', 22522),
(39392, 'deDE', 'Bärenjagd', 'Sammelt $1oa Borstenpelze auf den Feldern von An''she ein.', 'Es heißt, ein einziges Haar vom Rücken eines Borstenpelzbären wäre scharf genug, um damit den Bart eines Zwergs zu schneiden. Doch wenn das Fell Euch nicht tötet, werden die Klauen es tun.\n\nEinige ungeübtere Jäger sind bereits als Bärenhappen geendet.\n\nBeweist mir, dass Ihr mehr als nur eine Zwischenmahlzeit seid und bringt mir ein paar dieser Borstenpelze.', '', '', '', '', '', '', 22522),
(40216, 'deDE', 'Jägerin im Herzen', 'Helft Addie, das Jagen zu lernen, indem Ihr im Kieferfelsbecken auf Beutejagd geht.', 'Addie ist eine überragende Schreiberin, aber ich habe sie noch nie ein Gewehr halten sehen. Sie möchte unbedingt das Jagen lernen, aber hat man schon mal was von Gnomenjägern gehört? \n\nIhr scheint ja gut mit solchen Amateuren auszukommen. Würde es Euch etwas ausmachen, die Gnomin mit auf die Jagd zu nehmen? Von jemandem mit Euren Fähigkeiten kann sie sicher eine Menge lernen.\n\nIhr fangt am besten mit kleinerer Beute an. Wir wollen sie ja nicht gleich entmutigen.', '', '', '', '', '', '', 22522),
(39859, 'deDE', 'Ziegen, die Notizen fressen', 'Beschafft Addies Manuskriptseiten von den vollgefressenen Hügelschreitern im Kieferfelsbecken zurück.', 'Ich bin Hemet auf all seinen Reisen vom Schlingendorntal bis nach Nagrand und... wieder nach Nagrand gefolgt, habe Notizen über seine großen Abenteuer verfasst und sie in zeitlose literarische Werke verwandelt.\n\nAber mein Herz schlägt eigentlich ganz für die Jagd. Nichts wäre mir lieber, als die Schreibfeder niederzulegen und selbst ein Gewehr in die Hand zu nehmen.\n\nHemet hat versprochen, mir das Jagen beizubringen, sobald ich den neuesten Roman fertiggestellt habe – doch die Ziegen hier haben meinen ersten Entwurf gefressen!\n\nEs gibt keine Hoffnung darauf, eine Jägerin zu werden, wenn ich das Manuskript nicht wiederfinde.', '', '', '', '', '', '', 22522),
(40170, 'deDE', 'Die Stunde der Amateure', 'Konfrontiert die Amateurjäger im Kieferfelsbecken.', 'Die Jagdhütte hat eine Menge Amateure angelockt, die sich als echte Jäger ausgeben. Mit ihren Mätzchen vertreiben die uns die ganzen Beutetiere!\n\nIch bin zwar ein Meister am Gewehr, aber in Verhandlungsfragen bin ich nicht ganz so bewandert.\n\nTut uns doch einen Gefallen und sagt diesen Anfängerjägern, dass sie sich verziehen sollen, ja?', '', '', '', '', '', '', 22522),
(42630, 'deDE', 'Bolabombardement', 'Tötet $1oa Angreifer des Zirkels.', 'Ich habe gehört, dass ein Zirkel Harpyien mit ihren Rocs einen groß angelegten Angriff plant.\n\nSie werden von den Bergen im Norden aus herabstoßen.\n\nSchnappt Euch einen der Bolawerfer. Ich nehme den anderen.\n\nSchnell!', '', '', '', '', '', '', 22522),
(40217, 'deDE', 'Befiederte Gabe', 'Bringt den Köcher mit Pfeilen zu Hemet Nesingwary in Nesingwarys Zuflucht.', 'Durch die Täler dieses Gebirges sind schon viele Reisende gezogen, die unseren Traditionen keine Beachtung schenkten.\n\nUnsere Anführer haben wichtigere Dinge zu tun, aber Losro und ich können die kürzliche Ankunft der Jäger im nahegelegenen Becken nicht ignorieren.\n\nSie jagen mit Feuerwaffen und anderen Gerätschaften und verweigern sich der traditionellen Jagd mit Pfeil und Bogen.\n\nWir werden nicht alle überzeugen können, aber vielleicht können wir einigen von ihnen unsere Jagdkultur näherbringen, wenn wir ihnen ein paar Pfeile schenken.', '', '', '', '', '', '', 22522),
(39387, 'deDE', 'Die Himmel des Hochbergs', 'Sprecht mit Lasan Himmelshorn am Fuß von Donnertotem.', 'Trefft mich am Fuß des Donnertotems, sobald Ihr bereit seid.$b$bEs wäre mir eine Ehre, Euch an meiner Seite zu haben, wenn mein Stamm dem Oberhäuptling erneut die Treue schwört.$b$bGemeinsam stehen wir für den Hochberg!', '', '', '', '', '', '', 22522),
(39322, 'deDE', 'Die Hexenkönigin', 'Tötet die Hexenkönigin, die Großschwester der Scharrfurien.', 'Die Zeit ist gekommen, die Großschwester der Scharrfurien, die Hexenkönigin, anzugreifen.$b$BIhr solltet wissen, dass man nicht durch Freundlichkeit zu Königin der Harpyien wird.$b$bDiese Kreatur hat zahllose Tauren, Harpyien und Kobolde getötet, gefoltert und ihnen alles Mögliche angetan, um ihre Position als Königin zu sichern.$b$bKommt, $gFreund:Freundin;, lasst uns das beenden.', '', 'Königin der Harpyien.', 'Großschwester der Scharrfurien', '', '', '', 22522),
(39429, 'deDE', 'Angriff auf die Hexenlande', 'Tötet $1oa Harpyien der Scharrfurien und $2oa Loyalisten der Himmelsschnauzer in den Hexenlanden.', 'Begebt Euch zu Fuß in die Hexenlande und tötet alle Harpyien, die Ihr finden könnt.$b$bMerzt sie aus!$b$bWir werden Euch Luftunterstützung geben und die Beute am Boden halten, wo Ihr sie bezwingen könnt.$b$bZeigt diesen Hexen den Zorn Eures... nun... Eure Wut halt.', '', '', '', '', '', '', 22522),
(39321, 'deDE', 'Die Drei', 'Tötet Ragi die Verhexerin, Agara Todesklage und Ugla die Hexe.', 'Als die Großschwester der Scharrfurien die Kontrolle über die Harpyien übernahm, übergab sie drei mächtigen und tödlichen Hexen den Befehl über die Zirkel unter ihr.$b$bDiese Hexen müssen sterben. Fühlt Ihr Euch dieser Aufgabe gewachsen?$b$bBegebt Euch in die Hexenlande hinter mir und findet diese bösartigen Kreaturen.$b$bWir werden Euch aus der Luft Deckung geben und Eurer Beute keine Chance zur Flucht in den Himmel lassen.$b$bWenn Ihr überleben solltet, findet Ihr uns oben auf dem Berg.', '', '', '', '', '', '', 22522),
(38910, 'deDE', 'Rocs gegen Adler', 'Rettet $1oa gefangene große Adler.', 'Die Großschwester der Scharrfurien hat ihre Hexen gelehrt, wie man unsere geliebten Adler verflucht und in widerwärtige Rocs verwandelt, die das Tal angreifen, wie Ihr selbst sehen könnt.$b$bEs gibt keine Möglichkeit, diesen Fluch aufzuheben, wenn er sich erst einmal festgesetzt hat.$b$bWir werden uns in die Lüfte schwingen und gegen die Rocs kämpfen, aber wir benötigen Eure Hilfe am Boden.$b$bBegebt Euch durch das Dunkelfedertal nach Norden und lasst alle Adler frei, die Ihr finden könnt.$b$bJeder Adler, den Ihr rettet, ist ein Roc weniger, den wir später töten müssen.', '', '', '', '', '', '', 22522),
(39317, 'deDE', 'Belagerung der Scharrfurien', '', '', '', '', '', '', '', '', 22522),
(39318, 'deDE', 'Die Wunden lecken', 'Findet Windmeister Julan und helft, die Feuer auf dem Himmelshorn zu löschen.', 'Ich weiß, weshalb Mayla Euch schickte. Aber wie Ihr gesehen habt, sind die Drogbar nicht die einzige Gefahr am Hochberg.$b$bIch werde keine Truppen zur Unterstützung schicken, bis wir nicht mit den Lakaien der Großschwester der Scharrfurien fertiggeworden sind.$b$bWenn Ihr helfen wollt, sucht meinen Schamanen auf. Er sollte eigentlich die Feuer löschen, die in meinem Dorf wüten.$b$bEr wurde das letzte Mal auf dem Weg zu seinem Geisterhaus im Süden gesehen, kurz bevor die Aschenhexen uns angriffen.', '', '', '', '', '', '', 22522),
(39419, 'deDE', 'Fluch-tpunkt', 'Tötet Fluchweberin Akara.', 'Die Harpiyen kämpfen unnachgiebig um die Vorherrschaft im Becken. Sie bauen ihre magischen Fluchsäcke so schnell, wie wir sie vernichten können.\n\nSo lang die Fluchweberin am Leben ist, werden die Himmelshörner nicht in der Lage sein, die Oberhand zu gewinnen. Sie muss aufgehalten werden.', '', '', 'Fluchweberin Akara', '', '', '', 22522),
(39580, 'deDE', 'Der Oberhäuptling', 'Sprecht mit Mayla Hochberg in Donnertotem.', 'Jetzt verstehe ich, was Ebenhorn mir schon seit einer Weile zu verstehen geben will.$b$bUnsere Leute brauchen keinen Hammer, sondern einen Anführer, der jeder Bedrohung entgegentritt, selbst wenn wir anderen uns fürchten.$b$bUnd... das bleibt zwischen uns... ich habe natürlich Angst, aber ich bin auch entschlossen.$b$bKehren wir zurück nach Donnertotem, um uns auf den Krieg vorzubereiten.', '', '', '', '', '', 'Sprecht mit Mayla Hochberg in Donnertotem.', 22522),
(39579, 'deDE', 'Die Hintertür', 'Flieht aus dem Hort.', 'Es wird etwas dauern, bis Mayla das verarbeitet hat.$b$bWir aber sollten jetzt nach Donnertotem zurückkehren.$b$bGleich südlich von hier gibt es einen Tunnel, der uns durch die alte Sklavenenklave der Drogbar bis zu einer Lichtung dahinter führen wird.$b$bWir unterhalten dort einen kleinen Himmelshornwachposten, um sicherzustellen, dass niemand das Gewölbe ohne unser Wissen betritt oder verlässt. Es sollte ihnen möglich sein, uns zurückzufliegen.', '', '', '', '', '', 'Trefft Euch mit Mayla Hochberg am Obsidianspähposten.', 22522),
(39577, 'deDE', 'Ein uraltes Geheimnis', 'Unterstützt Geistwandler Ebenhorn in Neltharions Hort.', 'Bevor Mayla zum Oberhäuptling erklärt werden kann, muss sie noch eine weitere Vision erhalten. Ich möchte sie auch Euch zeigen.$b$bEs handelt sich um ein uraltes Geheimnis, das nur innerhalb der Blutlinie von Hochberg weitergegeben wurde. Ich bitte Euch, dass Ihr es ebenfalls wahrt.', '', '', '', '', '', '', 22522),
(39578, 'deDE', 'Titanisches Kräftemessen', 'Findet den Geist von Huln Hochberg und helft ihm.', 'Die Geschichte von Huln endete nicht damit, dass er den Hammer von Khaz''goroth in seinen Besitz brachte.$b$bNein. Für diesen Teil müssen wir tiefer in die Höhle eindringen. Dort wartet ein letztes Kohlenbecken auf uns.$b$bKommt, lasst uns sehen, was als Nächstes geschehen ist.', '', '', '', '', '', '', 22522),
(40219, 'deDE', 'Todesschwinge zum Trotz', 'Erlebt 3 Visionen.', 'An diesem Ort hat Huln Hochberg vor vielen, vielen Jahren den großen Todesschwinge herausgefordert.$b$bUnd heute werden wir die großen Momente dieses Tages durch die Augen unserer Vorfahren erneut miterleben dürfen.$b$bFür den nächsten Teil unserer Geistreise müssen wir die drei Kohlenbecken aufsuchen, die vor langer Zeit aufgestellt wurden, damit alle, die Huln nacheifern wollen, seine großen Taten nacherleben können.', '', '', '', '', '', '', 22522),
(39576, 'deDE', 'Echos von Todesschwinge', '', '', '', '', '', '', '', '', 22522),
(39575, 'deDE', 'Der Pfad des Huln', 'Folgt Geistwandler Ebenhorn auf dem Pfad des Huln.', 'Armeen sind für erfolgreiche Schlachten unerlässlich. Symbole jedoch ebenfalls.$b$bFür unser Volk gibt es nur wenige Symbole, die mehr Wert für uns besitzen als der Segen unserer ältesten Vorfahren.$b$bMayla muss auf dem Pfad des Huln wandeln.$b$BDer Pfad ist gefährlich und sie darf nur zwei Begleiter mit sich nehmen: einen Geistwandler, der ihr den Weg weist, und einen Champion, der sie beschützt.$b$bWerdet Ihr als Champion teilnehmen?', '', '', '', '', '', '', 22522),
(42590, 'deDE', 'Familienzusammenführung mit Moozy', 'Sucht Ethel Wasserweise auf und bringt sie zu ihrer Familie in der Wiege des Hammers in Donnertotem.', 'Ihr habt ja keine Vorstellung, wie dankbar wir Euch sind, $R. Dank Euch sind Moozy und mein Mann in Sicherheit. Ich werde die beiden jetzt nicht mehr aus den Augen lassen!\n\nUnd es tut mir leid, aber ich muss Euch um einen letzten Gefallen bitten: Könntet Ihr bitte meine Mutter aufsuchen und sie hierherbringen, damit sie sieht, dass ihr Enkelkind in Sicherheit ist?\n\nIhr Name ist Ethel und sie lebt am nördlichen Rand von Donnertotem.', '', '', '', '', '', '', 22522),
(42596, 'deDE', 'Zusammentrieb der Bergschreiter', 'Sucht $1oa verängstigte Bergschreiter und bringt sie zurück in den Stall von Liza Sturmschreiter.', 'Seid Ihr $gderjenige, von dem:diejenige, von der; ich so viel gehört habe, $gFremder:Fremde;?\n\nAls das Scharmützel mit den Drogbar losging, sind einige der jungen Elche ausgerissen!\n\nIch kann sie nicht verfolgen, weil die anderen sonst vielleicht auch weglaufen.\n\nKönntet Ihr sie vielleicht zu uns zurückbringen?', '', '', '', '', '', '', 22522),
(42622, 'deDE', 'Zeremonielle Trommeln', 'Findet $1oa große, $2oa mittlere und $3oa kleine zeremonielle Trommeln in Donnertotem.', 'Diese närrischen Kälber!\n\nSie haben sich schon wieder mit unseren zeremoniellen Trommeln davongemacht und behandeln sie wie... Spielzeuge, wie... normale Trommeln!\n\nKönntet Ihr mir helfen, sie wieder einzusammeln? Sie haben sie sicher überall in ganz Donnertotem liegen lassen, nachdem sie damit ihren Spaß hatten.', '', '', '', '', '', '', 22522),
(38916, 'deDE', 'Geheimnisse des Hochbergs', 'Trefft Geistwandler Ebenhorn an der südlichen Klippe von Donnertotem.', 'Maylas Vater Ulan war unser letzter Oberhäuptling. Er wurde getötet, als er den Unterkönig davon abhalten wollte, den Hammer von Khaz''goroth zu ergreifen.$b$bIch habe Mayla schon ihr ganzes Leben lang beobachtet und weiß deshalb, dass sie genug Tapferkeit und Stärke besitzt, um die Rolle des Oberhäuptlings zu übernehmen.$b$bWir müssen nur dafür sorgen, dass sie dies selbst erkennt.$b$bWürdet Ihr uns am Stadteingang entlang der südlichen Klippen treffen? Es gibt ein Ritual, das helfen kann, doch um es zu überleben, bräuchten wir Eure Hilfe.', '', '', '', '', '', 'Trefft Geistwandler Ebenhorn an der südlichen Klippe von Donnertotem.', 22522),
(39992, 'deDE', 'Hulns Krieg - Die Nathrezim', 'Besiegt Tichondrius.', 'Ich bin vollauf damit beschäftigt, meine Truppen und die Planung für den bevorstehenden Kampf zu organisieren.\n\nIch würde den Schreckenslord Tichondrius ja gerne persönlich töten, aber ich überlasse Euch diese Ehre, Huln Hochberg.', '', '', '', '', '', '', 22522),
(40388, 'deDE', 'Hulns Krieg - Schattensang', 'Trefft Euch mit Jarod Schattensang auf dem Schlachtfeld.', 'Unten auf dem Schlachtfeld führt jetzt jemand das Kommando, den Ihr meines Wissens nach kennt.\n\nJarod Schattensang hält große Stücke auf Euch und wünscht, dass Ihr an seiner Seite kämpft.\n\nEr will diesen Kampf endgültig beenden.', '', '', '', '', '', '', 22522),
(39990, 'deDE', 'Hulns Krieg - Verstärkungen', 'Beschützt Malfurion.', 'Cenarius hat viel riskiert, indem er hierhergekommen ist – wenn auch nur kurz –, um Euch einen Segen zu gewähren. Er hat gesehen, was Ihr zu leisten imstande seid, und hat Euch zum Champion in seinem Kampf erwählt. Ich werde später mit Euch darüber sprechen. Das ist wirklich ein seltenes Geschenk.\n\nAber jetzt bitte ich um Eure Hilfe, Huln Hochberg. Ich muss meine Beschwörungen abschließen, damit Dryaden, die sich jenseits des Schleiers befinden, Zugang zu diesem Ort finden und uns helfen können.', '', '', '', '', '', '', 22522),
(39988, 'deDE', 'Hulns Krieg - Sturmgrimm', 'Trefft Euch mit Malfurion.', '<Eche''ro stupst Eure Hand an und dreht sich um. Dabei wirft er einen Blick zurück und bedeutet Euch, nach Süden zu gehen.>', '', '', '', '', '', 'Trefft Euch mit Malfurion.', 22522),
(40112, 'deDE', 'Hulns Krieg - Malornes Liebling', 'Rettet Eche''ro.', '[In gebrochener Gemeinsprache] Shan''do nach Euch gefragt. Hat Eche''ro-Tier mit Nachricht am Geschirr geschickt.\n\nUnng Ak hört Geräusch, wie Eche''ro kämpft. Ihr sucht Tier? Ihm helfen, vielleicht gefiederter Nachtelf erleichtert.', '', '', '', '', '', '', 22522),
(39983, 'deDE', 'Hulns Krieg - Die Ankunft', 'Besiegt $2oa Kämpfer der Legion.', 'Die Stärke unseres Stammes lässt sich auf Huln Hochberg zurückführen. Als die Legion uns das letzte Mal auslöschen wollte, hat er sie besiegt und viele der verstreuten, kleineren Stämme vereint.\n\nDurchlebt seine Geschichte, um zu verstehen, warum sein Name den Ängstlichen Mut macht und den Mutigen Angst einflößt.', '', '', '', '', '', '', 22522),
(39133, 'deDE', 'Keine Zeit zum Reden', 'Zerstört $1oa wütende Bernsteinsplitter.', 'Dargrul hat seine Begleiter in den Kampf gegen uns geschickt.$b$bWir müssen diese Elementare vernichten, bevor ihr Zorn über Donnertotem hinwegfegt.', '', '', '', '', '', '', 22522),
(39134, 'deDE', 'Zornsplitter', 'Tötet Zornsplitter.', 'Der Unterkönig hat den Hammer von Khaz''goroth dazu benutzt, die Bergflanke in eine Waffe gegen uns zu verwandeln. Der Hammer ist ein Herstellungswerkzeug, keine Waffe zu seiner Belustigung!$b$bSein Kristalldiener, Zornsplitter, hetzt während wir sprechen die Elementare aus dem Berg auf meine Leute herab.$b$bWir müssen die Brücke gegen diesen Ansturm verteidigen.$b$bBegebt Euch in die Höhle, findet diesen Zornsplitter und schickt ihn in Einzelteilen zurück zu seinem Herrn.', '', 'Von den Drogbar herbeigerufen, um Donnertotem zu zerstören.', 'Zornsplitter', '', '', '', 22522),
(40520, 'deDE', 'Blick in die Vergangenheit', 'Sammelt eine Flasche mit Luftfunken, Mu''shas Tränen und einen Dunkelsplitterkristall.', 'Ich möchte, dass Ihr aus erster Hand erfahrt, was Huln Hochberg während des Krieges der Ahnen in einer einzigen Schlacht vollbringen konnte.\n\nUnd dazu benötige ich ein paar sehr spezielle Zutaten. Sie sind nicht schwer zu finden. Schaut Euch einfach in der Nähe von Donnertotem um.', '', '', '', '', '', '', 22522),
(40167, 'deDE', 'Hulns Geschichte', 'Sprecht mit Geistwandler Ebenhorn, um mehr über Huln Hochberg zu erfahren.', 'Ihr arbeitet daran, die Stämme zu vereinen. Das ist eine beachtliche Leistung. In der Geschichte dieses Berges ist das zuvor nur einmal vorgekommen.\n\nHuln Hochberg hat vor Tausenden von Jahren die Stämme vereint, um die Legion im Krieg der Ahnen zu bekämpfen.', '', '', '', '', '', '', 22522),
(40515, 'deDE', 'Geisterlicher Spaziergang', 'Sprecht mit Geistwandler Ebenhorn.', 'Der Geistwandler, den ihr an der Flusswindung gerettet habt, ist schon seit seiner Ankunft unerträglich. Er besteht darauf, Euch die Bräuche unseres Volkes zu erklären, damit Ihr verstehen könnt, was bevorsteht.$b$bEr ist schon merkwürdig, selbst für einen Geistwandler.$b$bDennoch würde nur ein Narr seine Erkenntnisse ignorieren. Bitte unterhaltet Euch mit ihm, wenn Ihr etwas Zeit habt.', '', '', '', '', '', '', 22522),
(38913, 'deDE', 'Der Stamm der Himmelshörner', 'Trefft Euch mit Lasan Himmelshorn am Himmelshorn.', 'Die Himmelshörner gehören zu den besten Pfadfindern und Jägern, die es je gegeben hat.$b$bSie sind ein stolzes Volk, aber man kann durchaus mit ihnen reden.$b$bWenn Euch der Weg über die Straße zu unsicher ist, könnt Ihr einen der Flugmeister des Hochbergs bitten, Euch hinzufliegen.$b$bBitte, $p, sucht ihren Anführer Lasan auf und erinnert ihn daran, dass wir nur gemeinsam für den Hochberg stehen.', '', '', '', '', '', '', 22522),
(38912, 'deDE', 'Der Stamm der Bluttotems', 'Trefft Euch mit Oakin Eisenstier am Klippenrand.', 'Der Bluttotemstamm wird von Torok angeführt, einem etwas launischen, aber kompetenten Taurenhäuptling. Er ist Fremden gegenüber misstrauisch, aber ich glaube, Ihr habt bereits seinen Abgesandten Oakin Eisenstier kennengelernt, als Ihr ihn an der Flusswindung gerettet habt.\n\nVielleicht könnt Ihr diesen kleinen Gefallen erwähnen und so die Bluttotems als Verbündete zurückgewinnen. Der Hochbergstamm braucht sie, um die Drogbar zu besiegen und den Hammer von Khaz''goroth zurückzubeschaffen. Passt auf Euch auf, $n.', '', '', '', '', '', '', 22522),
(38909, 'deDE', 'Hoch hinaus', 'Bringt Jale Flussmähne nach Donnertotem.', 'Es ist kein Zufall, dass die Flussmähnen von den Dargrul in großer Zahl angegriffen wurden. Ihre Bauernhöfe und Weiden liefern die Nahrung für diese Region.$b$bBitte bringt Jale nach Donnertotem. Mit ein wenig Glück hat Mayla Verstärkung zur Verfügung, die sie hierher entsenden kann.$b$bWir werden die Überlebenden hier zusammenrufen und dann so schnell wie möglich nachkommen.', '', '', '', '', '', '', 22522),
(39027, 'deDE', 'Dargrul und der Hammer', 'Stellt Euch Dargrul dem Unterkönig.', 'Wir wollen gerade einen Drogbar in eine Höhle verfolgen, dabei haben wir das unseren Kindern immer verboten.$b$bAllerdings sind einige unserer Kriegerhelden bereits Dargrul dort hinein gefolgt.$b$bKommt, schließt Euch uns im Kampf an, damit wir den Hammer von Khaz''goroth zurückerobern können.', '', '', '', '', '', '', 22522),
(39043, 'deDE', 'Die Beißsteinenklave', 'Tötet Beißsteindrogbar in der Beißsteinenklave.', 'Dies ist eine alte Drogbarenklave, mit der wir früher Handel getrieben haben. Jetzt ist sie eine Operationsbasis für ihre Raubzüge.$b$bIch brauche mehr Zeit, um die verbleibenden Kriegerhelden zusammenzurufen. Geht in die Enklave hinein und tötet alle, die Ihr dort vorfindet.$b$bVerschafft uns die Zeit, die wir benötigen, um unsere Kraft zu sammeln.', '', '', '', '', '', '', 22522),
(39026, 'deDE', 'Die Drogbar', 'Tötet $1oa Drogbar bei der Flusswindung.', 'Es reichte Dargrul nicht, dieses Dorf zu zerstören. Er hat obendrein seine Steinräuber entsandt, um alles an sich zu reißen, was übrig ist.$b$bBitte, Champion, beweist meinem Volk, dass Ihr der Held seid, den ich in meinen Visionen gesehen habe.$b$bTötet so viele Steinräuber, dass ihre Entschlossenheit schwindet.', '', '', '', '', '', '', 22522),
(39025, 'deDE', 'Umklammerung des Unterkönigs', 'Rettet Jale Flussmähne, Kriegerheld Oro und Oakin Eisenstier.', 'Wenn es an dieser ganzen Situation ein Gutes gibt, dann ist es die Tatsache, dass Dargrul nicht weiß, wie man den Hammer benutzt.$b$bEr glaubt immer noch, dass der Hammer eine Waffe ist und kein Werkzeug der Schöpfung.$b$bDennoch hat er unsere Stammesmitglieder in Fäusten aus Stein festgesetzt, die er aus der Erde beschworen hat. Unsere Waffen haben sich gegen diese Magie als nutzlos erwiesen.$b$bIhr verfügt womöglich über genug Macht, um diese Fäuste aufzubrechen. Könnt Ihr uns helfen?', '', '', '', '', '', '', 22522),
(42104, 'deDE', 'Der Unterkönig naht', 'Evakuiert die Flusswindung.', 'Ich bin zu lahm, um diese Nachzügler in Sicherheit zu bringen. Nehmt mein Horn und gebt das Signal für die Evakuierung.$b$bWir müssen alle hier wegbringen, bevor die Drogbar ankommen. Dann können die Kriegerhelden übernehmen.$b$bSchickt sie fürs Erste einfach zu Geistwandler Ebenhorn. Der Narr kann sich um sie kümmern, bis es wieder sicher ist.', '', '', '', '', '', '', 22522),
(39572, 'deDE', 'Moozys Abenteuer', 'Bringt Moozy zu Sella Wasserweise an den Weißwasserschnellen.', '<Moozy treibt den Fluss hinab und muss wieder zu seiner Mutter gebracht werden.>', '', '', '', '', '', '', 22522),
(39498, 'deDE', 'Hochwasser', 'Sprecht mit Jale Flussmähne an der Flusswindung.', 'Die Flusswindung! Ist es das, wovon der Unterkönig sprach?$b$bDas ist meine Heimat und der Ort, wo wir unsere Wintervorräte lagern. Wir müssen so schnell wie möglich dorthin und sie warnen.$b$bTrefft mich dort.', '', '', '', '', '', 'Kehrt zu Jale Flussmähne an der Flusswindung zurück.', 22522),
(39487, 'deDE', 'Kristallzorn', 'Tötet Gelmogg.', 'Ein Steinzauberer der Drogbar namens Gelmogg hat unsere heilige Wasserhöhle mit dem Ziel betreten, ihre Kraft in sich aufzunehmen.\n\nWir müssen ihn vernichten, bevor er zu mächtig wird.\n\nEr befindet sich im oberen Abschnitt der Höhle.', '', '', 'Gelmogg', '', '', '', 22522),
(39489, 'deDE', 'Einfallende Höhlenkletterer', 'Tötet $1oa Manadürster der Drogbar oder brutale Felsbeißer.', 'Die Drogbar und ihre brutalen Felsbeißer haben die Höhle überrannt. Sie verderben ihre natürliche Magie und zerstören die Elementare, die seit Ewigkeiten hier wohnen.\n\nLasst uns zusammenarbeiten, um sie aus dieser heiligen Höhle zu vertreiben.', '', '', '', '', '', '', 22522),
(39488, 'deDE', 'Gleichgewicht der Elemente', 'Sammelt $1oa energieerfüllte Kristalle.', 'Die Drogbar platzieren ihre scheußlichen Kristalle in unserer Höhle, um die ihr innewohnende Magie zu absorbieren. Wenn sie mit Macht gesättigt sind, sammeln sie sie ein.\n\nWir müssen die Kristalle an uns bringen, sodass die Magie sich wieder verflüchtigen und in das Wasser zurücksickern kann. Sonst werden die Drogbar Wege finden, die Magie für ihre eigenen Zwecke zu manipulieren.', '', '', '', '', '', '', 22522),
(39661, 'deDE', 'Die Höhle des Lebensquells', 'Trefft Jale Flussmähne an der Höhle des Lebensquells.', 'Die Drogbar haben meine Höhle überrannt. Dort praktizieren wir Flussmähnen unsere Wassermagie und schöpfen das reinste Heilwasser des Hochbergs.\n\nTrefft mich dort und lasst uns diesen heiligen Ort gemeinsam von diesen Bewohnern des Dunkels befreien.', '', '', '', '', '', '', 22522),
(39323, 'deDE', 'Moozys Verbleib', 'Findet Moozy flussabwärts bei den Weißwasserschnellen.', 'Mein Sohn! Sie haben ihn mitgenommen! Meine Frau und ich haben uns aufgeteilt, um nach ihm zu suchen.\n\nBitte, geht und findet Moozy... ich werde schon klarkommen. Wenn Ihr Moozy findet, bringt ihn bitte flussaufwärts, zu meiner Frau.', '', '', '', '', '', '', 22522),
(39277, 'deDE', 'Das Parfüm', 'Sprüht die Drogbar an den Weißwasserschnellen mit dem Käferspray ein.', 'Das Käferspray, das Ihr Ormgul dem Befallenen abgenommen habt, könnte uns vielleicht einen Vorteil gegenüber den Drogbar und ihren Insekten verschaffen.\n\nSprüht die Drogbar mit ihrer eigenen Alchemie ein und Ihr werdet sehen, wie ihre Insekten sich gegen sie wenden.', '', '', '', '', '', '', 22522),
(39614, 'deDE', 'Fische an Land', 'Werft $1oa Weißwasserkarpfen in den Fluss zurück.', 'Die Drogbar sind in totale Zerstörungswut verfallen! Sie zerschmettern alles, was ihnen in die Quere kommt, und werfen Fische an Land.\n\nSchlimmer noch, es ist die falsche Zeit zum Fischfang. Die Tiere müssen zurück ins Wasser, damit sie laichen können.', '', '', '', '', '', '', 22522),
(39316, 'deDE', 'Tauren in der Falle', 'Befreit die gefangenen Tauren der Flussmähnen.', 'Die Drogbar haben so viele von uns zusammengetrieben, wie sie konnten, und in eine Höhle in der Nähe gebracht.\n\nWenn Ihr schnell handelt, dann können sie, glaube ich, noch gerettet werden. Ich weiß nicht, was sie mit den Gefangenen vorhaben.\n\nMir graut es, wenn ich nur daran denke... an den tiefliegenden Orten regiert die Finsternis.', '', '', '', '', '', '', 22522),
(39274, 'deDE', 'Verteidigung der Flusslande', '', '', '', '', '', '', '', '', 22522),
(39496, 'deDE', 'Der Strom des Flusses', 'Trefft Euch mit Angler Fischkorb an den Weißwasserschnellen.', 'Jetzt, da die größte Gefahr gebannt ist, können wir Bäuerin Maya die Aufsicht über die Felder überlassen.\n\nDas bedeutet allerdings nicht, dass wir bereits das Schlimmste hinter uns gebracht haben, $n. Die Fischerei ist genauso wichtig wie unsere Felder, und sie ist sogar in noch größerer Gefahr, sollten Angler Fischkorbs Worte der Wahrheit entsprechen.', '', '', 'Angler Fischkorb', '', '', '', 22522),
(39490, 'deDE', 'Befall', 'Tötet $1oa verseuchte Maden und $2oa übergroße Larven an der Gabe der Erdenmutter.', 'Diese Käfer zerstören unsere Ernte und greifen jeden von uns an, der sich ihnen nähert.\n\nWürdet Ihr einige dieser bösartigen Kreaturen vernichten?', '', '', '', '', '', '', 22522),
(39272, 'deDE', 'Vergiftete Ernte', 'Zerstört die Giftgötzen der Drogbar.', 'Nachdem sie uns von unserem Ackerland vertrieben und unsere Feldfrüchte mit Insekten verseucht hatten, stellten die Drogbar widerliche Totems auf, um unsere Ernte noch weiter zu vergiften.\n\nIch kann nur hoffen, dass Ihr diese zerstört und wir danach in der Lage sein werden, unser Land mit Hilfe von Jales Wassermagie wieder zu heilen.\n\nDanke, dass Ihr gekommen seid, um uns zu helfen, $n. Wir sind nur einfache Bauern und Fischer und haben nur sehr wenig Kampferfahrung.', '', '', '', '', '', '', 22522),
(39491, 'deDE', 'Ormgul der Befallene', 'Tötet Ormgul den Befallenen und nehmt sein Käferspray.', 'Seit vielen Jahren haben wir in Frieden mit den Drogbar gelebt – unser Stamm mehr als die anderen. Doch bei unserem letzten Handel mit ihnen, an der Flusswindung, wurden wir betrogen.\n\nFür gewöhnlich versorgen sie uns mit kräftigen alchemistischen Mixturen, die verhindern sollen, dass Schädlinge unsere Felder befallen. Aber diesmal haben sie unter der Führung von Ormgul die Pflanzen angegriffen, die uns alle mit Nahrung versorgen.\n\nOrmgul der Befallene ist derjenige, der unsere Felder der Vernichtung preisgegeben hat. Vernichtet ihn und bringt mir, was auch immer er benutzt hat, um die Käfer auf unser Land zu locken.', '', '', 'Ormgul der Befallene', '', '', '', 22522),
(38911, 'deDE', 'Der Stamm der Flussmähnen', 'Trefft Jale Flussmähne bei der Gabe der Erdenmutter.', 'Die Zeit reicht nicht aus, alles zu erzählen, was hier geschehen ist. Kurz gesagt haben unsere Stämme sich nach dem Angriff, bei dem der Hammer von Khaz''goroth in die Hände der barbarischen Drogbar fiel, in alle Winde zerstreut.\n\nDie Drogbar greifen den Stamm der Flussmähnen noch immer an, und ich habe nicht genug Krieger, um ihnen Hilfe zu schicken.\n\nWir brauchen $geinen Helden:eine Heldin; wie Euch. Bitte begebt Euch zur Gabe der Erdenmutter und eilt ihnen zu Hilfe.', '', '', '', '', '', '', 22522),
(1959, 'deDE', 'Meldung bei Anastasia', 'Sprecht mit Anastasia Hirschbrunn.', 'Die Magier von Unterstadt haben eine magische Störung in der Stadt bemerkt und die Magier der Horde um Untersuchung gebeten.$B$BAnastasia Hirschbrunn kann Euch mehr sagen. Sprecht mit Ihr im Magieviertel in Unterstadt.', '', '', '', '', '', '', 22522),
(38882, 'deDE', 'Ein neues Leben für die Untoten', 'Folgt Sylvanas in die Kammer von Eyir.', 'Ihr scheint voller Überraschungen zu stecken, $n. Mit dem Segen der Königin der Valkyra ist es Euch gelungen, einen Pfad zu öffnen, der das Schicksal von ganz Azeroth beeinflussen wird.\n\nWerdet Zeuge der Geburt eines neuen Zeitalters der Verlassenen!', '', '', '', '', '', '', 22522),
(39155, 'deDE', 'Aszendentenriten', 'Versucht, den Segen an den vier Schreinen im Tempel von Skold-Ashil zu erlangen und dann mit der Statue zu kommunizieren.', 'Die Gravierungen scheinen einen Aszendentenritus darzustellen, mit dessen Hilfe die Schildmaiden die Kammer betreten können. Ich denke, das können wir zu unserem Vorteil nutzen.\n\nIhr müsst $gder nächste Aszendent:die nächste Aszendentin; werden.\n\nBegebt Euch zum Tempel und vollzieht die Riten. Und wenn Ihr dazu über Leichenberge von Schildmaiden waten müsst, dann ist das eben so. \n\nSucht mich auf, wenn Ihr diese Aufgabe erfüllt habt, damit wir die Kammer für die Horde einnehmen können!', '', '', '', '', '', '', 22522),
(39405, 'deDE', 'Geschichten des Kampfs', 'Sammelt 8 Strophen der Ashilvaradichtung von den Priesterinnen von Eyir in Skold-Ashil ein.', 'Die von der Priesterin mitgeführten Schriftstücke scheinen der erste Teil eines Epos zu sein. Dieses Werk beschreibt das Leben von Ashildir, der alten Königin der Valkyra.\n\nWenn es eine Verbindung zwischen Ashildir und den Schildmaiden hier gibt, könnt Ihr diesen Umstand vielleicht zu Eurem Vorteil nutzen.\n\nInwieweit sie in diese Sache involviert ist, steht wahrscheinlich im übrigen Text.', '', '', '', '', '', '', 22522),
(38878, 'deDE', 'Schildgeheimnisse', 'Untersucht 6 gravierte Vrykulschilde in Skold-Ashil.', 'Unser Ziel liegt im Innern dieser Kammer, $C. Jetzt ist keine Zeit, es in allen Einzelheiten zu erzählen. Ihr müsst nur wissen, dass es von höchster Bedeutung für die Verlassenen ist, dass wir erfolgreich sind.\n\nDie Schildmaiden haben bis jetzt erbitterten Widerstand geleistet, aber ich glaube, es gibt noch einen anderen Weg hinein. Einige Wenige ihres Ordens haben dort Zutritt, und ich möchte, dass Ihr herausfindet, wie sie das machen.\n\nIn der Stadt habe ich auffällig gravierte Schilde gesehen, die ihnen wohl von den Champions überreicht werden. Die darauf gezeigten Szenen könnten der Schlüssel zur Kammer sein.', '', '', '', '', '', '', 22522),
(38939, 'deDE', 'Angriff auf Skold-Ashil', '<insert>', '<insert>', '', '', '', '', '', '', 22522),
(39154, 'deDE', 'Nach Skold-Ashil', 'Trefft Euch mit den Dunklen Waldläuferinnen am Waldläufervorposten außerhalb von Skold-Ashil.', '$C, dank Eures Eingreifens ist die Situation am Schreckensflutufer jetzt unter Kontrolle. Aber wir haben keine Zeit, uns auf unseren Lorbeeren auszuruhen. Wir müssen immer noch Sylvanas'' Mission erfüllen.\n\nDie Dunklen Waldläuferinnen kontrollieren ihre Operationen in Skold-Ashil von dem Turm oberhalb der Flussgabelung herab. Sucht sie auf und bietet ihnen Eure Hilfe bei ihren Aufgaben an.', '', '', '', '', '', '', 22522),
(39385, 'deDE', 'Ein Geschenk für Graumähne', 'Platziert 3 schwere gilnearische Sprengstoffladungen am Rumpf von Graumähnes Schiff und lasst sie aus sicherer Entfernung detonieren.', 'Die gilnearischen Eindringlinge scheinen hochexplosive Sprengmittel mit in die Schlacht gebracht zu haben. Ich vermute, dass sie damit die Mauern am Schreckensflutufer angreifen wollen.\n\nWenn man diese Ladungen an Graumähnes Schiff befestigen könnte, ist sein Angriff auf den Außenposten vielleicht ein für alle Male beendet.', '', '', '', '', '', '', 22522),
(39153, 'deDE', 'Schreckensflutdilemma', 'Tötet 10 Saboteure der Grauen Wacht bei Graumähnes Sturm.', 'Die Situation hat sich deutlich verschlechtert, $n. Wir waren auf Graumähnes plötzlichen Angriff nicht eingestellt. Am Schreckensflutufer befindet sich nur noch eine kleine Einheit und dafür zahlen wir jetzt den Preis.\n\nDer Strand wimmelt jetzt nur so vor Worgenspitzeln. Und wenn die Kanonen unsere Verteidigung aufbrechen, werden sie uns wohl überrennen. Wir haben zu wenig Leute, um sie anzugreifen und zurückzuschlagen, aber Ihr könnt das ändern.\n\nLöscht sie aus.', '', '', '', '', '', '', 22522),
(38873, 'deDE', 'Macht das Deck klar!', 'Feuert 4 Katapulte der Verlassenen bei Graumähnes Sturm ab.', 'Unsere Mission in Skold-Ashil muss vorerst warten, $C. Jetzt gibt es dringendere Angelegenheiten, um die wir uns kümmern müssen.\n\nIch bin erst vor Kurzem mit Verstärkung hier eingetroffen, als Graumähnes Schiff auftauchte und die Mauern unter Beschuss nahm. Wir haben unsere Belagerungswaffen kampfbereit gemacht, aber ihre Bodentruppen haben uns blitzschnell überwältigt.\n\nWenn Ihr an die Katapulte rankommt und auch noch die Männer an den Kanonen ausschalten könnt, verschafft uns das vielleicht genug Zeit, den Angriff zurückzuschlagen!', '', '', '', '', '', '', 22522),
(40072, 'deDE', 'Die Aegis sichern', 'Besiegt den Gottkönig Skovald in den Hallen der Tapferkeit und sichert die Aegis von Aggramar.', 'Die Teufelsskorn mögen am Boden sein, aber der Kopf der Bestie regt sich noch. Der Gottkönig ist noch immer hinter der Aegis her und jemand muss ihn aufhalten.\n\nIhr habt schon so viele Prüfungen gemeistert... An dieser hier dürft Ihr nicht scheitern. Stellt die Aegis sicher, und wenn Ihr Skovald dazu jeden Knochen einzeln brechen müsst!\n\nDie Hallen der Tapferkeit erwarten Euch!', '', '', '', '', '', '', 22522),
(40005, 'deDE', 'Sturmheims Rettung', 'Besiegt Gottkönig Skovald bei der Pforte der Tapferkeit.', 'Augenscheinlich waren die Portale, die wir hier zerstört haben, nicht die einzigen ihrer Art. Von oberhalb der Pforte der Tapferkeit dringen weitere Dämonen ein, beschworen vom Gottkönig selbst!\n\nSkovald kann ich allein nicht besiegen. Kommt mit mir, Champion! Lasst uns den Kampf mit ihm aufnehmen und diese Bedrohung ein für alle Mal beenden!\n\nFür Sturmheim!', '', '', '', '', '', '', 22522),
(39998, 'deDE', 'Treibt die Wellenskorn zurück', '', '', '', '', '', '', '', '', 22522),
(40004, 'deDE', 'Das Rückgrat brechen', 'Besiegt Garzareth, Karuas, Muorg und Ravathes bei der Pforte der Tapferkeit.', 'Um seine verderbten Truppen anzuführen, hat der Gottkönig mächtige Dämonen zu seinen Leutnants ernannt. Wenn wir den Angriff beenden wollen, müssen wir ihre Anführer ausschalten.\n\nFür normal Sterbliche sind diese Dämonen viel zu mächtig. Euch aber steht die Macht Sturmheims zu Verfügung. Sie werden keine Chance gegen Euch haben. \n\nSchaltet sie aus, um Skovalds Kommandostruktur das Rückgrat zu brechen!', '', '', '', '', '', '', 22522),
(40003, 'deDE', 'Eindämmen der Flut', 'Verwendet das sturmgeschmiedete Horn, um drei Dämonenportale bei der Pforte der Tapferkeit zu vernichten.', 'Skovald und seine dämonischen Verbündeten haben die Pforte der Tapferkeit geschändet. Und seine verderbten Mystiker haben Portale errichtet, um seinen teuflischen Verbündeten aus verseuchten Welten Zutritt zu verschaffen.\n\nWenn wir die Flut dieser Feinde eindämmen wollen, müssen diese Portale verschlossen werden.\n\nIch werde meine Verbündeten zusammenrufen. Nehmt dieses Horn und blast Alarm, wenn Ihr ein Portal gefunden habt. Dann werden wir aus der Luft zuschlagen!', '', '', '', '', '', '', 22522),
(40002, 'deDE', 'Ein vertrautes Schicksal', 'Sammelt den Splitter des Kerkermeisters von Dravax ein und befreit Vethir aus seinem Gefängnis.', 'Wir sind hierhergekommen, um die Hallen der Tapferkeit zu verteidigen... Sie haben mich vom Himmel geholt...\n\nIch kann aus diesen Fesseln nicht ausbrechen. Findet den Kerkermeister, der diese Zelle eingerichtet hat...\n\nBitte befreit mich, $gmein Freund:meine Freundin;...', '', '', '', '', '', '', 22522),
(38872, 'deDE', 'Auf Befehl der Dunklen Fürstin', 'Trefft Euch mit Nathanos Pestrufer am Schreckensflutufer.', 'Sind uns diese Kerle freundlich gesinnt? Ist wohl ein gutes Zeichen, dass sie nicht versuchen, Herzogin zu fressen... noch nicht.\n\nWie dem auch sei, ich habe Befehle. Nathanos hat sich den Dunklen Waldläufern angeschlossen, die auf Befehl von Fürstin Sylvanas höchstpersönlich an Bord der Ewigen Vergessenheit eine geheime Mission ausführen! Leider hat die Allianz von unserem Plan erfahren, und jetzt greift Graumähne den Außenposten an!\n\nWir könnten da drüben wirklich Eure Hilfe brauchen, $n. Herzogin kann Euch dorthinbringen, sagt mir einfach Bescheid.', '', '', '', '', '', '', 22522),
(40001, 'deDE', 'Vor der Pforte der Tapferkeit', 'Findet Vethir im Lager der Teufelsskorn.', 'Zwar ist es Euch vorbestimmt, die Hallen der Tapferkeit zu erobern, aber der Gottkönig mit seinen Dämonen und seiner Verschlagenheit schändet die heiligen Treppen und macht Euch diesen Anspruch streitig.\n\nEure Drachenverbündeten sind zur Verteidigung der Hallen geeilt, aber sie benötigen Eure Unterstützung. Die Vrykul haben den Anführer der Sturmdrachen gefangen genommen, der Euch als Vethir bekannt ist.\n\nBefreit Euren Drachenfreund, Champion, und fegt die Teufelsskorn von der Pforte der Tapferkeit!', '', '', '', '', '', '', 22522),
(40078, 'deDE', 'Schwere Bürde', 'Nehmt den Fisch aus Havis Fischfass und steckt ihn auf den Speer über dem Feuer.', 'Nach Ashildirs Urteil ist Eure letzte Prüfung abgeschlossen.\n\nAber selbst die Hallen der Tapferkeit können warten, denn erst müssen wir etwas essen! Tot zu sein, macht ganz schön hungrig.\n\nKommt jetzt und bringt den Fisch mit zum Feuer. Der ist viel zu schwer, als dass ich ihn tragen könnte.', '', '', '', '', '', '', 22522),
(40956, 'deDE', 'Immer dem Leitstrahl nach...', 'Verwendet den telemantischen Signalgeber, um die Ruinen von Elune''eth zu untersuchen.', 'Wir sollten jetzt die erste sichere Verbindung unseres Teleportationsnetzwerkes einrichten.$B$BNehmt diesen Signalgeber mit zu den Ruinen von Elune''eth über uns und sucht gründlich die Gegend ab, um einen geeigneten Ort für ihn zu finden.$B$BWenn die Untersuchung beendet ist, platziert Ihr einfach den Signalgeber an der Stelle, damit ich von hier aus eine Verbindung zu ihm herstellen kann.', '', '', '', '', '', '', 22522),
(39855, 'deDE', 'Ein Leben als Preis', 'Sucht Helya auf und verlasst Helheim.', 'Wir haben getan, was Helya von uns verlangt hat. Jetzt werden wir ja sehen, ob sie Wort hält.\n\nWenn Ihr bereit seid, machen wir uns auf den Weg zum Thron. Wenn Ihr hier noch etwas zu erledigen habt, solltet Ihr das jetzt tun.\n\nGeht Ihr voran, $gFremder:Fremde;.', '', '', '', '', '', '', 22522),
(39853, 'deDE', 'Ein Sieg für die Ewigkeit', 'Besiegt Geir auf dem Ewigen Schlachtfeld in Helheim.', 'Jetzt gibt es keinen Grund mehr zu zögern. Es ist an der Zeit zuzuschlagen.\n\nWir werden dieses widerwärtige Ungetüm vernichten und dann unseren Preis bei Helya einfordern. Der Weg aus Helheim heraus liegt vor uns, $gFremder:Fremde;. \n\nMacht Euch bereit.', '', '', '', '', '', '', 22522),
(39851, 'deDE', 'Verbündete im Tod', 'Befreit 8 ertrinkende Valkyra in Helheim.', 'Unsere Kräfte allein werden vielleicht nicht ausreichen, um dieses Ungetüm Geir zu besiegen. Zum Glück hat uns Faljar ein paar Verbündete nach Helheim geschickt.\n\nIch habe schon vor einer Weile ein paar vor ihnen entdeckt, wie sie von Helheims Elementarmächten gefoltert wurden. Man würde meinen, die Toten könnten nicht ertrinken, doch mit Helyas Macht ist auch dies möglich.\n\nIch konnte sie nicht befreien, aber da Ihr jetzt das Weglicht habt, könnte es Euch gelingen.\n\nSucht sie und befreit sie von ihren ewigen Qualen!', '', '', '', '', '', '', 22522),
(39850, 'deDE', 'Zerrissen', 'Sucht das Fragment des Willens, das Fragment der Macht und das Fragment der Tapferkeit in Helheim und befreit sie.', 'Ich vermute, dass wir mit der Kraft Eurer Waffe Geir besiegen können, aber momentan seid Ihr zu schwach für diesen Kampf. Wir müssen erst die fehlenden Teile Eures Geistes zurückgewinnen.\n\nHelya hat sie sicher über ihr ganzes Reich verstreut, um sie vor Euch zu verbergen. Solltet Ihr sie finden, dann leuchtet mit der Laterne darauf, um sie wieder freizusetzen.\n\nAber gebt gut auf Euch acht: Helya lässt Eure Geistfragmente ganz sicher bewachen.', '', '', '', '', '', '', 22522),
(39849, 'deDE', 'Folgt dem Licht', 'Besorgt ein Weglicht aus Helheim von Blutbart.', 'Helya glaubt wohl, dass ihre Herausforderung nicht zu bestehen ist. Wir werden ihr das Gegenteil beweisen.\n\nIch bin mir nicht sicher, wie sie es geschafft hat, aber irgendwie hat Helya Euch geschwächt. Ich habe Ähnliches bei meinen Schildmaiden erlebt, und nun werden sie von Helyas Dienern gefangen gehalten.\n\nAber ich weiß vielleicht eine Möglichkeit. Auf den Schiffen der Kvaldir befinden sich bestimmte Laternen, die an diesen Ort gebunden sind und der Navigation durch den Nebel dienen. Man sagt, dass das Licht dieser Laternen Fesseln des Geistes lösen kann.\n\nEs befindet sich ein Schiff der Kvaldir ganz in der Nähe. Vielleicht findet Ihr dort eine dieser Laternen.', '', '', '', '', '', '', 22522),
(39857, 'deDE', 'Ewiger Kampf', 'Kehrt zu Ashildir zurück und erzählt ihr von Helyas Herausforderung.', '<Helyas Stimme hallt durch den Himmel über dem Altar.>\n\nIhr behauptet, Ihr gehört nicht hierher zu den Unwürdigen, $gFremder:Fremde;, doch Euer Geist zerfällt wie Sand. Wie kann ich wissen, dass Ihr die Wahrheit sprecht?\n\nGar nicht! Daher werdet Ihr es beweisen.\n\nBegebt Euch zum Ewigen Schlachtfeld und stellt Euch meinem Champion. Wenn Ihr ihn besiegen könnt, will ich Euch Gnade gewähren.', '', '', '', '', '', '', 22522),
(39848, 'deDE', 'Eine verzweifelte Bitte', 'Sucht Helya auf und bittet sie, Euch aus Helheim zu entlassen.', 'Euer Freund scheint es nicht geschafft zu haben. Das tut mir leid, aber wir müssen weiter.\n\nIhr solltet mit Helya sprechen. Sie ist hier die Herrscherin. Ich würde Euch ja begleiten, aber ich fürchte, das würde Eurem Anliegen nicht weiterhelfen.\n\nSucht mich auf, wenn Ihr fertig seid. Ich versuche herauszufinden, wo sich meine Schildmaiden aufhalten.', '', '', '', '', '', '', 22522),
(38347, 'deDE', 'Seetang tarnt besser', 'Benutzt die Kvaldirverkleidung, um an Guarm vorbeizuschleichen.', 'So, die Verkleidung ist fertig!\n\nUnd der Gestank ist ein Teil davon. Es ist wahrscheinlich am besten, wenn wir auch so riechen wie die Kvaldir. Aber ich befürchte, dass ich einen Priester brauchen werde, um diesen Gestank hinterher wieder loszuwerden.', '', '', '', '', '', '', 22522),
(38324, 'deDE', 'Accessoires der Verfluchten', 'Sammelt 8 verfluchte Knochen in Helheim.', 'Moment! Bevor Ihr losstürzt, brauchen wir noch mehr als nur ein bisschen Seetang. \n\nDie Vrykul laufen doch immer mit diesen Knochen herum. Sie tragen sie wie eine Art Schmuck. Wenn Ihr ein paar davon einsammeln könnt, würde ich damit die Verkleidung noch etwas verbessern.\n\nAlso, worauf wartet Ihr noch?', '', '', '', '', '', '', 22522),
(38339, 'deDE', 'Etwas Hilfe von guten Feinden', 'Sammelt $1oa Bündel Seetang von den Nebelrufern und Seelenschindern der Kvaldir in Helheim ein.', 'Ich weiß nicht, was Ihr hier macht, aber ich möchte Euch danken. Wenn Ihr aber noch weiter wollt, müsst Ihr an diesem Höllenhund, Guarm, vorbei.\n\nIch habe das selbst schon ein paar Mal versucht und ich glaube, ich kenne eine Möglichkeit. Wir könnten diesen Kvaldir ein bisschen Seetang abnehmen, um Guarm zu täuschen. Er ist zwar sehr bösartig, aber nicht sehr klug.\n\nWas meint Ihr? Könntet Ihr es mit ein paar Kvaldir aufnehmen?', '', '', '', '', '', '', 22522),
(39837, 'deDE', 'Eine unwürdige Aufgabe', 'Sucht unter den gefangenen Vrykul in Helheim nach einem, der Euch bei der Flucht behilflich sein kann.', 'Das hier ist Helheim, das Reich der unwürdigen Toten. Hier kämpfen die Geister der Ehrlosen einen ewigen Kampf, stets auf der Suche nach Ruhm, doch nie in der Lage, ihn zu erlangen.\n\nWir sollten nicht hier sein.\n\nWir müssen einen Weg zurück finden. Ich werde mich umsehen. Ihr solltet versuchen, ein paar dieser gefangenen Geister zu befreien. Vielleicht können sie uns ja weiterhelfen.', '', '', '', '', '', '', 22522),
(38343, 'deDE', 'Macht keinen Hel daraus', '', '', '', '', '', '', '', '', 22522),
(38818, 'deDE', 'Das letzte Urteil', 'Besiegt Runenseher Faljar in Haustvald.', 'In meinem zerrissenen Zustand habe ich bruchstückhaft erblickt, wie $gein Held:eine Heldin; die verderbten Mystiker von Haustvald bestraft. Jetzt erkenne ich, dass Ihr das wart, $C.\n\nIch weiß, dass Ihr mein Urteil erwartet, aber mein Geist ist immer noch nicht vereint. Schon jetzt verwendet Runenseher Faljar dunkle, verbotene Magie, um meinen Geist nach Helheim zu verbannen. Wenn es ihm gelingt, ist Eure Prüfung verloren.\n\nMeine Bindung an diesen Ort wird schwächer. Beeilt Euch! Es bleibt nicht mehr viel Zeit!', '', '', '', '', '', '', 22522),
(38815, 'deDE', 'Die Erweckung der Schildmaid', 'Verwendet die Rune der Erneuerung, um Ashildirs Geist mit ihren Knochen zusammenzuführen.', 'Jetzt, wo die Rituale unterbrochen und die Knochen zurückgebracht worden sind, können wir vielleicht Ashildir aus den Klauen dieser Knochensprecher befreien.\n\nTief drinnen in Haustvald verfügen die Mystiker über einen besonderen Altar, mit dessen Hilfe Körper und Geist gefallener Krieger wiedervereint werden können. Vielleicht können wir dort Ashildirs Geist mit ihren Knochen zusammenführen und sie so diesen üblen Mystikern entreißen.\n\nGeht jetzt, $gFremder:Fremde;. Ich rufe ihre Wache zusammen.', '', '', '', '', '', '', 22522),
(38823, 'deDE', 'Die bindenden Runen', 'Zerstört die Ritualsteine, um die Rituale aufzuhalten, die Ashildirs Geist in Haustvald binden.', 'Auf dieser Steintafel wird ein Ritual beschrieben, mit dem man einen mächtigen Geist in schwächere Teile aufspalten kann, indem man diese an spezielle Runensteine bindet. Offensichtlich will der Runensprecher genau dieses Ritual verwenden, um den Geist von Ashildir zu schwächen.\n\nWenn ihr Geist derart geschwächt ist, könnte es unmöglich werden, sie zu beschwören. Den Ritualen muss Einhalt geboten werden.', '', '', '', '', '', '', 22522),
(38816, 'deDE', 'Knochenbrecher der Knochensprecher', 'Besiegt 15 Knochensprechervrykul oder deren Verbündete in Haustvald.', 'Die Verbrechen dieser Ritualisten dürfen nicht ungesühnt bleiben, aber wir können die Strafe nicht vollstrecken. Ihre Runenmagie verleiht ihnen unermessliche Macht über die Toten.\n\nIhr aber seid nicht tot und ich spüre große Kraft in Euch. Bekämpft diese Ketzer und zeigt Ashildir, dass Ihr wahrlich ihres Urteils würdig seid!', '', '', '', '', '', '', 22522),
(38817, 'deDE', 'Royale Reste', 'Sammelt 8 von Ashildirs Knochen in Haustvald.', 'Ihr wollt Euch also dem Urteil der Königin der Valkyra stellen? Dann muss es Eure Schuld sein!\n\nDie Knochensprecher von Haustvald haben ein monströses Verbrechen begangen. Sie haben Ashildirs Knochen gestohlen. Sie versuchen damit sicher, Außenseiter davon abzuhalten, die Prüfungen zu bestehen. Das dürft Ihr nicht zulassen!\n\nDringt in Haustvald ein, um ihre Überreste zu bergen. Ich hoffe um Euretwillen, dass Ashildir nachsichtig ist.', '', '', '', '', '', '', 22522),
(39791, 'deDE', 'Allerletzte Ruhe', 'Verwendet den Ast des Runenwalds, um 8 geschwächte ruhelose Vorfahren wieder zur Erde zurückkehren zu lassen.', 'Ich möchte Euch um einen Gefallen bitten, $gFremder:Fremde;.\n\nWenn Ihr an den Gräbern der legendären Helden vorbeigeht, werdet Ihr sicher auf die Geister der ruhelosen Toten stoßen, die über die Missetaten der Knochensprecher sehr aufgebracht sind. Sie müssen wieder zurück in die Erde, in der sie begraben wurden, damit auch über sie ein Urteil gefällt werden kann.\n\nNehmt diesen Ast. Er enthält die Essenz des Runenwalds. Wendet seine Kraft auf einen geschwächten Geist an, um ihn wieder zur Ruhe zu bringen.', '', '', '', '', '', '', 22522),
(38811, 'deDE', 'Der Tag des Urteils', 'Stellt Euch dem Urteil der alten Herrscher der Vrykul.', 'Ihr habt Euch bis jetzt als sehr findig erwiesen, $gFremder:Fremde;. Wie versprochen werde ich Euch nun das Ritual der Erweckung erklären.\n\nZunächst müsst Ihr die Opfergabe direkt auf das Grab des Herrschers legen, den Ihr erwecken möchtet. Verbrennt dann das Räucherwerk, damit der Duft über das Grab zieht. Wenn Eure Opfergabe angemessen ist, erhebt sich der Herrscher und Ihr könnt Euch seinem Urteil stellen.\n\nPasst gut auf Euch auf, $gFremder:Fremde;. Die toten Könige der Vrykul schätzen es nicht, wenn man sie aus ihrem Schlaf erweckt.', '', '', '', '', '', '', 22522),
(38808, 'deDE', 'Bjornhjarta', 'Sammelt 4 geschwollene Bärenherzen von den gierigen Bären im Runenwald.', 'Auch im Jenseits verspüren die Toten noch Gelüste auf die Köstlichkeiten aus ihrem früheren Leben. Für die Könige der Vrykul gibt es nichts Schöneres als Bjornhjarta, ein Festmahl aus geschwollenem Bärenherz.\n\nDie Bären im Runenwald sind wohlgenährt und berühmt für ihr saftiges Fleisch. Begebt Euch auf die Jagd und sucht nach einem geeigneten Herz als Gabe für die alten Herrscher. Einem solchen Geschenk können sie einfach nicht widerstehen.', '', '', '', '', '', '', 22522),
(38810, 'deDE', 'Der Traumpilz', 'Sammelt 8 Seelenkappen im Runenwald.', '$gEin Fremder:Eine Fremde;, $gder:die; sich dem Urteil der alten Herrscher der Vrykul stellen will? Das ist seltsam. Aber Ihr solltet wissen, dass ihnen Eure Gegenwart nicht gefallen wird.\n\nWenn Ihr unbedingt vor sie treten wollt, könnt Ihr sie mit einem Erweckungsritual heraufbeschwören. Dazu benötigt Ihr aber eine aromatische Substanz zur Verbrennung und ein Geschenk als Opfergabe.\n\nIm Runenwald wächst ein Pilz, der sich als Räucherwerk eignet. Wenn Ihr einige besorgt habt, erkläre ich Euch die weiteren Schritte.', '', '', '', '', '', '', 22522),
(39788, 'deDE', 'Die Rache des Runenwalds', 'Tötet 8 Runenäxte der Knochensprecher und 8 Schnitzer der Knochensprecher im Runenwald.', 'Die Knochensprecher... Sie sind verderbt... Sie dürsten nach Macht.\n\nTilgt sie aus meinen Wäldern...', '', '', '', '', '', '', 22522),
(41413, 'deDE', 'Was soll schon schiefgehen?', '', '', '', '', '', '', '', '', 22522),
(44153, 'deDE', 'Die Bürde des Lichts', 'Platziert das Herz des Lichts in Eurer Ordenshalle.', '"Ihr habt nicht um diese Bürde gebeten, aber das Schicksal hat es so gewollt." Turalyons düstere letzte Worte klingen mir immer noch in den Ohren. Ich fürchte um die Draenei... und um meinen Freund Velen. Aber wir müssen stark bleiben, $n!$B$BFür den Moment verbleibt das Herz des Lichts unter Euren wachsamen Augen. Es gibt einen Ort auf Azeroth, dem ich die Sicherheit dieses Objekts anvertrauen würde: Eure Ordenshalle. Bringt es dorthin, $n. Ich bleibe in Dalaran und suche weiter nach Antworten. Es muss einen anderen Weg geben!', '', '', '', '', '', '', 22522),
(43705, 'deDE', 'Nobundos letztes Gefecht', 'Verteidigt Scharfseher Nobundo gegen den Sturmangriff der Legion.', 'Die Exodar erzitterte unter dem Ansturm der Legion. Bevor wir reagieren konnten, wimmelte es in den Hallen nur so von Dämonen. Sie brachten alle um, die sich ihnen in den Weg stellten. Ohne den Propheten wären wir wohl alle in diesem ersten Angriff gefallen.$B$BMeine Aufgabe war, die Kristallhalle zu evakuieren. So wurden meine Zerschlagenen und ich von der Hauptstreitmacht getrennt.$B$BWir zogen uns in die eingestürzte Halle zurück, um hier unser letztes Gefecht zu bestreiten.$B$BMit Eurer Hilfe könnten wir diese Invasion vielleicht doch noch überleben.', 'Verteidigt Scharfseher Nobundo gegen den Sturmangriff der Legion', '', '', '', '', '', 22522),
(43483, 'deDE', 'Teufelsvernichtung', '', '', '', '', '', '', '', '', 22522),
(44004, 'deDE', 'Bringer des Lichts', 'Findet den Zweck des mysteriösen lichtgebundenen Objekts heraus.', 'Turalyon lebt! Seit ewigen Zeiten war ich überzeugt, mein alter Freund hätte auf irgendeiner kargen Welt der Legion den Tod gefunden. Aber zu wissen, dass er noch lebt und sich in Gefahr befindet, ist einfach unerträglich.$B$BWir müssen uns beeilen, $n. Wir müssen den heiligen Zweck des Objekts herausfinden.$B$BDer Prophet, von dem Turalyon gesprochen hat, ist zweifellos der Anführer der Draenei, Velen. Nehmt das Objekt und begebt Euch zur Exodar. Dort findet Ihr den Propheten Velen.$B$BIch werde nun ein Portal für Euch öffnen. Nehmt meinen Signalgeber und benutzt ihn, wenn Ihr zurückkehren wollt.', '', '', '', '', '', '', 22522),
(40188, 'deDE', 'Kalt serviert', 'Tötet Ranaris in den Untiefen von El''dranil.', 'Mein Partner und ich wurden beide Opfer der Folgen der Zerschlagung. Wir wurden von einem Freund, dem wir vertrauten, betrogen und im Stich gelassen.$b$bDieser "Freund" ist vor Kurzem in den Ruinen im Osten von hier aufgetaucht. Sein Name ist Ranaris.$b$bGeht und rächt uns, und ich werde Euch mit einem Teil meiner Weisheit belohnen.', '', '', '', '', '', '', 22522),
(40176, 'deDE', 'Von Kopf bis Fuß', 'Stellt ein Paar robuste Steinbalgstiefel her.$b$bDazu werdet Ihr Namhas Gerbrahmen und Namhas Werkbank im Legendäre Leder verwenden müssen.', 'Ihr wart fleißig, $n. Und wie ich sehe, habt Ihr ein paar neue Muster entworfen.$b$bSollen wir noch eins hinzufügen?', '', '', '', '', '', '', 22522),
(41889, 'deDE', 'Nebel der Vergangenheit', 'Stellt einen Steinbalglederharnisch her.', 'Lasst uns die Sache angehen und diese Reittierrüstung herstellen, ja?', '', '', '', '', '', '', 22522),
(40189, 'deDE', 'Nagafachwissen', 'Besorgt $1oa Nagaschulterschutze.', 'Die Naga dieser Gegend tragen nur wenig Rüstung, aber was sie tragen, ist besonders haltbar.$b$bWenn Ihr ein stärkeres Schulterteil herstellen wollt, würde ich mir zunächst die Rüstung der Naga anschauen und dann ein Muster von ähnlicher Herstellungsweise fertigen.$b$bDie Stadt in der Nähe ist von Naga überschwemmt. Tötet sie und bringt mir ihre Rüstung, dann zeige ich Euch, was ich meine.', '', '', '', '', '', '', 22522),
(40030, 'deDE', 'Widderhornschaufel', 'Bringt die Widderhornschaufel zu Kuhuine Sanftschreiter in Dalaran.', 'Die Runenmarkierungen auf dem Griff des Werkzeugs lassen darauf schließen, dass es von einem Kräuterkundigen der Vrykul stammt.$b$bOffensichtlich sammeln auch die Vrykul diesen Fjarnskaggl. Eure Lehrerin, Kuhuine, erwähnte, dass sie früher etwas darüber gelesen habe.$b$bVielleicht möchte sie die Werkzeuge sehen, mit denen die Vrykul diese Pflanze ernten.', '', '', '', '', '', '', 22522),
(38907, 'deDE', 'Hüter des Hammers', 'Trefft Euch mit Mayla Hochberg in Donnertotem am Hochberg.', 'Die Hochbergstämme hatten vor, Euren Streitkräften unsere Hilfe zuzusichern, denn wir wissen, dass der Krieg mit der Brennenden Legion vor uns liegt.$b$bWir kämpfen jedoch gegen Verrat aus den eigenen Reihen und müssen nun Euch um Unterstützung bitten.$b$bEin wichtiges Relikt, der Hammer von Khaz''goroth, wurde meinen Leuten von den niederträchtigen Drogbar gestohlen.$b$bWir benötigen Eure Hilfe, um ihn zurückzuerobern.', '', '', '', '', '', '', 22522),
(44075, 'deDE', 'Essenz der Wildheit', 'Beschafft 1 Essenz der Wildheit von Fenryr in den Hallen der Tapferkeit und beweist dann Odyn Eure Würdigkeit.', 'Um die Essenz der Wildheit zu finden, müsst Ihr eine Kreatur aufsuchen, die schon Tausende getötet hat und selbst von den Göttern verehrt wird.$B$BIch spreche von niemand Geringerem als dem Wolf Fenryr, der in den ewigen Wäldern der Hallen der Tapferkeit jagt.', '', '', '', '', '', '', 22522),
(44077, 'deDE', 'Essenz des Gleichgewichts', 'Beschafft 1 Essenz des Gleichgewichts von Azsharas Zorn.', 'Im Auge Azsharas in Azsuna gibt es ein mächtiges Wesen namens Azsharas Zorn. Es ist eine Blasphemie der Natur, durchdrungen mit gestohlener arkaner Macht, deren Existenz wir nicht zulassen dürfen.$B$BTötet es. Vielleicht findet Ihr so die Essenz des Gleichgewichts, nach der Ihr sucht.', '', '', '', '', '', '', 22522),
(44074, 'deDE', 'Essenz der Hartnäckigkeit', 'Beschafft 1 Essenz der Hartnäckigkeit von Rokmora in Neltharions Hort und tötet dann Dargrul den Unterkönig.', 'Ich grüße Euch, $gErzdruide:Erzdruidin;. Ich hörte, dass Ihr auf der Suche nach der Essenz der Hartnäckigkeit seid.$B$BEs gibt nichts Robusteres und Unzerbrechlicheres als eine Kreatur aus Stein.$B$BIn Neltharions Hort gibt es ein Monster namens Rokmora. Tötet es und entzieht ihm die Essenz, die Ihr benötigt.', '', '', '', '', '', '', 22522),
(44076, 'deDE', 'Essenz des Nachwachsens', 'Beschafft 1 Essenz des Nachwachsens von Eichenherz im Finsterherzdickicht und besiegt dann den Schemen von Xavius.', 'Mein Kind, ich kann mir nur einen Ort vorstellen, an dem man die Essenz des Nachwachsens bekommen könnte.$B$BTief im Finsterherzdickicht befindet sich Eichenherz, ein treuer, uralter Verteidiger des Hains. Er ist kürzlich der Verderbnis des Alptraums anheimgefallen, aber tief im Innern seines Herzens kämpft er noch dagegen an.$B$BBeschafft die Essenz dieses großen Baumes, damit seine unsterbliche Seele zur Schaffung neuen Lebens verwendet werden kann.', '', '', '', '', '', '', 22522),
(43899, 'deDE', 'Rösser der Verdammten', 'Schließt 5 Missionen der Reihe "Gefallene Rösser" ab.', 'Salanar der Reiter ist gerade aus dem Schattenreich in der Schwarzen Festung eingetroffen. Er allein verfügt über das Wissen und die Macht, die Rösser der Vier Reiter zu beschwören.$b$bSalanar hat uns eine Liste der Dinge gegeben, die er für die Rösser benötigt. Ich habe daher eine Reihe von Missionen zusammengestellt, um diese Gegenstände zu besorgen.$b$bÜber den Missionstisch könnt Ihr die Aufgaben Euren bevorzugten Champions zuweisen.', '', '', '', '', '', '', 22522),
(42037, 'deDE', 'Versammlung der Traumweber', 'Sammelt Druiden, indem Ihr Missionen der Reihe "Versammlung der Traumweber" abschließt.', 'Nachdem Ihr den Götzen der Wildnis bergen konntet, wird es jetzt Zeit, dass wir Kontakt mit Malorne aufnehmen. Allerdings wird dieses Ritual die vereinte Kraft vieler Druiden in Anspruch nehmen.$B$BEntsendet Eure Champions, um den Druiden unseres Ordens mitzuteilen, dass wir uns bei Nordrassil versammeln werden. Wir alle werden uns daran beteiligen müssen.', '', '', '', '', '', '', 22522),
(42038, 'deDE', 'Champion: Broll Bärenfell', '', '', '', '', '', '', '', '', 22522),
(42039, 'deDE', 'Champion: Sylendra Hainsang', '', '', '', '', '', '', '', '', 22522),
(40654, 'deDE', 'Druiden der Klaue', 'Sprecht mit Yaris Dunkelklaue.', 'Ein neuer Verbündeter hat sich uns angeschlossen. Sein Name ist Yaris Dunkelklaue und er ist ein erfahrener Ausbilder der Druiden der Klaue.$B$BGeht und sprecht jetzt mit ihm.', '', '', '', '', '', '', 22522),
(43991, 'deDE', 'Die Beschützer', 'Verwendet Eure Druidenkräfte, um mit den Beschützern am Stein der Wildheit, am Wächterstein, am Stein der Wiederherstellung und am Stein des Gleichgewichts zu kommunizieren.', 'Um die Kräfte des Götzen vollständig zu entfesseln, benötigen wir vier Manifestationen der Wildnis: eine Essenz der Wildheit, eine Essenz des Nachwachsens, eine Essenz des Gleichgewichts und eine Essenz der Hartnäckigkeit.$B$BUm diese Essenzen zu finden, müssen wir die Beschützer des Waldes konsultieren.$B$BBegebt Euch zum Smaragdgrünen Traumpfad und ruft die Mächte der uralten Druidensteine an. Die Beschützer werden sich nur jenen zeigen, die ihre Verbindung mit der Wildnis unter Beweis stellen können.', '', '', '', '', '', '', 22522),
(42884, 'deDE', 'Entwurzelung', 'Nutzt den Stab des alten Graurückens, um $1oa verworrene Dornengestrüppe in Grauholz zu vernichten.', 'Als ich zu Hause ankam, war die Eingangstür durch magische Wurzeln versperrt.\n\nIch konnte sie entfernen, doch nur wenige Anhänger meines Stammes verfügen über die Fähigkeiten, es mir gleichzutun.\n\nNehmt meinen Stab. Seine Macht sollte die störenden Wurzeln mit Leichtigkeit entwirren.', '', '', '', '', '', '', 22522),
(42865, 'deDE', 'Greller Wahnsinn', 'Besiegt Enderkin in Grauholz.', 'Der Anführer der Grell muss aus dem Weg geräumt werden.\n\nIhr könnt ihn an seiner weißen Haut und seiner scharfen Zunge erkennen.', '', 'Der Anführer der Grell. Oberster Missetäter. Brutal und unberechenbar. Schandmaul.', 'Enderkin', '', '', '', 22522),
(42883, 'deDE', 'Die Grell sind los', 'Tötet $1oa irre Untergrell in Grauholz.', 'Die Grell sind rasend. Sie greifen meine Brüder an und zerstören unser Dorf!\n\nBitte, $gFremder:Fremde;, helft uns, diese niederträchtigen Übeltäter unter die Erde zu befördern.', '', '', '', '', '', '', 22522),
(42857, 'deDE', 'Feuchtes Fresschen', 'Sammelt $1oa feuchte Graukämme in Grauholz.', 'Es scheint, als würden die Grell sich von diesen Früchten ernähren.\n\nDer alte Graurücken wäre sicher erfreut, wenn sie aus dem Dorf entfernt werden würden.', '', '', '', '', '', '', 22522),
(42036, 'deDE', 'Götze der Wildnis', 'Greift 6 Satyrn der Schattenteufel an, um von ihnen Informationen über den Götzen der Wildnis zu erhalten.', 'Diesem hier konnten wir ein Geständnis abringen. Wir wissen jetzt, dass ihr Anführer den Götzen in das Mondklauental verschleppt hat, aber nicht, wo genau er ihn versteckt.$B$BEiner der Satyrn wird sicherlich wissen, wohin genau er gebracht wurde.$B$BBringt diese Satyrn dazu, Euch Genaueres zu verraten... und wenn es das Letzte ist, was sie tun.', '', '', '', '', '', '', 22522),
(42035, 'deDE', 'Den Feind verfolgen', 'Verwendet Eure Katzengestalt, um der Satyrspur zu folgen.', 'Auch wenn der Götze gestohlen wurde, gibt es vielleicht noch Hoffnung.$B$BAls ich im Schrein gefangen war, habe ich gesehen, wie sich die räuberischen Satyrn mit ihm davongemacht haben. Ihre Spur ist sicher noch ganz frisch.$B$BMit unseren geschärften Sinnen sollten wir sie ausfindig machen können.$B$BWir müssen ihrer Spur durch den Wald folgen und den Götzen zurückholen!', '', '', '', '', '', '', 22522),
(42034, 'deDE', 'Umklammerung des Alptraums', 'Tötet 5 Räuber der Schattenteufel, rettet Sylendra Hainsang und untersucht den Altar von Malorne.', 'Die meisten Druiden konnten entkommen, aber sie sagen, dass sich immer noch unzählige Feinde am Schrein aufhalten würden.$B$BDie Anführerin des Schreins, Sylendra Hainsang, ist zurückgeblieben, um Malornes Götzen zu verteidigen. Wir müssen ihr zu Hilfe eilen.', '', '', '', '', '', '', 22522),
(42438, 'deDE', 'Saat der Erneuerung', 'Delandros Schimmermond möchte, dass Ihr die Wälder des Nebelgrunds absucht und die drei Samenkörner von Tel''andu beschafft.', 'Die Fänge von Ashamane sind in Gefahr, $n. Seit Zehntausenden von Jahren werden sie von einem mächtigen Zauberschutz vor Dieben geschützt. Aber im Laufe der Zeit hat dieser Schutz so weit nachgelassen, dass er womöglich versagen könnte. Ich habe meinen drei treuesten Äschernen die Samenkörner von Tel''andu übergeben, die wir für das Ritual der Erneuerung benötigen. Aber die Wälder wimmeln jetzt nur so von Dämonen und ich fürchte, dass die drei gefallen sind.$b$bWir dürfen die Fänge auf keinen Fall der Legion überlassen! Geht und findet die Samenkörner von Tel''andu. Wir müssen uns beeilen, bevor der Schutz durchbrochen werden kann!', '', '', '', '', '', '', 22522),
(42439, 'deDE', 'Hilfe für die Äschernen', 'Besiegt $1oa Seelenfolterer der Eredar und befreit ihre Gefangenen.', 'Richtet Euren Blick nach oben, dann seht Ihr den Grund für unsere Misere. Seit dieses Schiff aufgetaucht ist, sind die Angriffe immer unerbittlicher geworden. Viele der Äschernen sind gefangen genommen worden und ich kann ihre gequälten Schreie bis hier hören.$b$bBitte zieht los und rettet so viele meiner Leute, wie Ihr nur könnt. Das Schicksal, das ihnen in den Händen der Legion droht, haben sie nicht verdient.', '', '', '', '', '', '', 22522),
(42033, 'deDE', 'Malornes Zuflucht', 'Sprecht mit Broll Bärenfell bei Malornes Zuflucht in Val''sharah.', 'Ich verstehe... Es ist so, wie wir immer gefürchtet hatten. Der Smaragdgrüne Alptraum hat unsere Welt gefunden.$B$BWir müssen den uralten Gott Malorne anrufen.$B$BReist nach Südosten zum Schrein von Malornes Zuflucht. Dort halten wir Druiden seit Tausenden von Jahren ein heiliges Relikt versteckt, ein Geschenk des weißen Hirsches selbst.$B$BIch habe bereits Broll Bärenfell vorausgeschickt, um Euch anzukündigen.', '', '', '', '', '', '', 22522),
(42031, 'deDE', 'Übles Wachstum', 'Trefft Euch mit Naralex, pflanzt den Peitschersamen ein und analysiert die Effekte.', 'Naralex hat Euch gebeten, zu ihm zu kommen. Er ist bereit, die Probe zu analysieren, die er von Shaladrassil zurückgebracht hat.', '', '', '', '', '', '', 22522),
(38778, 'deDE', 'Dreht die Schlüssel', 'Aktiviert die Runensteine im Runenwald.', 'Meine Wurzeln verwittern... meine Äste sterben ab...\n\nDie Knochensprecher wollen den Runenwald verstummen lassen. Die Runensteine sind der Schlüssel...\n\nDreht die Runensteine... Befreit mich von den Fesseln...', '', '', '', '', '', '', 22522),
(39796, 'deDE', 'Nach Haustvald', 'Trefft Euch mit Vydhar im Runenwald.', 'Eure nächsten Schritte sind eindeutig. Eure Füße kennen den Weg. Folgt ihnen nach Haustvald, um Euch dort Eurem Schicksal zu stellen!\n\nEs ist ein Baum, nach dem Ihr sucht, das Herz des mystischen Runenwalds. Er wird Rat für Euch haben... und womöglich ein paar Blätter.', '', '', '', '', '', '', 22522),
(39804, 'deDE', 'Wo wir von Knochen sprechen...', 'Helft Havi, den Elch zu schlachten, und erfahrt mehr über Eure nächste Prüfung.', 'Die Prüfung des Mutes wird Eure nächste Aufgabe sein. Und das wird nicht leicht!\n\nAber nur Narren kämpfen mit leerem Magen. Seid Ihr etwa $gein Narr:eine Närrin;, $gFremder:Fremde;?\n\nNein? Dann geht einem alten Greis zur Hand und schlachtet diesen Elch für unser Mahl. Und ich werde Euch eine Geschichte erzählen, von Felsen, Bäumen und Geistern.', '', '', '', '', '', '', 22522),
(39803, 'deDE', 'Die Prüfungen gehen weiter', 'Trefft Euch mit Havi in Valdisdall.', '<Havis Stimme hallt erneut durch Euren Kopf, während der Rabe Euch weiter anstarrt.>\n\nUm Mut zu finden, müsst Ihr Zwiesprache mit den Knochen halten!\n\nDoch zuvor müsst Ihr Euch vorbereiten!', '', '', '', '', '', '', 22522),
(38624, 'deDE', 'Donner des Schmerzes', 'Fliegt mit Vethir und tötet 150 Verbündete des Gottkönigs in Hrydshal.', 'Ihr habt Euch als $gwürdiger Verbündeter:würdige Verbündete; der Thorignir erwiesen. Wir werden Euch unsere Stärke verleihen. Zuvor habe ich allerdings noch ein Anliegen an unseren neuen Champion.\n\nDer Drache, der Euch zu mir geführt hat, wartet nur darauf, blutige Rache an den Vrykul zu üben. Ich möchte, dass Ihr diesen Drekirjar gemeinsam zeigt, was es bedeutet, den Zorn der Thorignir zu wecken. Egal ob sie Teufelsskorn sind oder nicht, ihre Bosheit hat meiner Brut Leid zugefügt. Ihre Schuld muss mit Blut bezahlt werden.\n\nSprecht mit Vethir, wenn Ihr bereit seid.', '', '', '', '', '', '', 22522),
(39652, 'deDE', 'Drachenherrschaft', 'Sprecht mit Vethir, um zum Gipfel des Thorimshorns zu fliegen.', 'Dank Eurer Hilfe konnte Thrymjaris aus den Klauen dieses grässlichen Dämons entkommen. Sie bat mich, auf Euch zu warten und Euch zu ihr zu bringen, wenn Ihr Eure Feinde ausgelöscht habt.\n\nSie hat unsere Brut am Gipfel des Thorimshorns versammelt und plant einen Gegenangriff.\n\nWürdet Ihr uns dabei unterstützen?', '', '', '', '', '', '', 22522),
(40568, 'deDE', 'Zorn des Sturms', 'Tötet 10 Teufelsskornvrykul am Thorimshorn.', 'Die Teufelsskorn, die sich über meine Brut hermachen, gehörten einst zu den Drekirjar, einem Stamm, mit dem wir einen uralten Pakt geschlossen hatten. Unter dem Einfluss des Gottkönigs vergessen sie nun ihren Eid und greifen die Vorfahren ihrer geliebten Drachen an.\n\nFür diesen Affront müssen sie büßen.\n\nErweckt Euren Zorn und tragt den Kampf zu ihnen, $C!', '', '', '', '', '', '', 22522),
(38413, 'deDE', 'Schwingen der Freiheit', 'Befreit Aleifir, Hrafsir, Erilar und Hridmogir am Thorimshorn.', 'Als die Teufelsskorn das Nest angriffen, haben die Thorignir tapfer gekämpft, um unsere Brut zu schützen. Aber ihre dämonischen Verbündeten waren zu stark, und so sind viele von uns gefangen genommen worden.\n\nWährend wir hier sprechen, versuchen sie, meine Brüder und Schwestern zu verderben und auf ihre Seite zu ziehen.\n\nIch bitte Euch, $gFremder:Fremde;, bewahrt sie vor diesem Schicksal!', '', '', '', '', '', '', 22522),
(38414, 'deDE', 'Herz eines Drachen', 'Sucht nach der Mutter der Thorignirbrut und rettet sie.', 'Mithilfe brutaler Gewalt und Verderbnis hat der Gottkönig die Macht an sich gerissen, die Ihr sucht. Aber vielleicht gibt es noch Hoffnung für Euch und die Thorignir, $C.\n\nDie Teufelsskorn nutzen teuflische Mächte, wie ich sie noch nie gesehen habe, um unsere Nachkommen zu verderben und die Thorignir zu versklaven. Ihr Anführer, ein mächtiger Dämon, hat unsere Mutter entführt und will sie dem Willen des Gottkönigs unterwerfen.\n\nFindet sie und befreit sie aus den Fängen des Dämons. Nur dann können die Thorignir ihre Macht zurückerlangen und Euch helfen.', '', '', '', '', '', '', 22522),
(38618, 'deDE', 'Über dem Wintermondlicht', 'Hangelt Euch vom Turm hinüber und steigt dann zum Gipfel des Gebirgspfads hoch.', 'Gleich da drüben scheint eine gute Stelle zum Abfeuern des Werfers zu sein. Auf diesem Pfad müsstet Ihr bis ganz nach oben kommen.\n\nLaut meinen Informationen findet Ihr da oben Eure Drachen. Ich hoffe nur, dass sie überhaupt noch in der Lage sind, Euch zu helfen.', '', '', '', '', '', '', 22522),
(38617, 'deDE', 'Ein anderer Weg', 'Besiegt den Champion der Drekirjar in Hrydshal und steigt dann zur Spitze des Turms hoch.', 'Moment! Habt Ihr noch den Greifhakenwerfer? Wenn Ihr den von der Spitze des Turms abfeuert, könntet Ihr wohl immer noch den Pfad erreichen. Der Abstand scheint dort nicht allzu groß zu sein.\n\nAber natürlich müssen wir noch den großen Typen davor ausschalten. Der sieht übel aus, aber wir sollten das schaffen, wenn wir ihn von zwei Seiten angreifen.', '', '', '', '', '', '', 22522),
(38615, 'deDE', 'Speerabwehr', 'Nutzt einen Greifhaken, um auf die Mauern von Hrydshal zu gelangen, und zerlegt 7 Harpunenschleudern der Wellenskorn.', 'Die Naturgewalten scheinen nicht Eure einzigen Feinde auf dem Pfad da oben zu sein. Diese Harpunenschleudern sehen nämlich auch ganz schön garstig aus. Wenn wir die nicht ausschalten, können sie jeden aufspießen, der den Pfad erklimmen will.\n\nSie sind dort oben auf den Mauern, also müsst Ihr den Greifhakenwerfer benutzen, um sie zu erreichen. Passt nur auf, dass Ihr nicht als Drachenfutter endet!', '', '', '', '', '', '', 22522),
(38614, 'deDE', 'Wetterfeste Ausrüstung', 'Sammelt Kletterschuhe, einen gefetteten Umhang und einen verzauberten Leitstein in Hrydshal.', 'Ich habe hier ein bisschen herumgeschnüffelt und dabei festgestellt, dass diese Vrykul selbst öfter den Gebirgspfad beschreiten, den wir erklimmen wollen. Und sie haben dafür sogar spezielle Ausrüstung, die Ihr ihnen vielleicht abnehmen könnt.\n\nDa gibt es zum Beispiel Kletterschuhe für besseren Halt und gefettete Umhänge, um trocken zu bleiben. Und sie führen auch Steine bei sich, mit denen man der Gefahr durch Blitze Herr werden kann. So einen solltet Ihr Euch auch zulegen.', '', '', '', '', '', '', 22522),
(38374, 'deDE', 'Angriff auf die Drekirjar', '', '', '', '', '', '', '', '', 22522),
(38613, 'deDE', 'Wer braucht schon Flügel?', 'Verwendet den sturmgeschmiedeten Greifhakenwerfer, um die Mauern von Hrydshal zu erklimmen.', 'Das Ding scheint über genug Reichweite zu verfügen, um uns über die Mauer zu bringen. Es gibt dafür eine gute Stelle gleich hinter uns. Ihr solltet sie von hier aus schon sehen können.\n\nUnd wenn Ihr drinnen seid, tut mir einen Gefallen und lenkt die Wachen ab, damit ich mich vorbeischleichen kann. Ich versuche dann, etwas mehr über diese Vrykul herauszufinden.', '', '', '', '', '', '', 22522),
(38616, 'deDE', 'Schuppe um Schuppe', 'Sammelt Sturmdrachenschuppen von den Sturmdrachen in Hrydshal ein.', 'Die Vrykul in dieser Gegend scheinen ihre Rüstungen aus den Schuppen dieser Drachen herzustellen. $B$BWenn Ihr genug Material von den hiesigen Drachen zusammenbekommt, könnt Ihr vielleicht etwas Nützliches daraus machen.', '', '', '', '', '', '', 22522),
(38612, 'deDE', 'Greifhaken abgreifen', 'Nehmt den sturmgeschmiedeten Greifhakenwerfer und trefft Euch mit Schreckensreiterin Cullen außerhalb der Mauern von Hrydshal.', 'Also erhalten wir Hilfe von irgendeinem alten magischen Vrykul? Das klingt schon mal gut, aber Eure Prüfung könnte kniffelig werden. Seht Ihr, der einzige Weg auf den Berg führt durch diese hohen Mauern.\n\nJa, die mit den fies aussehenden Drachen oben drauf.\n\nIch habe mich schon ein wenig umgesehen, doch da diese Drachen die arme Herzogin als Mittagessen auserkoren haben, kam ich nicht weit. Die Vrykul haben sie trainiert und das verheißt nichts Gutes.\n\nIch sage, wir schleichen uns rein. Ich habe mir schon ein paar Greifhakenwerfer besorgt. Nehmt Euch einen und trefft mich außerhalb der Stadtmauern.', '', '', '', '', '', '', 22522),
(38611, 'deDE', 'Der Wille der Thorignir', 'Trefft Euch außerhalb von Hrydshal mit Schreckensreiterin Cullen, um mit der Prüfung der Thorignir zu beginnen.', 'Wo die Stürme am heftigsten tosen, erwarten Euch die Drachen. Sie sind Nachkommen der Götter und werden Eure Willensstärke auf die Probe stellen.\n\nSie leben oberhalb von Hrydshal, wo ihre Kinder den Drekirjar dienen. Allerdings sind sie keine Verbündeten der Menschen oder der Vrykul. Daher müsst Ihr versuchen, ihre Gunst zu gewinnen.\n\nEine Freundin erwartet Euch in Hrydshal und ist zweifelsohne auf der Suche nach Antworten, die Ihr nun habt. Geht zu ihr und stellt Euch gemeinsam der Macht des Sturms!', '', '', '', '', '', '', 22522),
(39597, 'deDE', 'Der Segen der Wächter', 'Sprecht mit Yotnar, um den Segen der Wächter zu empfangen.', 'Da Ihr die Prüfung der Macht bestanden habt, gewähre ich Euch den Segen der Wächter.\n\nZudem werde ich Euer Artefakt mit der Kraft des Kammerwächters versehen, damit Ihr in den weiteren Prüfungen bestehen könnt. Ich hoffe, dass es Euch von Nutzen sein wird.\n\nSprecht mich an, wenn Ihr bereit seid, dieses Geschenk zu empfangen.', '', '', '', '', '', '', 22522),
(39594, 'deDE', 'Eine Prüfung der Macht', 'Aktiviert das Archivgerät, um mehr über die Prüfung der Macht zu erfahren.', 'Ich schulde Euch Dank dafür, dass ich jetzt wieder vollständig bin, $gFremder:Fremde;. Ihr seid dieser Prüfung zweifellos würdig.\n\nBetretet jetzt die Kammer. Dort findet Ihr das Archiv für Eure erste Prüfung.\n\nIch treffe Euch im Inneren.', '', '', '', '', '', '', 22522),
(39592, 'deDE', 'Eine Prüfung des Willens', 'Fahrt das Archivgerät hoch und seht Euch die holografische Aufzeichnung für die Prüfung des Willens auf der Terrasse der Hüter an.', 'Auf dem höchsten Punkt der Ebene über Euch findet Ihr das Archivgerät für die Prüfung des Willens. Sucht es auf, um zu sehen, was Euch am Thorimshorn erwartet.\n\nUnd passt auf Euch auf, $gFremder:Fremde;. Dieser andere Kandidat, Skovald, hat viele Soldaten seiner Armee zurückgelassen, und sie werden Euch ganz sicher nicht wohlgesonnen sein.', '', '', '', '', '', '', 22522),
(39593, 'deDE', 'Der gespaltene Wächter', 'Sammelt die Bruchstücke des Körpers von Yotnar auf der Terrasse der Behüter ein.', 'Nachdem Euer Widersacher – der, den sie Skovald nennen – seine erste Prüfung abgelegt hatte, setzte er seine verderbten Kräfte ein, um meinen weltlichen Körper zu zerschmettern.\n\nIch kann es keinem Kandidaten gestatten, die Kammer zu betreten, solange ich nicht vollständig bin.\n\nSeine Truppen haben meine Bruchstücke sicher auf dem ganzen Plateau über uns verteilt, während sie es besudelten. Bringt sie mir zurück, sodass ich Euch ordnungsgemäß Eurer Prüfung unterziehen kann.', '', '', '', '', '', '', 22522),
(39591, 'deDE', 'Eine Prüfung des Mutes', 'Fahrt das Archivgerät hoch und seht Euch die holografische Aufzeichnung für die Prüfung des Mutes auf der Terrasse der Behüter an.', 'Um die Aegis von Aggramar zu erlangen, muss ein Champion drei große Prüfungen bestehen, um dann durch die Pforte der Tapferkeit zu reiten und sich in einer glorreichen Schlacht zu beweisen. Hier findet Ihr das Wissen über die erforderlichen Prüfungen und die erste Herausforderung, der Ihr Euch stellen müsst.\n\nÜber uns, als Zierde der mittleren Ebene, findet Ihr das Archiv der Prüfung des Mutes. Sucht es auf, um mehr darüber zu erfahren, wohin Euer Weg Euch führt.', '', '', '', '', '', '', 22522),
(38442, 'deDE', 'Ein würdiger Herausforderer', '', '', '', '', '', '', '', '', 22522),
(39590, 'deDE', 'Der Konkurrenz voraus', 'Sammelt die Köpfe der Champions der Bauchreißer, Trampelhufe und Teufelsskorn im Tal des Schwertes ein.', 'Der Wächter Yotnar bewacht die Kammer und gilt als besonders wählerisch. Die Herausforderer kommen von nah und fern, um die Prüfungen zu bestehen, aber der Wächter gibt vor, sie nicht zu sehen.\n\nWenn Ihr seine Aufmerksamkeit erregen wollt, müsst Ihr beweisen, dass Ihr es wert seid, angesehen zu werden.\n\nEinige der Herausforderer warten im Lager im Tal des Schwertes auf eine Gelegenheit. Bringt Yotnar ihre Köpfe, damit er sie sich ansehen kann... und Euch.', '', '', '', '', '', '', 22522),
(38331, 'deDE', 'Havis Test', 'Werft Zutaten aus dem Korb voller Wurzelgemüse, dem Fass mit Krabben und herzhaftes Vrykulgetreide in Havis Eintopf.', 'Gleich bei Eurem Eintreffen in diesem Land konnte ich Eure Macht spüren. Ihr verfolgt ein großes Ziel und tragt eine schwere Bürde.\n\nDie Aegis von Aggramar wird in den heiligen Hallen des allmächtigen Vaters verwahrt. Nur wenn Ihr Eure Tapferkeit unter Beweis stellt, könnt Ihr sie vor Euren Feinden beanspruchen.\n\nDazu müsst Ihr viele Prüfungen bestehen, aber zunächst muss ich mich Eurer Stärke vergewissern. Mein Eintopf ist noch nicht fertig und meine alten Knochen können die Zutaten nicht den Hügel hinaufschleppen.\n\nIhr seht so aus, als ob Ihr starke Arme habt. Würdet Ihr einem alten Mann helfen, seinen Eintopf fertig zu kochen?', '', '', '', '', '', '', 22522),
(38317, 'deDE', 'Meister der Tarnung', 'Schaltet Meisterspionin Illandris und Meisterspion Shwayder aus.', '<Ihr überfliegt Kloppfeifers Bericht.>$B$BMein Fallschirm ist von einem Schrapnell beschädigt worden, und ich habe eine harte Landung in einem Rabenbärennest hingelegt. Zum Glück haben ein paar Eier meinen Sturz abgefangen.$B$BIllandris und Shwayder habe ich aus den Augen verloren. Sie sind beide Meister der Tarnung, und ich bin mir sicher, dass man sie nicht entdecken wird.$B$BIch werde weiterhin die feindlichen Aktivitäten beobachten. Ich muss nur noch das ganze Eigelb von meinem Tarngerät entfernen. Ich hoffe, die Mutter treibt sich nicht hier herum...', '', '', '', '', '', '', 22522),
(39775, 'deDE', 'Sturmgeschmiedeter Greifhakenwerfer', '', '', '', '', '', '', '', '', 22522),
(38308, 'deDE', 'Augen in der Warte', 'Sichert die Geheimdienstinformationen des SI:7 von Meisterspion Kloppfeifer an der Schwarzschnabelwarte.', 'Fallschirmeinheiten der Allianz sind von der Himmelsfeuer abgesprungen und auf der Schwarzschnabelwarte gelandet.$B$BWährend des Überfluges konnte ich ihre Anzahl und Positionen zwar nicht bestimmen, aber ich habe ihre Fallschirme in den Bäumen und EINE MENGE Rabenbären am Boden gesehen.$B$BWenn sie noch da oben sind, verstecken sie sich dort und behalten uns im Auge.$B$BIch habe einen Reiter über der Warte als Wache abgestellt, während ich weiter nach Sturmheim eile.$B$B- Liza Cullen, Schreckensreiterin, 1. Geschwader', '', 'Offensichtlich hat Cullen diesen Bericht für Nathanos hinterlassen und sich dann auf eine andere Mission begeben.', 'Schreckensreiterin Cullen', '', '', '', 22522),
(38459, 'deDE', 'Die uralten Prüfungen', 'Sucht nach Havi in der Nähe von Aggramars Kammer.', '<Während Euch der Rabe in die Augen starrt, dringt eine seltsame Stimme in Euren Geist.>\n\nIch weiß, wer Ihr seid, Champion, und ich weiß, wonach Ihr sucht.\n\nKommt zu mir, wenn Ihr Euch selbst als würdig erachtet.', '', 'Dieser seltsame alte Vrykul scheint Euch irgendwoher zu kennen...', 'Havi', '', '', 'Sprecht mit Havi nahe Aggramars Kammer.', 22522),
(38362, 'deDE', 'Eine grausame Trophäe', 'Schlagt Than Sturmhimmel den Kopf ab.', 'Es hat den Anschein, dass Sylvanas nicht will, dass man ihr folgt... weder wir noch andere.$B$BUnd ihr Wille zählt. Ich habe ihn nicht zu hinterfragen, sondern ihm nur zu gehorchen.$B$BIn diesen nahe gelegenen Hügeln bereitet sich gerade ein Greifenmeister auf den Abflug vor. Ich gehe davon aus, dass er die Königin aus der Luft suchen will. Meine Waldläufer sind schon in Stellung gegangen. Sie warten nur noch auf den richtigen Moment.$B$BWenn dieser Auftrag erledigt ist, will ich seinen Kopf als Trophäe.', '', '', '', '', '', '', 22522),
(38361, 'deDE', 'Der Zorn des Pestrufers', 'Tötet $1oa Fallschirmjäger der 7. Legion oder Greifenreiter der Himmelsfeuer.', 'An diesem Brocken werden sich die Allianzhunde verschlucken. Sie haben die Bansheekönigin angegriffen und sich den Zorn ihres Champions zugezogen!$B$BGenau wie meine Bogeneinheiten, Axtkämpfer und Höllenhunde werdet Ihr ein Werkzeug meiner Rache sein. Ich will, dass Ihr Leichenberge hinterlasst, $C. Und ich werde das Gleiche tun. Wenn wir uns wiedertreffen, will ich Blut an Euren Händen sehen.', '', '', '', '', '', '', 22522),
(38360, 'deDE', 'Das Schicksal der Windläufer', 'Sucht am Wrack der Windläufer und an der nahe gelegenen Küste nach Anzeichen auf Sylvanas.', 'Sylvanas zu finden, hat jetzt oberste Priorität!$B$BWir können einen größeren Suchbereich abdecken, wenn wir uns aufteilen. Ich werde mit einem Team in die Hügel über dem Lager der Feinde gehen, und Ihr untersucht das Wrack selbst nach Anzeichen der Besatzung.$B$BWenn wir da oben fertig sind, treffen wir uns am anderen Ende des Strandes.', '', '', '', '', '', '', 22522),
(38357, 'deDE', 'Mögliche Nebenwirkungen: leichtes Untotsein', 'Tötet $1oa instabile Bären oder Seemänner.', 'Die Effekte dieses "Nebenprodukts" sind recht drastisch.$B$BAufgrund der rapiden Zellendegeneration werden die Subjekte blitzschnell in willenlose Hüllen verwandelt, die nur noch von blindem Hass auf alles Lebende angetrieben werden.$B$BWie Ihr Euch sicher vorstellen könnt, hat die lokale Fauna einigen Schaden genommen. Oh, und auch der Großteil der Besatzung...$B$BAuf jeden Fall wünsche ich Euch viel Glück!', '', '', '', '', '', '', 22522),
(38358, 'deDE', 'Immer rein damit', 'Füllt den Sicherheitsbehälter mit instabilem Abfall.', 'Die Schwarze Rose war ein mobiles Marinelabor der Königlichen Apothekervereinigung. Und natürlich hatten wir auch ein paar... instabile "Nebenprodukte" an Bord.$B$BBedauerlicherweise ist eine dieser Substanzen während des Gefechtes mit dem Allianzschlachtschiff über Bord gegangen. Und es hat die Küste ganz schön hart getroffen.$B$BDie K.A.V. muss sich darum kümmern, bevor wir weiter ins Inland drängen.$B$BBitte nehmt Euch einen Sicherheitsbehälter und füllt ihn auf. Dann werden wir uns später um die korrekte Entsorgung kümmern.', '', '', '', '', '', '', 22522),
(38332, 'deDE', 'Der Waldläuferlord', 'Meldet Euch bei Nathanos Pestrufer.', 'Schön, dass Ihr das Gemetzel beim Wrack überlebt habt. Nathanos hat es auch geschafft, aber jetzt ist sein Gesicht noch hässlicher als vorher.$B$BEr ist mit einer Gruppe Waldläufer nach Norden gegangen, um nach Sylvanas zu suchen. Seit dem Kampf hat sie niemand mehr gesehen.$B$BEr lässt ausrichten, dass Ihr ihm folgen sollt, wenn Ihr wieder auf den Beinen seid.', '', '', '', '', '', 'Meldet Euch bei Nathanos Pestrufer.', 22522),
(39801, 'deDE', 'Die zersplitterte Flotte', 'Schließt Sylvanas'' Mission ab, um die Flotte nach Sturmheim zu bringen.', 'Die Vorbereitungen wurden getroffen und wir sind bereit, die Segel zu setzen.\n\nWir müssen sofort zuschlagen. Trödelt nicht, sonst bleibt Ihr zurück.', '', '', '', '', '', '', 22522),
(43750, 'deDE', 'Der Ruf der Schlacht', 'Sprecht mit Hymdall.', 'Einst handelten die mächtigen, von den Titanen geschaffenen Hüter von Ulduar und Odyn als Einheit. Seit Odyn alles hinter sich gelassen hat, um die Valarjar zu erschaffen, wird nicht einmal mehr Odyns Sohn Thorim auf den Rat seines Vaters hören.$B$BEs gibt allerdings eine Hoffnung, die Mächte der Himmelsfeste und Ulduar wiederzuvereinen.$B$BDas Gjallarhorn war ein Geschenk von Hymdall vor Odyns Weggang. Die Hüter von Ulduar sind durch einen Eid verpflichtet, dem Ruf des Gjallarhorns zu folgen, wenn er ertönt, da dies das Signal wäre, dass das Ende der Zeit angebrochen ist!', '', '', '', '', '', '', 22522),
(42032, 'deDE', 'Proben des Alptraums', 'Schließt die Mission "Proben des Alptraums" ab.', 'Es gibt noch vieles, das wir über den Alptraum nicht wissen.$B$BBevor wir ihn bekämpfen können, müssen wir begreifen, wie er sich verbreitet.$B$BLasst uns unsere Champions zu den Wurzeln von Shaladrassil in Val''sharah schicken, um sie einige Proben holen zu lassen, die wir untersuchen können.', '', '', '', '', '', '', 22522),
(42588, 'deDE', 'Buschfunk', 'Sprecht mit Blattbart dem Erzähler im Hain der Träume in Val''sharah.', 'Blattbart wird nicht umsonst "der Erzähler" genannt.$B$BEs heißt, er kennt sogar noch Geschichten aus der Zeit vor der Zerschlagung. Wenn er das von unseren Champions geborgene beschriebene Blatt studiert hat, kann er daraus vielleicht neues Wissen und Verbesserungsvorschläge für den Hain ableiten.$B$BBringt es ihm jetzt.', '', '', '', '', '', '', 22522),
(38721, 'deDE', 'Leutnant des Turms', 'Tötet Desdel Sternauge.', 'All Eure Vermutungen waren korrekt. Gul''dan hat Lord Rabenkrone und seine geisterhafte Armee aus dem Grab erweckt. Jetzt dienen sie der Legion und bewachen die Geheimnisse des Turms.$B$BWährend Rabenkrone von oben das Kommando führt, hat Leutnant Desdel Sternauge die Aufsicht über den Kerker.$B$BEr hat mich die letzten Monde über gefoltert, aber ich blieb unbeugsam.$B$B<Maiev lässt einen Finger über ihre geschwungene Klinge gleiten.>$B$BSein Leben muss enden.', '', 'Vizekommandant unter Lord Rabenkrone', 'Leutnant Desdel Sternauge', '', '', '', 22522),
(38719, 'deDE', 'Freiheit für die Illidari', 'Befreit Sirius Schwarzschwinge, Cassiel Nachtdorn und Asha Rabenruf.', '<Der Dämonenjäger der Illidari blutet aus mehreren grässlichen Wunden. Offensichtlich ist er hier zum Sterben zurückgelassen worden.>$B$BIm Gefängnis sind noch drei meiner... <hust>... Kameraden.$B$BWir sind auf unserer Patrouille hier in einen Hinterhalt geraten.$B$BFür mich besteht keine Hoffnung mehr, aber bitte rettet die anderen.', '', '', '', '', '', '', 22522),
(38724, 'deDE', 'Bruderliebe', 'Sucht Maiev Schattensang im Kerker der Rabenwehr.', 'Maiev muss irgendwo in diesem Kerker sein. Auf dem Weg hierher fand ich ihre abgestreifte Rüstung.\n\nWir sollten uns aufteilen, um die Zellen schneller abzusuchen.$B$BWir müssen sie finden.', '', '', '', '', '', '', 22522),
(38717, 'deDE', 'Kerker der Rabenwehr', 'Tötet Araxxas und nehmt die Gefängnisschlüssel an Euch.', 'Ich bin nicht den ganzen Weg hierhergekommen, um sie im letzten Moment noch zu verlieren.$B$BMaiev muss sich in den Kerkerzellen unterhalb der Rabenwehr befinden.$B$BIch habe mich da oben umgesehen: Der Kerkereingang wird von einem Furcht erregenden Kerkermeister bewacht.$B$BIch werde mich vorbeischleichen und die Wachen im Inneren ausschalten. Besiegt Ihr Araxxas, nehmt ihm die Schlüssel ab und trefft mich dann drinnen.', '', 'Er hat den Hauptschlüssel.', 'Araxxas', '', '', '', 22522),
(38715, 'deDE', 'Die Rabenwehrwache', 'Tötet Kester Weitsucher, Trelan Schildbrecher und Starlys Starkbogen.', 'Die Rabenwehr ist nicht mehr so, wie ich sie einst kannte. Mit ihren eingefallenen Mauern ist sie nur noch ein Schatten ruhmreicherer Zeiten.$B$BWenn Lord Rabenkrone zurückgekehrt ist, dann werden ihn sicher seine früheren Offiziere begleiten.$B$BKester Weitsucher, der Hauptmann der Wache.$B$BTrelan Schildbrecher, ein Streitkolbenmeister ohnegleichen.$B$BStarlys Starkbogen, die Pfeile mit verbundenen Augen spalten konnte.$B$BWenn wir in den Turm selbst eindringen wollen, müssen sie zuvor eliminiert werden.', '', '', '', '', '', '', 22522),
(38714, 'deDE', 'Auf Maievs Spur', 'Durchsucht die Schmiede, die Säblergehege und den Wachturm.', 'Das Festungsgelände liegt gleich da vorn. Das Gebiet ist recht weitläufig, aber wenn wir uns aufteilen, sollten wir das hinbekommen.$B$BSucht Ihr die Kerkerzellen, die Schmiede und den Wachturm nach Anzeichen auf Maiev ab. Ich werde die oberen Wälle absuchen und treffe Euch dann auf der anderen Seite der Festung.', '', '', '', '', '', '', 22522),
(38716, 'deDE', 'Die Rabenwehr', 'Bringt das Signet des Wächters zu Jarod Schattensang.', 'In der Kiste, unter verschiedenem Krimskrams verborgen, befindet sich ein Signet mit einem unverwechselbaren Wappen.$B$BDer Ring aus einem silbrigen, unzerbrechlichen Metall scheint im Laufe der Zeit abgenutzt worden zu sein, als hätte ihn sein Besitzer immer wieder geistesabwesend durch die Finger gleiten lassen.', '', '', '', '', '', '', 22522),
(38718, 'deDE', 'Kur''talos Rabenkrone', 'Betrachtet das Siegel von Rabenkrone.', 'Lord Rabenkrone war ein furchtloser Anführer, ein gestandener Befehlshaber, ein aufrichtiger und ehrenvoller Mann und ein Lehrmeister und Beschützer seines Volkes.$B$BMalfurion, Illidan, Tyrande und ich... Wir waren noch jung, als er uns half, unser Schicksal in die Hand zu nehmen.$B$BViele Legenden ranken sich um das Siegel seines Hauses. Lasst Euren Blick für einen Moment auf dem Siegel über seiner ehemaligen Ruhestätte verweilen. Danach werde ich Euch mehr über sein Vermächtnis erzählen.', '', '', '', '', '', '', 22522),
(38691, 'deDE', 'Jarods Mission', 'Sprecht mit Jarod Schattensang beim Mausoleum des Hauses Rabenkrone.', 'Seit Maiev Gul''dan im Verlies der Wächterinnen nachgestürmt ist, haben ihre Schwestern unter den Wächterinnen sie überall gesucht.$B$BDa uns nur wenige Wächterinnen verblieben sind, habe auch ich mich der Suche freiwillig angeschlossen. Ich hoffe, ich finde in der Rabenwehr Hinweise auf ihren Verbleib.$B$BDiese Festung wird gut bewacht. Ich werde Hilfe brauchen, um dort einzudringen. Helft mir, sie zu finden, und ich werde Euch großzügig entlohnen.', '', '', '', '', '', '', 22522),
(39117, 'deDE', 'Es hat sich ausgekrächzt!', 'Findet Rottschnabel am Bradenbach und schlagt der Bestie den Kopf ab.', 'Papa hat dieses Dorf geliebt. Er wollte nie von hier fortgehen, nicht einmal in diesen schlimmen Zeiten.$B$BBevor er starb, sagte er mir, was ihn angegriffen hat: ein monströser Falke namens Rottschnabel.$B$BIhr kämpft doch gegen diese Untoten, oder? Würdet Ihr meinen Vater rächen? Wenn ja, dann findet Ihr die Bestie nordwestlich von hier. Sie fliegt entlang unserer Westroute zum Meer auf und ab.', '', '', 'Rottschnabel', '', '', '', 22522),
(38646, 'deDE', 'Zielhilfe', 'Verwendet Muttchens Leuchtpistole, um 6 Falken der Rabenwehr zu markieren.', 'Seht Ihr die gefräßigen Vögel, die über meinem Gehöft kreisen? Diese widerlichen Viecher hinterlassen hier ihren Dreck und fressen meine Ernte auf.$B$BIch würde sie gern selbst mit meiner Flinte vertreiben, aber meine Augen sind nicht mehr die besten.$B$BNehmt diese Leuchtpistole und markiert sie damit für mich.$B$BWenn ich sie einmal im Visier habe, werden sie ordentlich Federn lassen!', '', '', '', '', '', '', 22522),
(38647, 'deDE', 'Rettet den Mais!', 'Sammelt 5 Getreidefässer.', 'Der Sommermais ist reif und muss schleunigst geerntet werden. Ich brauche jemanden, der mir dabei hilft, bevor die Nachtelfenräuber sich darüber hermachen.$B$BWenn Ihr ihn für mich ernten könnt, werde ich Euch fürstlich belohnen.$B$BDie Stadtbewohner brauchen Lebensmittel – und ich werde nächsten Winter auf keinen Fall wieder Ratten essen.', '', '', '', '', '', '', 22522),
(40181, 'deDE', 'Rabenwehrräuber', 'Beschafft $1oa Teile der Rabenwehrrüstung.', 'Ich habe von einem riesigen Schloss in Val''sharah gehört, in dem eine große Armee einquartiert ist. Die Rüstungen dieser Soldaten werden sicher nicht aus Stroh und Stöckchen bestehen.$b$bIch wette, sie haben bereits eine Reihe Kettenrüstungen für die Truppen gefertigt, die nur darauf warten, abhandenzukommen.$b$bWir sollten dort mal vorbeischauen und sehen, was wir uns aus ihrer Waffenkammer "borgen" können.', '', '', '', '', '', '', 22522),
(40187, 'deDE', 'Kettenglieder', 'Findet einen Meister der Lederverarbeitung in Azsuna.', 'Nun $gmein Junge:meine Liebe;, ich fürchte, mein Wissen, was diese Schuppen anbetrifft, hat sich erschöpft. Ich weiß wirklich nicht, wie ich die übrigen Teile für einen kompletten Satz herstellen soll.$b$bMir ist zu Ohren gekommen, dass es in den Städten der Elfen von Azsuna einige großartige Handwerker gibt. Vielleicht gibt es den einen oder anderen noch.$b$bGeht und sucht einen Meister. Vielleicht bekommen wir von ihm die fehlenden Muster.', '', '', '', '', '', '', 22522),
(40178, 'deDE', 'Voll vor die Brust', 'Besorgt das Gewand des Balgformers.', 'Alle Vrykul in Nordend haben starke Rüstungen getragen, aber die ihrer Handwerker waren die stärksten.$b$bBegebt Euch nach Sturmheim und findet jemanden, der sich mit Lederrüstungen auskennt. Bringt mir dann seine Brustrüstung.', '', '', '', '', '', '', 22522),
(40182, 'deDE', 'Die muss mit', 'Beschafft eine Rabenwehrhalsberge.', 'Zwar sind alle Arten Rüstungsteile der Rabenwehr spektakulär, am meisten aber interessieren mich ihre Brustrüstungsteile.$b$bVielleicht könnt Ihr jemanden Bedeutendes unter ihnen finden und ihm sein Kettenhemd abnehmen.$b$bUnd falls es Euch dann leichter fällt: Ich hab gehört, dass die alle verflucht sind. Ihr tut ihnen wahrscheinlich noch einen Gefallen.', '', '', '', '', '', '', 22522),
(40179, 'deDE', 'Sturmheimmassaker', 'Besorgt $1oa Lederbindungen der Vrykul.', 'Da unten gibt es ein Land namens Sturmheim, in dem diese Vrykul leben. Ich erinnere mich noch an sie aus meinen Tagen in Nordend.$b$bUnd am deutlichsten erinnere ich mich an ihre gut verarbeiteten Rüstungen.$b$bWenn die hiesigen Vrykul auch so geschickt sind wie die in Nordend, dann können wir sicher noch etwas von ihren Rüstungen lernen.', '', '', '', '', '', '', 22522),
(40197, 'deDE', 'Notwendige Materialien', 'Besorgt $1oa Stücke Steinbalgleder.', 'Um dieses Pferdegeschirr herzustellen, benötigen wir eine Menge Leder. Soll heißen: eine MENGE Leder.$b$bIch habe mir noch mal durch den Kopf gehen lassen, wie wir die Stücke alle zusammenfügen. Geht, besorgt das Leder.$b$bWenn Ihr damit zurück seid, fangen wir an, alles zu verarbeiten.', '', '', '', '', '', '', 22522),
(38644, 'deDE', 'Die Gehöfte', 'Seht euch beim Haus von Bürgermeisters Heathrow, beim Hof von Muttchen Marl und bei der Angelanlegestelle der Radcliffes um.', 'Vergebt uns, $gFremder:Fremde;. Bürgermeister Heathrow stand in letzter Zeit wirklich unter sehr viel Anspannung.$B$BUnd ich kann ihn verstehen: Seine eigene Tochter, Penelope, ist seit zwei Tagen verschollen.$B$BTatsächlich fehlen uns immer noch Informationen über einige Gehöfte im Süden: das Haus von Bürgermeister Heathrow, der Hof von Muttchen Marl und die Anlegestelle der Radcliffes. Könntet Ihr vielleicht nachsehen, was dort vor sich geht?$B$BWenn Ihr uns helft, lässt sich vielleicht auch das Herz des Bürgermeisters erweichen.', '', '', '', '', '', '', 22522),
(38645, 'deDE', 'Kinder der Nacht', 'Tötet $1oa auferstandene Assassinen, Falken der Rabenwehr oder auferstandene Hunde.', 'Ich bin nicht ihr Feind, aber davon wollen diese Leute nichts wissen.$B$BIhr Dorf wurde von ein paar meiner ältesten Freunde angegriffen. Freunde, die... nicht mehr am Leben sind.$B$BIch weiß nicht, unter wessen Einfluss sie jetzt stehen, aber zu Lebzeiten hätten diese edlen Soldaten niemals so gehandelt. Doch die Lösung dieses Rätsels muss vorerst warten.$B$BJetzt müssen wir erst einmal das Vertrauen der Dorfbewohner gewinnen. Schaltet die Soldaten der Rabenwehr aus, die die Dorfbewohner bedrängen.', '', '', '', '', '', '', 22522),
(38888, 'deDE', 'Die Hochbergtauren', 'Meldet Euch bei Ronos Eisenhorn auf dem Hochberg.', 'Ihr habt mir ein paar interessante Steine mitgebracht, $n. Ich habe einen besonderen Auftrag für Euch, falls Ihr interessiert seid.$b$bIch habe Geschichten über diese Tauren auf dem Hochberg gehört. Sie behaupten von sich selbst, Meister des Bergbaus zu sein. Ich weiß nicht, ob sie so viel Talent wie wir beide haben, aber sie bauen Leystein schon weitaus länger ab.$b$bSucht sie doch auf und hört Euch an, was sie zu erzählen haben.', '', '', '', '', '', '', 22522),
(40019, 'deDE', 'Ein empathisches Kraut', 'Beschafft $1oa Proben gesundes Traumlaub und $2oa Proben pestbefallenes Traumlaub.', 'Aber irgendetwas an diesem Traumlaub ist anders. Es ist weniger rein.$b$bIch habe eine Theorie.$b$bDie Wildnis von Val''sharah ist von einer alptraumartigen Seuche heimgesucht worden. Und empfindliche Kräuter wie dieses reagieren vielleicht stärker auf so eine Seuche.$b$bIch benötige mehr Informationen, bevor ich mich festlegen kann. Sucht ein paar gesunde und ein paar pestbefallene Proben, damit wir unsere Nachforschungen fortsetzen können.', '', '', '', '', '', '', 22522),
(44547, 'deDE', 'Inselhüpfen', 'Wählt Euren nächsten Angriffspunkt auf den Verheerten Inseln auf der Schlachtkarte in Eurer Ordenshalle aus.', '<Nach dem errungenen Sieg wisst Ihr, dass es immer noch Säulen der Schöpfung gibt, die geborgen werden müssen, wenn die Legion endgültig bezwungen werden soll.>$B$B<Euer Orden hat bisher unbekannte Regionen der Verheerten Inseln ausgemacht, in denen sich immer noch die restlichen Säulen befinden. Es würde Euren Bemühungen sicherlich helfen, wenn Ihr Euch die Informationen anseht.>', '', '', '', '', '', '', 22522),
(40890, 'deDE', 'Die Tränen von Elune', 'Verwendet den zentralen Teleporter in Dalaran und platziert die Tränen von Elune dann in der Porträtgalerie.', 'Die Träne von Elune schwebt mühelos in der Luft und glitzert in einer ätherischen Säule aus Mondlicht.', '', '', '', '', '', '', 22522),
(40567, 'deDE', 'Den Alptraum betreten', 'Begebt Euch in das Finsterherzdickicht und tötet den Schemen von Xavius.', 'Wir konnten Ysera nicht retten, aber wir haben sie davon abgehalten, den Tempel zu zerstören.\n\nNun kann ich mich endlich meinem Ehemann widmen.\n\nIch weiß nicht, welches Schicksal er gerade erleidet. Ich weiß nur, dass Ihr sofort gehen müsst, wenn wir überhaupt eine Chance haben wollen, ihn zu retten.\n\nFolgt Xavius in den Alptraum und findet Malfurion.\n\nBitte rettet meinen Ehemann, wenn Ihr könnt.', '', '', '', '', '', '', 22522),
(38743, 'deDE', 'Das Schicksal von Val''sharah', 'Verteidigt den Tempel von Elune.', 'Malfurion, mein Geliebter... Es bricht mir das Herz, doch ich kann ihm nicht dorthin folgen, wo er hingeht.$b$bIch schwor meiner Göttin Elune die Treue. Ich muss ihre Tempel und Lande mit all meiner Kraft verteidigen. Ich kann nicht untätig zusehen, wenn mein Orden in Gefahr ist.$b$bMöge sie dich stets ihre Gnade erfahren lassen, Malfurion.$b$b<Tyrande wendet sich mit feucht glänzenden Augen ab.>', '', '', '', '', '', '', 22522),
(41763, 'deDE', 'Dem Ziel so nahe', 'Sucht in Shala''nir nach Malfurion.', 'Er ist in der Nähe, $R. Ich fühle es.$b$bWir müssen Shala''nir nach Malfurion absuchen. Wenn wir ihn nicht bald finden, könnte ich ihn für immer verlieren.$b$bAls wir uns das erste Mal trafen, wusste ich nicht, ob man Euch trauen kann. Aber jetzt weiß ich, dass Ihr mich nicht enttäuschen werdet.$b$bIch... danke Euch.', '', '', '', '', '', '', 22522),
(38748, 'deDE', 'Ruinen von Shala''nir', '', '', '', '', '', '', '', '', 22522),
(43702, 'deDE', 'Zermürbung des Ziels', 'Tötet 50 Alptraumkreaturen in Shala''nir, während Ihr auf der unterjochten Nachtschwinge reitet.', 'Das Ende des Erzdruiden ist nahe. Aber seine Erinnerung wird in den Herzen aller weiterleben, die ihn kannten.$b$bElothirs verzweifelte Anstrengung hat gezeigt, dass Malfurion in Shala''nir festgehalten wird, zwischen den Wurzeln von Shaladrassil.$b$bMein Liebster. So nah und doch so fern...$b$bShala''nir ist von Alptraumkreaturen überrannt. Ein Frontalangriff wäre Selbstmord.$b$bIch habe eine von Xavius'' Kreaturen unterjocht. Setzt sie gegen ihn ein, $n, und bahnt uns einen Weg.', '', '', '', '', '', '', 22522),
(41749, 'deDE', 'Der Verderbnis überlassen', 'Tötet Hüter Varethos.', 'Dürfte ich Euch um einen letzten Gefallen bitten? Varethos, ein sehr alter Freund von mir, ist den Qualen des Alptraums zum Opfer gefallen. Wir haben einst zusammen dieses Land beschützt. Seine enge Verbundenheit mit ebendiesem ist ihm jetzt zum Verhängnis geworden.$b$bWie bei den anderen Opfern gibt es auch für ihn keine Hoffnung mehr.$b$bIch bitte Euch, $n, sorgt dafür, dass er Frieden findet.', '', '', '', '', '', '', 22522),
(38684, 'deDE', 'Die Weisheit der Blätter', 'Sammelt $1oa verderbte Blütenblätter.', '$n, $gmein Freund:meine Freundin;. Ihr müsst vollbringen, was mir versagt blieb. Ihr müsst unseren teuren Malfurion finden.$b$bDer Alptraum verschlingt meinen Geist, und ich höre die Stimmen dieser gequälten Bäume. Aber ich muss an meinem Verstand festhalten, um Euch zu helfen.$b$bBegebt Euch nach Ash''theran und bringt mir verderbte Blütenblätter von denen, die dort gefallen sind.$b$bMit ihrer Hilfe finde ich vielleicht Euren Erzdruiden, solange ich noch bei klarem Verstand bin.', '', '', '', '', '', '', 22522);
INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(43241, 'deDE', 'Fluss des Alptraums', '', '', '', '', '', '', '', '', 22522),
(38675, 'deDE', 'Das Herz des Alptraums', 'Folgt Malfurions Pfad durch den Alptraum.', 'Ich weiß lediglich, dass mein Gatte irgendwo tiefer in diesem entstellten Wald steckt.$b$bEr ist noch am Leben, aber ob er unversehrt ist, ist ungewiss.$b$b<Tyrandes Gesichtsausdruck verhärtet sich, während sie Euch einen finsteren Blick zuwirft.>$b$bWenn er es nicht ist, wird nicht einmal Elune Xavius beschützen können.', '', '', '', '', '', '', 22522),
(43576, 'deDE', 'Neuformierung an der Zuflucht', 'Kehrt in die Sternensangzuflucht zurück.', 'Ich kann hier nicht klar denken.\n\nDer Mondbrunnen ist zwar immer noch rein, aber der verderbliche Einfluss des Alptraums ist weiter vorhanden.\n\nKommt, lasst uns in die Zuflucht zurückkehren und unsere Gedanken sammeln. Danach werden wir weiter nach meinem Mann suchen.', '', '', '', '', '', '', 22522),
(41707, 'deDE', 'Wurmkrallentrümmer', 'Tötet 15 Wurmkrallenharpyien im Schattenmoor.', 'Als ob Shaladrassils sich nähernde Wurzeln noch nicht reichen würden, wollen jetzt die Wurmkrallenharpyien auch noch unsere Misere ausnutzen.$b$bTötet sie, $R. Das verdienen sie dafür, dass sie sich an unserem Elend weiden.', '', '', '', '', '', '', 22522),
(38671, 'deDE', 'Rettet die Habseligkeiten', 'Beschafft 5 Erbstücke aus dem Schattenmoor.', 'Ist dies das Ende?$b$bUnser geliebter Malfurion. Ich sah ihn in den Klauen der Träumerin, aber Ysera war... so anders, so verderbt...$b$bShaladrassil, unser mächtiger Weltenbaum, ist bis zur Unkenntlichkeit durch die Verderbnis entstellt.$b$bWir mussten aus unserem Zuhause fliehen und unsere Habseligkeiten zurücklassen. Einige davon waren Erbstücke, die uns heilig sind. Ich bitte Euch, $C, bringt sie mir zurück, wenn Ihr könnt.', '', '', '', '', '', '', 22522),
(41890, 'deDE', 'Die dunkle Seite des Mondes', 'Beschützt Tyrande, während sie am uralten Mondbrunnen ihre Wache hält.', 'Xavius ist hier vorbeigekommen, doch sein Ziel ist nicht ganz klar. Der Alptraum vernebelt mir die Sinne!\n\nDiese Flüchtlinge haben von einem uralten Mondbrunnen gesprochen, der in den Ruinen vor uns verborgen liegt.\n\nWenn er der Verderbnis widerstehen konnte, wird er mir die nötige Konzentration verleihen. Bei Elunes Licht, wir werden meinen Gatten finden.\n\nSucht mich sofort am Mondbrunnen auf, $C.', '', '', '', '', '', '', 22522),
(41054, 'deDE', 'Verlorene Liebe', 'Sprecht mit Tyrande Wisperwind in der Sternensangzuflucht.', 'Malfurion, mein Geliebter...$b$bIch sah ihn in einer Vision. Er war zornig... dann litt er plötzlich Qualen. Und nun kann mein Geist ihn nicht mehr erreichen.$b$b$R, ich weiß, wir haben wenig Grund, einander zu vertrauen, doch ich werde Eure Hilfe nicht ablehnen.$b$bWenn Euch die Unantastbarkeit dieser Welt etwas bedeutet, werdet Ihr mich begleiten.', '', '', '', '', '', '', 22522),
(38753, 'deDE', 'Die Spur des Dämons', 'Sucht nach Malfurion Sturmgrimm.', 'Malfurion ist in der Nähe. Ich kann ihn spüren.$b$bIhr seht auf dem Gipfel von Andu''talah nach, und ich durchsuche weiterhin den Wald.$b$bEilt Euch, $n. Die Zeit drängt!', '', '', '', '', '', '', 22522),
(38582, 'deDE', 'Auf alte Freunde', 'Tötet 50 verderbte Kreaturen in Mondruh.', 'Ich spüre die Anwesenheit einer dunklen Macht, $n, aber es ist nicht Xavius. Es fühlt sich viel zu vertraut an.$B$BEs ist die Nähe eines Druiden, der vom Schatten des Alptraums verderbt wurde.$B$BWir müssen uns dieser Bedrohung stellen, wenn wir Xavius finden wollen. Ich werde in die Lüfte aufsteigen und Euch von oben unterstützen.$B$BFindet den verderbten Druiden und macht seinen bösartigen und pervertierten Plänen ein Ende.', '', '', '', '', '', '', 22522),
(38595, 'deDE', 'Malfurions Zorn', 'Sprecht mit Ysera, um nach Malfurion zu suchen.', 'Dass wir Cenarius an den Alptraum verloren haben, ist ein furchtbarer Schlag für uns alle.$b$bMalfurions Wut mag gerechtfertigt sein, aber sein Hass auf Xavius schwelt seit Jahrhunderten. Ich fürchte, dass jemand seine blinde Wut gegen ihn selbst richten wird.$b$bMöge Elune uns auf der Suche nach ihm leiten, auf dass wir heute nicht noch einen weiteren Freund betrauern müssen.', '', '', '', '', '', '', 22522),
(38663, 'deDE', 'Die Würfel sind gefallen', 'Sprecht mit Ysera im Hain des Cenarius.', 'Dank Eurer Hilfe ist der Tempel erst einmal sicher.$b$bAber die Tränen von Elune wurden gestohlen, und der Weltenbaum Shaladrassil, der einstige Beschützer dieser Lande, ist nun ein Instrument in den Händen unseres Feindes. Der Smaragdgrüne Traum selbst wird von einem Alptraum verschlungen.$b$bDer Mut verlässt die Herzen unseres Volkes und sein Glaube wird härter geprüft als je zuvor. Kehrt mit meinem Bericht zu Ysera zurück, $n.$b$bIch werde mich an Elune wenden, solang es mir möglich ist. Vielleicht gibt es ja doch noch Hoffnung.', '', '', '', '', '', 'Sprecht mit Ysera im Hain des Cenarius.', 22522),
(38656, 'deDE', 'Zeichen des Dämons', 'Bringt das dämonische Emblem zu Isoraen Nachtstern am Tempel von Elune zurück.', 'Die dämonische Rune glüht in Eurer Hand und strahlt dunkle Macht aus. Ein einzelnes Wort in der Schrift der Dämonen leuchtet kalt auf...$b$bGRAVAX...', '', '', '', '', '', '', 22522),
(38655, 'deDE', 'An der Wurzel packen', 'Sammelt $1oa verderbte Wurzelproben.', 'Die gewaltigen Wurzeln von Shaladrassil verbreiten im ganzen Wald eine fürchterliche Verderbnis.$b$bWenn wir Val''sharah retten wollen, müssen wir einen Weg finden, die Ausbreitung des Weltenbaums zu unterbinden.$b$bWenn Ihr mir genug verderbte Wurzelproben bringt, werde ich versuchen, ihnen ihre Geheimnisse zu entlocken.', '', '', '', '', '', '', 22522),
(38662, 'deDE', 'Tränen der Hoffnung', 'Besorgt die Tränen von Elune.', 'Die Tränen von Elune sind ein heiliges Artefakt, das seit Tausenden von Jahren von meinem Orden beschützt wird. Das erzähle ich Euch, damit Ihr die Bedeutung Eurer Aufgabe einzuschätzen versteht.$b$bAber Ysera ist edel und weise, und ich vertraue auf ihre Führung. Ihr findet die Tränen in einer Kammer am östlichen Rand des Tempelgeländes.$b$bAber seht Euch vor, denn das ganze Gebiet wurde von Dämonen überrannt.', '', '', '', '', '', '', 22522),
(39029, 'deDE', 'Verteidigung des Tempels', '', '', '', '', '', '', '', '', 22522),
(38641, 'deDE', 'Der Tempel von Elune', 'Sprecht mit Lyanis Mondfall beim Tempel von Elune.', 'Cenarius ist in Gefahr.$b$bWenn sich sein Zustand nicht bald bessert, verlieren wir ihn an den Alptraum, der den Smaragdgrünen Traum bedroht. Um ihn vor diesem Schicksal zu bewahren, brauchen wir die Tränen von Elune. Dasselbe Relikt, das Ihr hier beschaffen solltet.$b$bElunes Tempel ist nicht weit von hier. Begebt Euch dorthin und sucht nach Lyanis. Zeigt ihr mein Siegel und weist sie an, die Tränen in Eure Obhut zu übergeben.$b$bBeeilt Euch, $n. Wir haben nicht viel Zeit.', '', '', '', '', '', 'Sprecht mit Lyanis Mondfall beim Tempel von Elune.', 22522),
(38377, 'deDE', 'Die Smaragdkönigin', 'Sprecht mit Malfurion, wenn Ihr bereit seid.', '$n, Ihr seid zurück! Das ist gut.$b$bIch habe mich mit den anderen Erzdruiden besprochen und wir stimmen überein, dass Cenarius'' gegenwärtiger Zustand nur das Symptom eines viel größeren Problems ist. Eure Erfahrungen im Mondklauental bestätigen das.$b$bWir brauchen Hilfe.$b$bEs gibt nur eine Person, die Licht in das Dunkel dieser Ereignisse bringen kann. Da Thaon tot ist, müssen wir Euch um Hilfe dabei bitten, die Herrin der Träume zu kontaktieren.', '', '', '', '', '', '', 22522),
(38322, 'deDE', 'Rückkehr zum Hain', 'Kehrt zu Malfurion Sturmgrimm im Hain des Cenarius in Val''sharah zurück.', 'Jetzt wo mein Hain sicher ist, kann ich mich um die von Euch erwähnte Bedrohung kümmern.$B$BUnd es ist in der Tat bestürzend, von Cenarius'' misslicher Lage zu hören. Sagt Malfurion, dass wir uns im Hain treffen werden.', '', '', '', '', '', '', 22522),
(38372, 'deDE', 'Elende Schwestern', '', '', '', '', '', '', '', '', 22522),
(38225, 'deDE', 'Tod der Hexenmutter!', 'Tötet Magula.', 'Die unzähligen Jahre meines Lebens habe ich dem Erlernen und Perfektionieren der Heil- und Regenerationskünste gewidmet.$b$bNichts in der Welt quält mich mehr, als Leben zu nehmen, $gReisender:Reisende;.$b$bUnd darum verurteile ich nur schweren Herzens Magula, die Anführerin der Harpyien, die diesen Hain bedrohen, zum Tode.$b$bMöge ihr Tod das Leben edlerer Lebewesen retten.', '', '', 'Magula', '', '', '', 22522),
(38235, 'deDE', 'Fels in der Brandung', 'Rettet Marnor Lorblatt, Erwind Nebelmoor und Del''thanar Weisenweide.', 'Cenarius benötigt meine Hilfe?$b$bIch werde baldmöglichst aufbrechen, doch ich kann hier nicht fortgehen, solange meine Druiden in Gefahr sind. Ein bösartiger Hexenzirkel von Harpyien ist aufgetaucht und will meinen Hain übernehmen.$b$bDiese Scheusale verbreiten eine versteinernde Krankheit, die alle Befallenen mit der Zeit dahinrafft.$b$bIch möchte natürlich, dass alle meine Anhänger gerettet werden, doch es ist von höchster Bedeutung, dass meine ältesten Schüler überleben, um den Hain in meiner Abwesenheit zu schützen.$b$bWenn Ihr uns helfen möchtet, dann findet meine Schüler und weckt sie aus diesem furchtbaren Zustand auf.', '', '', '', '', '', '', 22522),
(38862, 'deDE', 'Diebische Distelblattgeister', 'Sammelt $1oa gestohlene Mondschwingeneier am Mondschwingentümpel.', 'Niemand weiß genau, warum die Distelblattgeister die Mondschwingeneier sammeln, aber sie wollen ihnen bestimmt kein warmes Nest bauen.$b$bSaylanna Flussbrise weiß sicher, was mit diesen hier zu tun ist.', '', '', '', '', '', '', 22522),
(38147, 'deDE', 'Umschlingende Träume', 'Tötet Morphael.', 'Ich spüre mächtige Magie aus der Tiefe des Baus dringen.$B$BWas auch immer sich dort unten aufhält, webt einen mächtigen Zauber, der uns in unseren eigenen Träumen gefangen hält.$B$BIch bin noch zu schwach, aber Eure Fähigkeiten sind ziemlich beeindruckend, $gHeld:Heldin;. Ich kann verstehen, warum Malfurion sein Vertrauen in Euch setzt.$B$BGeht und eliminiert die Quelle dieser Magie.', '', '', '', '', '', '', 22522),
(38144, 'deDE', 'Dämonen in der Tiefe', 'Tötet 8 Eindringlinge der Schattenteufel.', 'Irgendetwas oder irgendjemand wollte, dass wir nie wieder aufwachen. Ich kann diese finstere Präsenz noch immer in dieser Höhle spüren.$B$B<Koda benötigt einen Augenblick, um sich aufzurichten.>$B$BMein Körper ist noch schwach vom tiefen Schlaf.$B$BWenn Ihr uns helfen wollt, dann schaltet die Feinde in unserer Höhle aus.', '', '', '', '', '', '', 22522),
(38145, 'deDE', 'Ende der Träume', 'Erweckt $1oa Druiden der Klaue.', 'Malfurion Sturmgrimm... Diesen Namen habe ich schon seit Jahrhunderten nicht mehr gehört.$B$BEr war vielen von uns ein Lehrer. Wenn er mich sehen möchte, kann ich ihm dies nicht abschlagen.$B$BIch werde mich zum Hain des Cenarius begeben, aber zunächst müssen wir den Rest meines Klans aufwecken. Sie sind in einem Schlaf gefangen, aus dem sie aus eigener Kraft nicht erwachen können.$b$bEine mysteriöse Macht hat sich gegen uns gewandt.$B$BIch bin immer noch zu schwach vom langen Schlaf. Darum bitte ich Euch, $gFremder:Fremde;: Befreit die anderen aus ihren Träumen.', '', '', '', '', '', '', 22522),
(38143, 'deDE', 'Das Erwachen der Erzdruidin', 'Weckt die Erzdruidin der Klaue auf.', 'Jetzt müssen wir uns darauf konzentrieren, Erzdruidin Koda Stahlklaue aufzuwecken.$B$BSie und die anderen Druiden ruhen in der Höhle der Schläfer im Süden.$B$BDa die Furbolgs ausgeschaltet sind, können wir jetzt sicher zum Eingang gelangen.$B$BGeht und weckt die Erzdruidin. Ich bete zu Elune, dass es noch nicht zu spät ist.', '', '', '', '', '', 'Sprecht mit Erzdruidin Koda Stahlklaue in der Höhle der Schläfer.', 22522),
(38246, 'deDE', 'Totemischer Ruf', 'Zerstört das Alptraumtotem.', 'Ich habe vorhin den anderen zugehört und kenne jetzt vielleicht den Grund ihrer Angst.$B$BSie sagen, dass der Häuptling ein geheimnisvolles Totem besitzt. Es verleiht ihm Kraft, bringt ihm aber auch beängstigende Visionen.$B$BDieser Wahnsinn wird aufhören, wenn Ihr das Totem zerstört.', '', '', '', '', '', '', 22522),
(38922, 'deDE', 'Jungflaum', 'Sprecht mit Jungflaum im Schwelhautdickicht.', 'Bitte helft mir, $gFremder:Fremde;! Meine einst so friedlichen Brüder und Schwestern sind in zornige Raserei verfallen.$B$BMein Neffe und ich haben versucht, den Stamm gegen den Willen des Häuptlings zu verlassen, woraufhin sie ihn mir weggenommen haben und mich fast zerrissen hätten, wenn ich nicht geflohen wäre.$B$BEr wird noch im Dorf gefangen gehalten und ich befürchte das Schlimmste.$B$B$C, ich flehe Euch an: Rettet ihn! Ich bin zu alt und zu schwach, um diese Aufgabe selbst zu bewältigen.', '', '', '', '', '', '', 22522),
(38455, 'deDE', 'Rasende Furbolgs', 'Tötet 8 Schwelhautfurbolgs.', 'Ich würde Euch zum Treffen mit dem Erzdruiden begleiten, aber wir haben hier selbst einige Probleme.$B$BDie Furbolgs in dieser Gegend sind in Raserei verfallen.$B$BSeit Tagen schon müssen wir unseren Hain gegen ihre Angriffe verteidigen.$B$BSie haben uns den Weg zur Höhle der Schläfer abgeschnitten. Das ist der Ort, wo der Erzdruide und unser Klan im Smaragdgrünen Traum schlafen.$B$BUm sie erwecken zu können, müssen wir zuerst an den Furbolgs vorbei.', '', '', '', '', '', '', 22522),
(40221, 'deDE', 'Mondschwingen in Not', 'Rettet 8 gefangene Mondschwingen am Mondschwingentümpel.', 'Die armen Feendrachen leiden furchtbare Qualen!$B$BUnd das Schlimmste daran ist: Wenn die Distelblattgeister genug haben, fesseln sie ihre Opfer, um sie später noch weiter zu malträtieren.$b$bSo kann das nicht weitergehen.$B$BBefreit bitte alle gefangenen Mondschwingen, die Ihr finden könnt.', '', '', '', '', '', '', 22522),
(40220, 'deDE', 'Dorniger Tanz', 'Besiegt $1oa Distelblattdorntänzer im Mondschwingentümpel.', 'Die Distelblattwaldgeister sind vollkommen übergeschnappt.$b$bIch fürchte, sie werden von einigen sehr bösartigen Dorntänzern manipuliert.$b$bIch bitte Euch, $gFreund:Freundin; des Waldes, findet diese verführerischen Tunichtgute und stellt den Frieden am Tümpel wieder her.', '', '', '', '', '', '', 22522),
(40573, 'deDE', 'Der Alptraumfürst', 'Berichtet Thaon Mondklaue von Xavius'' Rückkehr.', 'Ihr seid gerade noch rechtzeitig gekommen, $gmein Freund:meine Liebe;.$b$bKagraxxis hätte mich jeden Moment seinem Meister geopfert... Ich hatte schon mit meinem Leben abgeschlossen.$b$bIch bin Euch sehr dankbar, aber wir sind immer noch in Gefahr... Xavius ist zurückgekehrt.$b$bDer Alptraumfürst ist hier in Val''sharah und koordiniert höchstpersönlich die Verderbnis des Landes. Das hat mir mein Peiniger offenbart, kurz bevor er mich schächten wollte.$b$bWir müssen Thaon Mondklaue finden und ihm davon berichten, bevor es zu spät ist!', '', '', '', '', '', '', 22522),
(39384, 'deDE', 'Der Verderber', 'Tötet Kagraxxis den Verderber.', 'Xandris ist erledigt, aber wir müssen uns noch um andere kümmern, um meinen Hain zu retten.$b$bWährend des Krieges war es Kagraxxis ein großes Vergnügen, das Land zu verderben, das er einst seine Heimat nannte.$b$bWenn die anderen Druiden ihm nicht törichterweise Gnade gewährt hätten, hätte ich ihn selbst hingerichtet.$b$bFindet diesen Dämon und lasst ihm Gerechtigkeit widerfahren.$b$bIch muss Euch diese Aufgabe übertragen, denn ich verspüre die Anwesenheit einer noch dunkleren Präsenz... einer Präsenz, die nach meiner persönlichen Aufmerksamkeit verlangt.', '', '', 'Kagraxxis der Verderber', '', '', '', 22522),
(39383, 'deDE', 'Ehrlos', 'Tötet Xandris den Ehrlosen.', 'Xandris der Ehrlose gehörte zu den Ersten, die ihr Volk verraten und Xavius'' Fluch angenommen haben.$B$BWährend des Krieges war er der Anführer der Truppen, die viele meiner besten Freunde umgebracht haben.$b$bAls wir uns das letzte Mal auf dem Schlachtfeld begegneten, ist er geflohen. Helft mir, endlich Rache an dieser widerwärtigen Kreatur zu nehmen.', '', '', 'Xandris der Ehrlose', '', '', '', 22522),
(39393, 'deDE', 'Das Mondklauental', '', '', '', '', '', '', '', '', 22522),
(38382, 'deDE', 'Erzdruide des Tals', 'Sprecht mit Thaon Mondklaue.', 'Tief in unserem Wald verborgen liegt das Mondklauental.$b$bDort findet Ihr eine zurückgezogene Gruppe wilder Druiden, die damit beauftragt sind, Val''sharah vor allem zu beschützen, was den Einklang der Natur stören könnte.$b$bDer Anführer dieser Druiden ist Thaon Mondklaue, der entschlossenste Kämpfer der Erzdruiden.$b$bSucht ihn auf und teilt ihm mit, dass Cenarius seine Hilfe benötigt.', '', '', '', '', '', 'Sprecht mit Thaon Mondklaue im Mondklauental.', 22522),
(38142, 'deDE', 'Erzdruidin der Klaue', 'Sucht das Stahlklauental.', 'Nahe des südlichen Waldrands findet Ihr die Höhle der Schläfer.$B$BAn diesem Ort kommen die Erzdruidin Koda Stahlklaue und ihre eifrigen Schüler ihrer heiligen Pflicht nach, den Smaragdgrünen Traum zu beobachten.$b$bFindet die Schülerin Rylissa Bärensang im Stahlklauental und informiert sie über die Krise im Hain des Cenarius.', '', '', '', '', '', 'Sprecht mit Rylissa Bärensang im Stahlklauental.', 22522),
(44106, 'deDE', 'Erzdruide der Lehren', 'Sucht Elothir in Thas''talah auf.', 'Elothir lebt im Einklang mit der Natur im uralten Hain Thas''talah und unterrichtet seine Akolythen in den Künsten des Heilens und im Bewahren der Lehren.$b$bSucht ihn auf und teilt ihm mit, dass er hier dringend benötigt wird.', '', '', '', '', '', '', 22522),
(38384, 'deDE', 'Der Ruf der Natur', 'Sprecht mit Aranelle in Lorlathil.', 'Was für eine bösartige Kraft ist das?!$b$bEin Fluch, der stark genug ist, einen Halbgott zu lähmen? Das ist unvorstellbar!$b$bSchnell, $n, Ihr müsst die Erzdruiden des Waldes versammeln. Mit ihrer Hilfe können wir Ysera herbeirufen. Sie wird sicher wissen, was meinen Mentor so peinigt.$b$bIch werde hierbleiben und Cenarius so gut ich kann helfen.$b$bKehrt nach Lorlathil zurück und sprecht mit Aranelle. Sie weiß, wo sich die Erzdruiden aufhalten.$b$bUnd bitte beeilt Euch...', '', '', '', '', '', '', 22522),
(40183, 'deDE', 'Auf den Schädel', 'Stellt eine grobe Kriegsbalgmaske her.$b$bDazu werdet Ihr Namhas Gerbrahmen und Namhas Werkbank im Legendäre Leder verwenden müssen.', 'Also gut, $n. Ich möchte, dass Ihr mir bei der Herstellung eines Helms helft.$b$bZunächst müssen wir die Bälge enthaaren, dann gerben und dann in verwendbare Stücke zuschneiden.$b$bFolgt meinem Beispiel.', '', '', '', '', '', '', 22522),
(42244, 'deDE', 'Das Schicksal der Revanche der Königin', 'Bringt das Logbuch des Kapitäns zu Beraterin Stillwasser in Windläufers Zuflucht in Dalaran.', '<Diese Art von Informationen sollte man mit anderen teilen. Vielleicht weiß jemand in Windläufers Zuflucht in Dalaran etwas damit anzufangen.>', '', '', '', '', '', '', 22522),
(37659, 'deDE', 'Die Schließkiste des Kapitäns', 'Sucht nach der Kiste des Kapitäns auf den unteren Decks der "Revanche der Königin".', 'Mir ist es egal, was Ihr treibt, $gmein Lieber:meine Liebe;. Aber wenn sie Euch erwischen, kenne ich Euch nicht.\n\nIch war gar nicht hier.', '', '', '', '', '', '', 22522),
(42268, 'deDE', 'Schiffbrüchige Matrosen', 'Rettet $1oa Matrosen der Revanche der Königin.', '<Die Gilblin und Makrura von Mak''rana haben die Matrosen der Verlassenen aus dem nahen Schiffswrack gefangen genommen.>\n\n<Es sieht so aus, als würden sie sehr schlecht behandelt. Also ist wohl eine Rettungsmission angebracht.>', '', '', '', '', '', '', 22522),
(37657, 'deDE', 'Eine sichere Handelswelt schaffen', 'Tötet $1oa Gilblin und/oder Makrura in Mak''rana und tötet ihre Anführerin, Königin Kraklaa.', 'Kann ich Euch um einen kleinen Gefallen bitten, $gmein Freund:meine Freundin;? Diese Kreaturen in den Gezeitenteichen im Osten fangen an, mich zu ängstigen. Unsere Handelsangebote an sie wurden jedes Mal aggressiv abgeschmettert. Das ist nicht gut, weder fürs Geschäft noch für den Profit.\n\nLeider muss ich eingestehen, dass die Zeit für Verhandlungen vorbei ist.\n\nWärt Ihr gewillt, als $gmein Repräsentant:meine Repräsentantin; zu fungieren und Euch um die Bewohner von Mak''rana und ihre "Königin" zu kümmern?\n\nIch kann Euch versichern, dass Eure Anstrengungen angemessen vergütet werden.', '', 'Tötet die Königin und kümmert Euch um alles und jeden, was Euch im Weg steht.', 'Königin Kraklaa', '', '', '', 22522),
(37566, 'deDE', 'Das Ende des Prinzen', 'Befreit Okuna Langhauer und tötet Prinz Ozeanus.', '$n! Ich wusste, dass Ihr kommen würdet.\n\nSchnell, lasst mich hier aus, damit wir diesen Riesen erledigen können.', '', 'Versetzt die Riesen in Aufruhr, indem Ihr ihren Prinzen tötet.', 'Prinz Ozeanus', '', '', '', 22522),
(38857, 'deDE', 'Ein Gefallen für Herrn Fessel', 'Befreit Spalt Donnerhügel, den sanftmütigen Rosh und Grazia die Grüne.', 'Throm''ka, $C. Mein Name ist Seska. Ich bin die Abgesandte einer... Handelsgesellschaft. Wie Ihr sehen könnt, bin ich von diesen Riesen und ihren Nagaverbündeten überwältigt worden.\n\nDrei meiner Gefährten sind während der Untersuchung dieser Höhle ebenfalls gefangen genommen worden. Ich bin mir sicher, dass Herr Fessel, mein Arbeitgeber, sehr dankbar sein würde, wenn Ihr sie befreit.', '', '', '', '', '', '', 22522),
(37565, 'deDE', 'Genau die richtige Waffe', 'Bewaffnet $1oa schiffbrüchige Gefangene.', '<Ihr und Okuna wolltet die Gefangenen hier drin bewaffnen. Die Idee war, sie zu befreien und so viel Chaos wie möglich anzurichten. Das ist die einzige Möglichkeit, an den Prinzen heranzukommen.>\n\n<Ihr habt immer noch die Waffen. Ihr müsst Euch nur vorsichtig an den Riesen vorbeischleichen.>', '', '', '', '', '', '', 22522),
(37495, 'deDE', 'Plündert die Höhle', '', '', '', '', '', '', '', '', 22522),
(37538, 'deDE', 'Runde 1, Kampf!', 'Tretet für Hecktief in der Arena an, besiegt Boss Walplauze und sucht dann Okuna Langhauer in Ozeanus'' Bucht auf.', 'Alle meine Gladiatoren sind ausgefallen!\n\nJetzt kommt es auf Euch an, $n. Ihr müsst gegen die drei Champions von Kimmruder antreten.', '', 'Überlebt die Arena, bis Ihr Boss Walplauze ausschalten könnt.', 'Boss Walplauze', '', '', '', 22522),
(37536, 'deDE', 'Moralschub', 'Besorgt das Soldatenfutter von der Knatzelfiddlerin.', 'Aus Eurer Warte sieht es vielleicht ganz gut aus, aber wir verlieren. Aber fürchtet Euch nicht, $gmein kleiner:meine kleine:r; $R.\n\nIn der Höhle der Champions ist die Knatzelfiddlerin, die Schwester vom Knatzelfiddler. Sie bereitet dort gerade ihr weltberühmtes Soldatenfutter zu. Damit können wir die Moral unseres Teams entscheidend steigern.\n\nIhr müsst sie einfach nur überzeugen, es rauszurücken.', '', '', '', '', '', '', 22522),
(37510, 'deDE', 'Hecktiefs Champion', 'Meldet Euch als $gneuer Grubenkämpfer:neue Grubenkämpferin; bei Hecktief.', 'Da Ihr den Knatzelfiddler besiegt habt, ist Platz für einen weiteren Grubenkämpfer im Team von Hecktief. Ihr solltet versuchen, sein Champion zu werden. Dann werden die Riesen nicht weiter darüber nachdenken, warum $gein Fremder:eine Fremde; plötzlich kommt und geht, wie es $gihm:ihr; beliebt.\n\nIn der Zwischenzeit werde ich für etwas Chaos sorgen, damit wir Prinz Ozeanus angreifen können.', '', '', '', '', '', '', 22522),
(37528, 'deDE', 'Schlafende Riesen soll man nicht wecken', 'Sammelt den Waffenvorrat der Riesen ein.', 'Wir müssen für Ablenkung sorgen, um an Prinz Ozeanus heranzukommen. Denken wir gerade das Gleiche?\n\nSeht Ihr den Schläfrigen Dhau da drüben? Er bewacht all die Waffen, die die Riesen aus unseren Schiffen gestohlen haben. Damit können wir den Rest der Grubenkämpfer bewaffnen, die in den Höhlen gefangen gehalten werden.\n\nIhr müsst nur an diesem Riesen vorbeikommen, ohne ihn aufzuwecken.', '', '', '', '', '', '', 22522),
(37542, 'deDE', 'Ohne Vorbereitung', 'Verabreicht dem Knatzelfiddler eine Tracht Prügel.', 'Wir haben nicht viel Zeit, um Euch vorzubereiten. Aber die Arenakämpfe sind die beste Möglichkeit, Euch mit den Riesen gut zu stellen.\n\nDieser warmherzige Riese, der da drüben gerade gegen Kimmruder verliert, heißt Hecktief. Wenn Ihr seinen Ho-zen-Champion besiegt, wird ein Platz für Euch in seinem Team frei.\n\nDer Knatzelfiddler scheint gerade ein Päuschen einzulegen. Jetzt könnt Ihr dem Affen so richtig einen auf den Pelz geben.', '', 'Sorgt dafür, dass er aufgibt, um Euch einen Platz in Hecktiefs Team zu sichern.', 'Der Knatzelfiddler', '', '', '', 22522),
(37507, 'deDE', 'Boss Walplauze hat das Sagen', 'Sprecht mit Boss Walplauze.', 'Sehr gut, dann fangen wir an! Wenn wir hier ausbrechen wollen, müsst Ihr Euch als $gGrubenkämpfer:Grubenkämpferin; ausgeben, genau wie wir anderen. Die werden Euch nicht einfach hier herumlaufen lassen, wie es Euch gefällt.\n\nDazu müsst Ihr zuerst mit Boss Walplauze sprechen. Die Riesen haben hier zwar das Sagen, aber in Sachen Grubenkampf ist Walplauze der Chef.\n\nEr spricht etwas südlich von hier gerade mit seinen Untergebenen.', '', '', '', '', '', '', 22522),
(38237, 'deDE', 'Das steht so im Vertrag!', 'Helft Daglop, sein Ritual auszuführen.', 'Ich muss mein Ritual in einer besonderen Höhle durchführen, die ich am Finsterbundgrabhügel vorbereitet habe.$b$bTrefft mich dort. Dann "gebe" ich Euch Eure Belohnung. Hi hi hi.', '', 'Tehds Meister-vor-dem-Letzten', 'Sharazaan', '', '', '', 22522),
(38232, 'deDE', 'Diener! Tötet sie!', 'Erfüllt Euren Vertrag mit Daglop.', 'Gemäß Eurem Vertrag werdet Ihr mir helfen, die Zutaten für ein absolut harmloses Ritual zusammenzutragen.$b$bIch brauche Federn und Hörner. Eine Menge Hörner. Seht Ihr die Schattenrösser, Pirscher und Reiher da drüben in den Olivanhöhen? Die sollten reichlich davon bieten.$b$bOh, und ich brauche Oliven! Jede Menge Oliven. Eine für jeden Finger.$b$BAlso, auf geht''s, Helferlein!', '', '', '', '', '', '', 22522),
(38460, 'deDE', 'Kommen wir ins Geschäft!', 'Unterschreibt Daglops Vertrag.', 'Hey, Ihr da!$b$bIhr scheint mir ja recht leichtgläu... äh... "intelligent" zu sein.$b$bIch will Euch ein Geschäft vorschlagen.$b$bIch brauche einen kräftigen Helfer, um ein paar... "Zutaten" für ein Ritual zu sammeln.$b$bIm Gegenzug biete ich meinen früheren Meister an. Ihr könnt dann mit ihm machen, was immer Ihr Dämonenschlächtertypen mit Dämonen so macht.$b$bWenn Ihr einverstanden seid, dann unterschreibt bitte hier... mit Eurem Blut.', '', '', '', '', '', '', 22522),
(42367, 'deDE', 'Arkethrax', 'Tötet Arkethrax.', 'Innerhalb des Vorstoßes befindet sich ein Dämon, der sterben muss.$b$bArkethrax ist eine Kreatur von hinterhältiger Bosheit. Wenn er unbeachtet bleibt, wird er in der Lage sein, den Vorstoß als Ausgangspunkt für gezielte und tödliche Angriffe auf unsere Streitkräfte zu nutzen.$b$bIch für meinen Teil habe nicht vor, diesen Gegner zu unterschätzen, und werde Euch auf dem Schlachtfeld beistehen, wenn Ihr bereit seid, ihm gegenüberzutreten.', '', 'Kommandant des Teufelsfeuervorstoßes', 'Arkethrax', '', '', '', 22522),
(42368, 'deDE', 'Quantität über Qualität', 'Tötet Dämonen im Teufelsfeuervorstoß.', 'Die Dämonen des Teufelsfeuervorstoßes sammeln sich in exponentieller Rate an.$b$bWir müssen diese Invasoren vernichten, bevor sie über genügend Dämonen verfügen, um unseren Streitkräften am Verteidigungsposten der Illidari in die Flanke zu fallen.$b$bWerdet Ihr unserem Kampf beistehen?', '', '', '', '', '', '', 22522),
(42369, 'deDE', 'Sie machen es falsch', 'Platziert 4 sabotierte Portalstabilisatoren.', 'Diese Dämonenportale sind geradezu furchtbar hingeschlampt. Ohne jegliche Finesse oder Können.$B$bDas tut mir geradezu körperlich weh... und ich habe schon jahrzehntelang keinen echten Schmerz mehr gespürt.$b$bIch habe einige dieser wertvollen Portalstabilisatorkristalle der Legion "repariert".$b$BIch möchte, dass Ihr damit ihr Hauptportal im Vorstoß "modifiziert" und den darauffolgenden "Spaß" begutachtet.', '', '', '', '', '', '', 22522),
(44383, 'deDE', 'Auf der Jagd nach Macht', 'Sagt Kayn Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Es gibt immer noch ein Paar Klingen, die Euch bei den bevorstehenden Kämpfen von großem Nutzen sein könnten.$B$BIch kann Euch darüber ins Bild setzen, was wir wissen, wenn Ihr ihnen nachgehen wollt.', '', '', '', '', '', '', 22522),
(42671, 'deDE', 'Erhebt Euch, Champions', 'Rekrutiert Kayn Sonnenzorn und Asha Rabenruf als Eure ersten Champions.', 'Die Matronenmutter, Lady S''theno und ich haben Euch und die anderen Illidari beobachtet. Da wir die Dinge von außen betrachten, ist uns eine gewisse Objektivität zu eigen. Wir sind zu einer einstimmigen Empfehlung gelangt: Um ein Zersplittern der Illidari zu verhindern, müsst Ihr anfangen, Champions zu rekrutieren.\n\nDiese wenigen Auserwählten werden dabei helfen, Eurer Macht über die Grenzen der Teufelshammer hinaus Geltung zu verschaffen.\n\nWir empfehlen einstimmig, als Erstes Kayn und Asha auszuwählen.', '', '', '', '', '', '', 22522),
(42375, 'deDE', 'Im Auge behalten', 'Nutzt Tehds Auge von Kilrogg, um den Teufelsfeuervorstoß auszuspähen.', 'Blind zu sein, ist die eine Sache, dumm zu sein, eine andere.$b$bDieses augenlose Wunder neben mir will den Teufelsfeuervorstoß ohne Vorbereitung stürmen.$b$bPah! Ich sage, wir kundschaften ihn vorher aus.$b$bHier, ich gebe Euch ein zusätzliches Auge, das ich von einem besser nicht näher definierten Ort mitgenommen habe.$b$BBetretet den Teufelsfeuervorstoß, seht Euch um und seht zu, dass es nicht ins Auge geht.', '', '', '', '', '', '', 22522),
(43007, 'deDE', 'Kehrt zur Kammer der Schatten zurück.', 'Sprecht mit Lord Jorach Rabenholdt in der Kammer der Schatten.', 'Lord Jorach Rabenholdt hat mich entsandt, um Euch zu suchen, $n. Er möchte Euch in einer dringenden Angelegenheit sprechen.\n\nEr erwartet Euch in der Kammer der Schatten.', '', 'Kehrt zu mir in die Kammer der Schatten zurück, sobald Ihr etwas Zeit habt.', 'Lord Jorach Rabenholdt', '', '', '', 22522),
(40142, 'deDE', 'Der Kern der Sturmschuppe', 'Besorgt $1oa Sturmschuppenfunken, indem Ihr Tiere mit Schuppen auf den Verheerten Inseln häutet.', 'Ah, ja. Die edle Sturmschuppe.$b$bIch habe ähnliche Schuppen in Nordend und einige am Himmelswall gesehen, aber diese Art Schuppen scheint hier ganz normal zu sein. Einer meiner Kollegen hat sich auf Kettenrüstungen spezialisiert und hätte sicher Interesse daran.$b$bUm diese Art Schuppen effizient zu nutzen, muss man zunächst einmal ihre Stärke verstehen. Achtet bei Eurer Beute auf zerbrochene, unverwendbare Schuppen und entfernt bei ihnen die Funken. Wenn Ihr erst die Funken gesehen habt, werdet Ihr die Sturmschuppe verstehen.', '', '', '', '', '', '', 22522),
(44545, 'deDE', 'Den Sturmangriff vorantreiben', 'Wählt Euren nächsten Angriffspunkt auf den Verheerten Inseln auf der Schlachtkarte in Eurer Ordenshalle aus.', '<Nachdem diese Säule der Schöpfung sicher unter Eurer Kontrolle ist, solltet Ihr Euren Schwung nutzen und Euch auf die Suche nach der nächsten machen.>$B$B<Ihr erinnert Euch, dass Euer Orden Informationen hat, die Euch behilflich sein könnten. Es wäre klug, sich diese vor Antritt der Reise noch einmal anzusehen.>', '', '', '', '', '', '', 22522),
(40990, 'deDE', 'Eine gute Rezeptliste', 'Erlernt 6 Kochrezepte von Legion.', 'Ich habe das Abenteurerleben ausprobiert, aber das ist nichts für mich. Meine wahre Leidenschaft ist das Kochen: die Hitze des Herdes, das Brutzeln des Specks... das ist einfach das Beste.$b$bLasst mich Euch helfen, $n. Wenn Ihr mir ein paar Rezepte bringt, kann ich sie hier in Dalaran vielleicht noch verbessern.$b$bWir würden ein tolles Team abgeben. Was meint Ihr?', '', '', '', '', '', '', 22522),
(38784, 'deDE', 'Probe eines Leysteinflözes', 'Bringt die Probe eines Leysteinflözes zu Mama Buddel in Dalaran.', 'Dieses Erz hat ganz eindeutig einige der latenten Leyenergien der Verheerten Inseln absorbiert. Es fühlt sich kalt an und reflektiert das Licht in seltsamen, verspielten Winkeln.$b$bDas sollte sich Eure Lehrerin in Dalaran ansehen.', '', '', '', '', '', '', 22522),
(38796, 'deDE', 'Probe eines Teufelsschieferflözes', 'Bringt die Probe eines Teufelsschieferflözes zu Mama Buddel in Dalaran.', 'Dieses Erz ist auffallend schwer. Aber was Euch eher beunruhigt, ist, dass es bei Berührung dunkler wird und zischt.$b$bVielleicht kann Eure Bergbaulehrerin in Dalaran etwas dazu sagen.', '', '', '', '', '', '', 22522),
(40015, 'deDE', 'Zerlumpte Seidenstreifen', 'Sprecht mit Kuhuine Sanftschreiter über Aethril und die Verdorrten.', 'In feine Streifen gerissener Stoff, der schon verwittert und ganz dreckig ist. Das sind klare Hinweise auf die Verdorrten.$b$bDiese wilden und degenerierten Ausgestoßenen der Nachtgeborenen scheinen auch Aethril zu sammeln. Aber warum...?', '', '', '', '', '', '', 22522),
(44087, 'deDE', 'Kehrt zur Teufelshammer zurück.', 'Sprecht mit Schlachtenfürst Gaardoun auf der Teufelshammer.', '$n, Schlachtenfürst Gaardoun hat mich angewiesen, nach Euch zu suchen. Er sagte, er bräuchte Eure Hilfe in einer sehr wichtigen Angelegenheit.\n\nEr möchte, dass Ihr so schnell wie möglich auf die Teufelshammer kommt.', '', 'Kehrt so schnell wie möglich zu mir auf die Teufelshammer zurück, Kommandant.', 'Schlachtenfürst Gaardoun', '', '', '', 22522),
(42516, 'deDE', 'Wachsende Macht', 'Sprecht mit Rensar Großhuf im Hain der Träume.', 'Erzdruide Rensar Großhuf wünscht, Euch zu sehen.$B$BUnsere Kampagne zur Verteidigung der Verheerten Inseln ist angelaufen und wir benötigen Eure leitende Hand.', '', '', '', '', '', '', 22522),
(43980, 'deDE', 'Noch eine alte Waffe', 'Wählt ein zweites Artefakt, das Ihr suchen wollt.', 'Ihr habt Euch gut geschlagen, aber unsere Feinde sind mächtig und hinterlistig. Es wäre klug, auf alles vorbereitet zu sein, was sie auf uns loslassen.$b$bIhr wäret gut beraten, darüber nachzudenken, noch eine uralte Waffe zu besorgen, die uns in diesem Kampf weiterhelfen könnte.$b$bLasst mich wissen, wie Ihr verfahren wollt.', '', '', '', '', '', '', 22522),
(42213, 'deDE', 'Golganneths Gezeitenstein', 'Verwendet den zentralen Teleporter in Dalaran und platziert Golganneths Gezeitenstein dann in in der Porträtgalerie der Kammer des Wächters.', '<Nachdem Ihr Königin Azsharas Zorn nun besiegt habt und den Gezeitenstein in Händen haltet, ist es an der Zeit, ihn in der Porträtgalerie der Kammer des Wächters in Dalaran zu platzieren.>', '', '', '', '', '', '', 22522),
(44437, 'deDE', 'Legiondungeon - Belohnungsquest', '', '', '', '', '', '', '', '', 22522),
(38286, 'deDE', 'Azsharas Zorn', 'Begebt Euch in das Auge Azsharas und besiegt dann Kriegsherr Parjesh und Azsharas Zorn.', '$n, Kriegsherr Parjesh hat sich in den magischen Vortex gleich da vorne gestürzt. Er ist zu einer Insel vor der Küste im Südosten zurückgekehrt, wo Königin Azshara weitere Truppen zusammenzieht.\n\nDie Königin schäumt vor Wut, dass wir uns ihr entgegenstellen. Sie beschwört gerade eine Monstrosität, die als ihr Zorn bekannt ist, um uns für unseren Widerstand zu strafen und die Verheerten Inseln untergehen zu lassen.\n\nIhr müsst Azsharas Zorn besiegen, bevor die Königin ihn entfesselt und uns alle vernichtet!', '', '', '', '', '', '', 22522),
(37470, 'deDE', 'Der Kopf der Schlange', 'Besiegt Athissa.', 'Diese Naga haben die Splitter des Gezeitensteins, $n. Sie werden sie schon bald in ihren nassen Löchern verstecken, wo wir sie nie erreichen können.$b$bEs wäre klug, ihre Anführerinnen auszuschalten, bevor sie sich zurückziehen.', '', '', '', '', '', '', 22522),
(37530, 'deDE', 'Rettet Euch', 'Sprecht mit Farondis und lasst ihn erzählen, wie er Euch gerettet hat.', 'Natürlich konnte ich nicht einfach nur zusehen, wie sie Euch davonkarren.$b$bIch werde Euch alles erklären.', '', '', '', '', '', '', 22522),
(37469, 'deDE', 'Der Gezeitenstein: Zersplittert', 'Sammelt $1oa Splitter des Gezeitensteins ein.', 'Dann könnt Ihr wohl eintreten.$b$bDie zerschmetterten Teile von Golganneths Gezeitenstein werden im Ostflügel der Akademie verwahrt. Selbst als der zuständige Kurator kann ich nichts über ihren Zustand sagen, denn ich war seit Jahrtausenden nicht mehr dort drin.$b$bUnd noch etwas, $n... Bitte nichts anfassen, ja?', '', '', '', '', '', '', 22522),
(37730, 'deDE', 'Der Schlüssel der Direktorin', 'Holt den Schlüssel des Gezeitensteingewölbes von Elya Azurmond, der Direktorin der Akademie.', 'Ich habe so eine Ahnung, dass Ihr Eure Studien vernachlässigt. Aber ich sehe, dass Ihr es kaum abwarten könnt, die Direktorin zu treffen.$b$bAlso gut! Die Direktorin Azurmond ist gleich da oben. Aber ich befürchte, dass sie nicht gern gestört wird.', '', '', '', '', '', '', 22522),
(37729, 'deDE', 'Stegreifaufgabe: Fortgeschrittenes Runenzeichnen', 'Zeichnet eine arkane Rune, eine Feuerrune und eine Frostrune nach.', 'Ihr seid noch immer wild darauf, in das Studienzimmer der Direktorin zu gelangen, was? Ich denke, ich kann Euch hineinlassen... sofern Ihr mir beweisen könnt, dass Ihr auch gelernt habt.$b$bDie korrekte Runenformung ist für das Zauberwirken von entscheidender Bedeutung. Wenn Ihr eine Rune zeichnen könnt, könnt Ihr auch einen Zauber wirken.$b$bIch werde Euch einen Umriss vorgeben, Ihr könnt dann die Runen einfach abschreiten.', '', '', '', '', '', '', 22522),
(42371, 'deDE', 'Studiersaal: Kampfkunde', 'Holt die Bücher, um die Sythorne Euch gebeten hat, und helft ihm dann, die darin beschriebenen Herausforderungen zu meistern.', 'Was haben wir denn hier? $gEin neuer, verwirrter Schüler, der:Eine neue, verwirrte Schülerin, die; ziel- und beschäftigungslos die Korridore durchstreift, ganz erpicht darauf, die Gunst der Direktorin zu gewinnen?$b$bPerfekt! Ich benötige Hilfe bei meiner Forschung.$b$bIch verfasse eine wissenschaftliche Arbeit über Kampftechniken. Holt mir ein paar Bücher aus dem Regal dort drüben.', '', '', '', '', '', '', 22522),
(42370, 'deDE', 'Einfache Zauberstabkunde', 'Übt an $1oa Zauberstabzielen, Euren Zauberstab zu benutzen.', 'Wir werden mit etwas Zauberstabarbeit beginnen. Ihr habt Euren Zauberstab dabei? Gut.$b$bIch habe mehrere Ziele in der Halle aufgestellt. Nutzt Euren Zauberstab, um so viele wie möglich davon zu treffen.$b$bUnd keine Schüsse aus nächster Nähe! Das wäre Mogelei.', '', '', '', '', '', '', 22522),
(37518, 'deDE', 'Die verfluchten Hallen', 'Begebt Euch in den Westflügel der Akademie.', 'Was wolltet Ihr noch gleich? Ihr seid hier, um Euch Golganneths Gezeitenstein anzusehen? Hah! Ho ho ha ha haa!$b$bOoooh Mann.$b$bNein, nur die Direktorin der Akademie hat den Schlüssel zu dieser Kammer, und sie hat diese Tür in... hm, ich würde sagen, in den letzten zehntausend Jahren nicht mehr geöffnet.$b$bUnd jetzt solltet Ihr Euch zum Unterricht aufmachen! Na los! Ab in den Westflügel!', '', '', '', '', '', '', 22522),
(37678, 'deDE', 'Zuschlagen statt nachschlagen', 'Zerstört 3 uralte Folianten.', '<Die Bibliothekarin spricht leise mit Euch.>$b$bEin Problem beim Betreiben einer solch großen arkanen Bibliothek besteht darin, dass die Bücher oft die ein oder andere unvorsichtig ausgesprochene Verzauberung aufnehmen und dann anfangen, von sich aus herumzufliegen. Diese magisch übersättigten Bücher müssen zerstört werden, bevor sie größeren Schaden anrichten.$b$bWenn Ihr einem von diesen entnervenden Essays begegnen solltet, kümmert Euch bitte darum. Aber leise.', '', '', '', '', '', '', 22522),
(37736, 'deDE', 'Kleider machen Leute', 'Besorgt Euch Hut, Gewand, Zauberstab und Zauberbuch der Akademie von Nar''thalas.', 'Moment mal. Wie seht Ihr eigentlich aus?! Als wäret Ihr geradewegs von der Straße hier hereinmarschiert. Wo sind Euer Gewand und Euer Hut?$b$bUnd erzählt mir nicht, Ihr wolltet die Akademie ohne Euren Zauberstab und Euer Zauberbuch betreten.$b$bImmer diese Erstsemester.$b$bSammelt Euren Kram zusammen. Dann werde ich Euch vielleicht Eintritt in die Akademie gewähren.', '', '', '', '', '', '', 22522),
(37468, 'deDE', 'In die Akademie', 'Betretet die Hallen der Akademie von Nar''thalas.', 'Das versunkene Gebäude auf der anderen Seite der Brücke wurde einst die Akademie von Nar''thalas genannt. Sie war das Zentrum aller magischen Studien in ganz Azeroth. Ihr hättet sie zu ihren prachtvollsten Zeiten sehen sollen.$b$bDarin sollte sich der Gezeitenstein befinden. Ich selbst traue mich dort nicht hinein. Aber Ihr, mein Champion, habt meinen Segen und könnt gehen.', '', '', '', '', '', '', 22522),
(37467, 'deDE', 'Weg der Schmach', 'Begleitet Prinz Farondis am Rand der Stadt Nar''thalas entlang.', 'Der Widerhall von Nar''thalas verfolgt mich immer noch und ich kann das Leid dieser verfluchten Stadt nicht mehr ertragen. Aber ich kann Euch vielleicht immer noch helfen, an den Gezeitenstein zu gelangen.$b$b<Farondis'' Entschlossenheit ist zurückgekehrt und seine Augen verengen sich.>$b$bWir müssen uns am Rand der Stadt entlangbewegen. Folgt mir.', '', '', '', '', '', '', 22522),
(42694, 'deDE', 'Zurück von den Toten', 'Verwendet die Seherkugel, um zu sehen, wo sich die Nachtelfengeister aufhalten könnten, und zieht dann los, um sie zu retten.', '<Olothil seufzt.>$b$bMeine Schwester kann manchmal ganz schön meine Geduld strapazieren.$b$bAber egal... Ich habe ein paar Orte identifiziert, wo sich unsere Eltern aufhalten könnten. Ich werde zusammen mit Kallistia diese Gegend absuchen. Ihr habt uns schon so viel geholfen... Würdet Ihr uns noch einmal helfen und im Osten nach ihnen suchen?$b$bIhr könnt meine Seherkugel verwenden, um Euch die Orte anzuschauen, die unser Ritual offenbart hat.', '', '', '', '', '', '', 22522),
(42693, 'deDE', 'Hellsicht ist besser als Nachsicht', 'Besorgt $1oa Glypten der Hassnattern.', 'Wenn ich die Geister unserer Familie finden will, benötige ich Materialien, um ihren Aufenthaltsort wahrzusagen.$b$bDiese Naga besitzen von Leyenergie erfüllte magische Schmuckstücke. Die sind besser auf dieses Land abgestimmt als alles, was wir in Dalaran haben. Mit ein paar davon könnte ich richtige Hellseherei leisten.', '', '', '', '', '', '', 22522),
(42692, 'deDE', 'Kinder von Nar''thalas', 'Sucht nach drei uralten Folianten der Hochgeborenen in den Ruinen von Nar''thalas.', 'Hallo! Ihr seid $n, richtig? Die Erzmagier in Dalaran erwähnten, dass Ihr vielleicht hier unten auftauchen würdet.$b$bMein Bruder und ich sind hier auf einer Aufklärungsmission unterwegs. Wir suchen nämlich nach unseren Eltern.$b$bUnser ganzes Leben lang hatten wir geglaubt, dass unsere Familie vor einigen tausend Jahren in Azsuna getötet worden sei. Aber seht selbst: Die meisten Elfen hier sind Geister.$b$bOlothil hat die Bücher auf der Suche nach Hinweisen studiert, aber wir könnten etwas Hilfe brauchen.', '', '', '', '', '', '', 22522),
(37486, 'deDE', 'Nar''thalas leidet immer noch', 'Besiegt Athissa.', 'Sie haben mir noch nicht vergeben. Ich kann es ihnen nicht verdenken.$b$bAber von den Verbrechen der Vergangenheit abgesehen bin ich immer noch ihr Prinz. Selbst im Tod werde ich alles tun, um mein Volk zu beschützen.$b$b$n, seid mein Champion. Findet und erledigt die Kreatur, die diesen Nagaangriff gegen das Volk von Nar''thalas anführt. Nach all den Jahren werdet ausgerechnet Ihr, $gein:eine:r; $R, das Werkzeug meiner Rache sein.', '', 'Gezeitenherrin der Hassnattern', 'Athissa', '', '', '', 22522),
(37466, 'deDE', 'Die verheerte Akademie', '', '', '', '', '', '', '', '', 22522),
(40988, 'deDE', 'Zu viele Köche', 'Sucht Nomi in Dalaran auf.', 'Seid gegrüßt, mein Name ist Nomi.$b$bIch habe gerade das Rezept bemerkt, das Ihr aufgehoben habt und... nun, ich kenne mich auch ein wenig mit dem Kochen aus.$b$bLeider bin ich im Kämpfen weniger begabt und dies ist ein sehr gefährlicher Ort. Ihr dagegen seid ganz offensichtlich $gein anerkannter:eine anerkannte:c; $C.$b$bSucht mich in Dalaran auf. Vielleicht können wir zusammenarbeiten!', '', '', '', '', '', '', 22522),
(37496, 'deDE', 'Infiltriert die Schiffbrucharena ', 'Infiltriert die Schiffbrucharena und findet Okuna Langhauer.', 'Die Riesen an der Schiffbrucharena haben große Freude daran, Schiffe zu zerstören, die dem Ufer zu nahe kommen. Sie nehmen die Matrosen gefangen und missbrauchen sie zu ihrem eigenen Vergnügen.\n\nIch vermute, dass Ihr dort mit Leichtigkeit eindringen könnt. Das Ausbrechen könnte schon schwieriger werden.', '', '', '', '', '', '', 22522),
(37497, 'deDE', 'Auf der Spur des Gezeitensteins', 'Begleitet Prinz Farondis zu den Ruinen von Nar''thalas.', 'Eure Suche nach dem Gezeitenstein... fasziniert mich. Mehr noch, sie kommt mir sogar sehr gelegen, denn ich bin schon seit sehr, sehr langer Zeit auf der Suche nach einem Champion.$b$bDamit steht es also fest. Ich werde Euch begleiten.$b$bWir müssen nach Osten durch Nar''thalas.$b$bWollen wir aufbrechen?', '', '', '', '', '', '', 22522),
(37257, 'deDE', 'Bis auf die Knochen', 'Sammelt $1oa gestohlene Nachtelfenknochen ein.', 'Ihr sucht Golganneths Gezeitenstein?$b$bJa, ich weiß, wo er ist. Er ist an einem Ort, an den ich nicht mehr zurückkehren möchte.$b$bIhr könntet mir vielleicht von Nutzen sein. Im Zuge dieses Nagaangriffs sind diese widerwärtigen Fischleute über mein Land gekrochen und haben unsere Grabstätten geschändet. Sie haben die Gebeine unserer Vorfahren und meiner Familie gestohlen.$b$bHelft mir, meinen Leuten etwas Trost zu spenden. Dann werde ich Euch etwas mehr über den Gezeitenstein erzählen.', '', '', '', '', '', '', 22522),
(37492, 'deDE', 'Ein ziemlich langer Spaziergang', 'Sucht nach dem Ältesten Aldryth.', 'Ich mache mir Sorgen um den Ältesten Aldryth. Er ist vor ungefähr zehntausend Jahren zu einem Spaziergang aufgebrochen und noch nicht wieder zurück.$b$bIch fürchte, dass er von diesen Naga angegriffen werden könnte!$b$bBitte haltet Ausschau nach dem Hofältesten, wenn Ihr Euch dort ins Gefecht hinauswagt.', '', '', '', '', '', '', 22522),
(37727, 'deDE', 'Der Magister der Mixologie', 'Sammelt $1oa arkangesättigte Eier ein.', 'Ihr müsst wissen, dass ich ein Liebhaber exklusiver Tränke bin... insbesondere arkangesättigter Brände. Das Mixen von Cocktails ist Alchemie und Alchemie ist ja Magie, wie jeder weiß.\n\nMein neuester Trank muss exotischer als alle anderen zuvor werden. Ich habe dafür schon fast alles zusammen, was ich brauche. Es fehlen nur noch ein paar Zutaten auf der Liste.\n\nHabt Ihr die Reiher in der Lagune bemerkt? Wie viele andere Lebewesen in Azsuna werden auch sie von der Magie dieser Insel durchdrungen.\n\nIhre Eier sind genau das Richtige für mich.', '', '', '', '', '', '', 22522),
(37728, 'deDE', 'Das Auge trinkt mit', 'Sammelt die sechs Augen von Gangamesh ein.', 'Die Eier werden dem Charakter meiner Tränke etwas Pep verleihen. Aber es fehlt noch eine weitere Komponente, und die darf man nicht unterschätzen!\n\nDie Dekoration.\n\nMan könnte natürlich die übliche Olive auf einem Zahnstocher verwenden. Oder einen nachlässig drapierten Karottenstängel.\n\nAber das ist nicht mein Stil.\n\nOh, nein, $gmein Freund:meine Liebe;. Was MEIN Trank als Alleinstellungsmerkmal braucht, kommt von einer einzigartigen Kreatur. Ich werde ihn nur ein einziges Mal kredenzen und deswegen muss es etwas Besonderes sein.\n\nWas ich brauche, sind die Augen von Gangamesh.', '', 'Magister Garuhod verlangt nach allen sechs Augen von Gangamesh, um seinen neuesten Super-Cocktail zu dekorieren.', 'Gangamesh', '', '', '', 22522),
(37733, 'deDE', 'Prinz Farondis', 'Trefft Euch mit Nachtwächterin Idri am Zerfallenen Palast.', 'Es freut mich, dass meine Zweifel an Euch unbegründet waren.$b$bIhr wünscht, Prinz Farondis zu sprechen? Dann folgt mir. Ich werde Euch zur Halle des Prinzen bringen.', '', '', '', '', '', '', 22522),
(37256, 'deDE', 'Sie kamen aus dem Meer', 'Holt Euch den Kopf von Tiefenkommandant Zarrin und tötet $2oa seiner Nagakämpfer.', 'Ihr seid auf der Suche nach Prinz Farondis?$b$b<Die Augen des geisterhaften und argwöhnischen Elfs verengen sich zu Schlitzen.>$b$bIch kann Euch zu Farondis bringen... Aber der Reihe nach... Diese Naga haben urplötzlich angegriffen. Ihren Befehlshaber, einen gewissen Zarrin, findet Ihr in den flachen Hügeln im Westen.$b$bWenn Ihr mir helft, den Kopf von Zarrin zu erbeuten, werde ich Euch anhören.', '', 'Tötet ihn und die Naga, die ihm dienen!', 'Tiefenkommandant Zarrin', '', '', '', 22522),
(42756, 'deDE', 'Das Ende des Hungers', 'Tötet Ael''Yith und sein Konstrukt.', 'Ich bin schwach. Der Hunger zehrt mich auf. Ich verstehe jetzt, warum Euer Drache in diesem Teich liegt.$b$b<hust, hust>$b$bIch bin gekommen, um Euch zu warnen, aber... es scheint zu spät zu sein.$b$bIch kann ihn da draußen hören. Ael''Yith... ist... hier...', '', '', '', '', '', '', 22522),
(42567, 'deDE', 'Zum Verdorren verdammt', 'Sucht die Nachtsüchtigen, die in die Leygrotte gegangen sind.', 'Cedonu hat also gesagt, er hätte Nachtsüchtige in die Leygrotte gehen sehen?$b$bDas gefällt mir überhaupt nicht.$b$bGroßvater scheint gut allein zurechtzukommen, aber der Erzmagier und ich werden ein Auge auf ihn haben. Ihr solltet Eurerseits mal diese Höhlen untersuchen.', '', '', '', '', '', '', 22522),
(38014, 'deDE', 'Sich an den Drachen laben', 'Tötet $1oa Verdorrte in der Azurschwingenrast.', 'Ihr habt ihren Anführer umgebracht und sie sind hierhergekommen? Das ist nachvollziehbar. Diese Kreaturen haben es einzig und allein auf Magie abgesehen, und sie können sie über große Entfernungen hinweg wittern.$b$bOhne Anführer werden sie sehr schnell verdorren und sterben. In der Zwischenzeit bleibt uns nur, so viele von ihnen wie möglich zu töten, bevor sie sich die Drachen greifen!', '', '', '', '', '', '', 22522),
(38015, 'deDE', 'Kein Zurück', 'Verwendet den strahlenden Leykristall oder Eure Heilzauber und Heilfähigkeiten, um die verwundeten Drachen zu heilen.', 'Diese vielen Verdorrten drohen, unsere ganze Brut auszulöschen!$b$bSchnell, $n! Brecht einen Leykristall ab und heilt alle verwundeten Drachen, die Ihr auf dem Schlachtfeld finden könnt.$b$bIch werde tun, was ich kann, um diese Welplinge zu schützen.', '', '', '', '', '', '', 22522),
(37862, 'deDE', 'Noch am Leben', 'Sprecht mit Stellagosa über einen Flug zurück zur Azurschwingenrast.', 'Wartet, was habe ich gehört? War es... "Labt Euch an den Drachen"? Wir müssen gleich Großvater fragen!$b$bSagt Bescheid, wenn Ihr zum Aufbruch bereit seid.', '', '', '', '', '', '', 22522),
(42583, 'deDE', 'Erhebt Euch, Champions', 'Rekrutiert Zen''tabra und Naralex.', 'Der Niedergang der Legion kann nicht allein herbeigeführt werden.$B$BUm diesen Krieg zu gewinnen, müssen wir wie in früheren Zeiten zusammenarbeiten.$B$BUnd dazu müssen wir erst einmal weitere Druiden rekrutieren, die uns helfen können. Sprecht mit der Trollin Zen''tabra und dem Nachtelfen Naralex.$B$BSie sind beide fähige Druiden und werden sicherlich bei der Verteidigung des Hains der Träume helfen wollen.', '', '', '', '', '', '', 22522),
(37860, 'deDE', 'Eine Klaue wäscht die andere', 'Lasst die Welplinge auf alle Manasammlerpylonen in Zarkhenar los.', 'Viel besser. Ich wusste dass, ich auf Euch zählen kann, $gmein Freund:meine Gute;. Und jetzt möchte ich mich mit einem Gefallen revanchieren.$b$bIch weiß, wie Ihr die großen Manasammlerpylonen zerstören könnt. Es gibt noch Hoffnung für Euren großen Drachen!$b$bDie Pylonen sind nur gegen Angriffe vom Boden, aber nicht aus der Luft geschützt. Lasst die Welplinge auf sie los. Im Gegensatz zu uns können sie fliegen.$b$bVertraut mir.', '', '', '', '', '', '', 22522),
(37861, 'deDE', 'Der Prinz der Nachtgeborenen', 'Tötet Ael''Yith.', '<Senegos wirkt konzentriert.>$b$bIch kann es jetzt spüren. In dem zerstörten Gebäude im Osten. Einer dieser Narren muss die Leylinie angezapft haben.$b$bIch gehe davon aus, dass er schwer bewacht wird, $n. Nur zur Erinnerung: Das Schicksal meiner Brut liegt in Euren Händen.', '', 'Er hat Euch von dort oben auf seinen Ruinen viel Ärger gemacht. Vielleicht solltet Ihr ihm einen Besuch abstatten...', 'Ael''Yith', '', '', '', 22522),
(37959, 'deDE', 'Ewig hungrig', 'Beschafft 4 aufgeladene Manajuwelen.', '<Runas wirkt nervös.>$b$bIch kann Euch helfen und ich will Euch wirklich helfen, aber ich bin gerade etwas abgelenkt. Seht Euch nur all das reine, ungefilterte, köstliche Mana an...$b$b<Runas gibt sich selbst eine Backpfeife.>$b$bNein! NEIN! Nicht die Beherrschung verlieren!$b$bIch muss nur wieder den Kopf freibekommen. Ja, genau... Ein paar meiner verkommenen Brüder und Schwestern führen Manajuwelen mit sich... für den kleinen Energieschub zwischendurch. Könntet Ihr ein paar davon für mich auftreiben? Dann werde ich Euch zeigen, wie Ihr die Leylinie zurückbekommt.', '', '', '', '', '', '', 22522),
(37960, 'deDE', 'Leylinienmissbrauch', 'Tötet Iskhaneth und Tyndrissen.', 'Diese Monster ziehen das Mana direkt aus dem Boden. Das erklärt, warum nicht einmal meine Manateiche meine Schmerzen lindern können.$b$bIch mache mir Sorgen um meine Brut. Ohne die Kraft der Leylinien sind wir gar nichts. Und wenn es so weitergeht, kann ich Euch vielleicht nicht einmal bei der Suche nach dem Gezeitenstein helfen.$b$bHaltet Ausschau nach den Manakondensatoren und bestraft diese schändlichen nachtsüchtigen Elfen, die sie benutzen.', '', '', '', '', '', '', 22522),
(37963, 'deDE', 'Nicht zu retten', '', '', '', '', '', '', '', '', 22522),
(37857, 'deDE', 'Runas kennt den Weg', 'Gestattet Runas, Euch zu begleiten.', 'Ich weiß zwar nicht, was ich getan habe, dass Ihr mich umbringen wolltet, aber ich möchte trotzdem Wiedergutmachung leisten.$b$bHm... Euer Drache beklagt sich über seine Schwäche. Ich kenne den Grund dafür. Runas kann das in Ordnung bringen, $gmein Freund:meine Liebe;.$b$bIn meinem Volk gibt es jemanden, der direkt aus dem Boden von Azsuna Leyenergie bezieht. Sein Name ist Ael''Yith. Ich kann Euch zu seinen Manakondensatoren bringen.$b$bEs ist nicht zu spät, den von den anderen verursachten Schaden wiedergutzumachen. Ihr habt recht daran getan, mir zu vertrauen.', '', '', '', '', '', '', 22522),
(37957, 'deDE', 'Runas der Geschmähte', 'Tötet Runas den Geschmähten.', 'Ich konnte mich nicht lange halten, $n. Ich bin nicht mehr so stark wie früher. Wir brauchen wirklich Eure Hilfe.$b$bEine dieser widerlichen Kreaturen ist mit ein paar meiner Welplingen davongelaufen. Ich habe aber seinen Namen gehört:$b$bRunas.$b$bIch werde überleben. Es ist nicht weit bis nach Hause. Ihr aber seid noch stark. Tötet diesen Runas, bevor er unserer Brut noch weiteren Schaden zufügen kann.', '', '', 'Runas der Geschmähte', '', '', '', 22522),
(37859, 'deDE', 'Die Aufgezehrten', 'Findet einen trüben Leykristall und holt dann $2oa manageschröpfte Welplinge ins Leben zurück.', '<Der Welpling ist am Leben, aber ohnmächtig. Einer der Verdorrten muss schon sein Mana abgesaugt haben.>$b$b<Ihr wisst bereits, dass freigelegte Leykristalle eine geringe Menge Energie bergen. Vielleicht könnt Ihr einen dieser Kristalle verwenden, um ein paar Welpen wiederzubeleben.>', '', '', '', '', '', '', 22522),
(37856, 'deDE', 'Die Verdorrten', 'Tötet $1oa Verdorrte im Welpenhort der Azurschwingen.', 'Die Verdorrten sind widerwärtige und gefährliche Kreaturen. Sie greifen sich unsere Welplinge, schlagen sie bewusstlos und saugen das Mana aus den wehrlosen Babys.$b$bNormalerweise würden unsere Drachen und Wächter sie vertreiben. Aber heute brauchen wir vielleicht Eure Hilfe.', '', '', '', '', '', '', 22522),
(37690, 'deDE', 'Die Erinnernden', 'Sucht Nachtwächterin Idri in den Ertränkten Gärten auf.', 'Einst waren die geisterhaften Elfen im Süden im Besitz der Säule der Schöpfung, die als Golganneths Gezeitenstein bekannt ist.$b$bIhre große Stadt Nar''thalas wurde in einem längst vergangenen Zeitalter zerstört. Der Herrscher der Stadt, Prinz Farondis, ging zusammen mit ihr unter. Ihr kleinen Wesen sterbt ja so leicht.$b$bWenn jemand weiß, wo sich die Fragmente befinden, dann der Geist dieses Prinzen. Macht Euch auf die Suche nach seinem Volk.', '', '', '', '', '', '', 22522),
(37855, 'deDE', 'Der Letzte seiner Art', 'Untersucht den Welpenhort der Azurschwingen.', 'Wir brauchen Hilfe.$b$bNach Todesschwinges Tod haben wir Drachen fast all unsere Kraft verloren. Und was noch schlimmer ist: Wir können keine Eier mehr legen.$b$bMeine Welplinge sind die letzten ihrer Art.$b$bIch brauche Euch jetzt, Champion. Helft mir, meinen Welpenhort zu verteidigen. Dann werden die Blauen Euch zur Seite stehen, wenn Ihr sie braucht, das schwöre ich.', '', '', '', '', '', '', 22522),
(42271, 'deDE', 'Ihr letzter Atem', 'Beschafft $1oa gesprungene Leykristalle und benutzt sie, um die manageschröpften Welplinge wiederzubeleben.', 'Mir bleibt nicht viel Zeit. Meine Truppen sind jetzt gerade damit beschäftigt, einen Ansturm verdorrter Elfen zurückzuschlagen.$b$bDiese vier Welplinge sind dem Tode nahe. Ihre Leben sind für unseren Schwarm äußerst wichtig, denn wir wissen nicht, ob Senegos noch mehr Welplinge großziehen kann.$b$bEin paar gesprungene Leykristalle sollten sie wieder auf die Beine kommen lassen.', '', '', '', '', '', '', 22522),
(37991, 'deDE', 'Agapanthus', 'Schaut nach den Besuchern in der Azurschwingenrast und bietet Eure Unterstützung an.', '<Dieser weibliche Welpling versucht, Eure Aufmerksamkeit zu erregen. Sie schaut Euch in die Augen und deutet dann schnell mit ihrem Kopf in Richtung Nordosten.>', '', '', '', '', '', '', 22522),
(38785, 'deDE', 'Probe lebenden Leysteins', 'Bringt die Probe lebenden Leysteins zu Mama Buddel in Dalaran.', 'Das ist kein normaler Leystein. Dieser hier ist viel lebendiger und schimmert in komplexen, beinah hypnotischen Mustern. Man könnte sich glatt darin verlieren.$b$bEure Lehrerin in Dalaran würde sicher gern einen Blick darauf werfen.', '', '', '', '', '', '', 22522),
(37853, 'deDE', 'Der Tod des Ältesten', 'Tötet Leywürmer, um $1oa knisternde Leywurmkerne zu beschaffen, und werft sie dann in Senegos'' Teich.', 'Ich werde tun, was ich kann, um ihn zu stabilisieren, aber ich brauche Eure Hilfe.$b$bDie wenige Energie, die ihm verbleibt, bezieht er aus dem Manateich, in dem er liegt.$b$bMacht es dem Welpling nach. Wenn wir Leykristalle in den Teich werfen, bezieht er daraus etwas Energie und somit noch ein wenig mehr Lebenszeit.$b$bSie scheinen diese Kristalle aus der Höhle im Norden zu beziehen.', '', '', '', '', '', '', 22522),
(42238, 'deDE', 'Vermisster Dämon', 'Sucht nach Daglop in Azsuna.', 'Ich habe wohl meinen Wichtel Daglop verloren.$b$bSagt ihm doch, dass er sich bei mir melden soll, falls ihr ihn seht.$b$bNormalerweise würde ich ihn einfach neu beschwören, doch ich befürchte, dass der blinde Marius ihn auf der Stelle töten und erst dann Fragen stellen würde.$b$bDem fehlt einfach der Weitblick.', '', '', '', '', '', 'Sprecht mit Daglop in den Olivanhöhen.', 22522),
(42372, 'deDE', 'Der Teufelsfeuervorstoß', 'Trefft Euch mit Tehd Shumaker am Teufelsfeuervorstoß.', 'Es ist meine Aufgabe, Rückendeckung für den Verteidigungsposten der Illidari zu gewährleisten. Ich habe dieses Hexenmeister...ding in meine Dienste genommen, um mich zu unterstützen.$b$bEs gibt jedoch eine wichtige Aufgabe, der wir zwei allein nicht gewachsen sind.$b$bIm Osten befindet sich eine massive Ansammlung von Dämonen im Teufelsfeuervorstoß. Diese Kreaturen haben begonnen, Portale zu öffnen, um zahllose Verstärkungstruppen herbeizurufen.$b$bTrefft uns am Teufelsfeuervorstoß. Gemeinsam können wir diese Gefahr abwenden.', '', '', '', '', '', 'Trefft Euch mit Tehd Shumaker am Teufelsfeuervorstoß.', 22522),
(40141, 'deDE', 'Sturmschuppenprobe', 'Bringt die Sturmschuppenprobe zu Kondal Jägerschwur in Dalaran.', 'Die Schuppe leuchtet vor elektrischer Energie auf.$b$bEuer Kürschnerlehrer in Dalaran hätte sicherlich Interesse daran.', '', '', '', '', '', '', 22522),
(38443, 'deDE', 'Reise zur Rast', 'Sprecht mit Erzmagier Khadgar bei der Azurschwingenrast.', 'Zurzeit können die Illidari noch die Front gegen die Legion halten. Wir werden später zurückkehren und ihnen helfen, die Legion aus den höheren Gebieten Faronaars zu vertreiben. Aber jetzt müssen wir uns auf die Säulen der Schöpfung konzentrieren, um das Grabmal des Sargeras zu versiegeln.\n\nEs ist ein glücklicher Umstand, dass Ihr Stellagosa gerettet habt. Wenn jemand den Standort einer Säule der Schöpfung kennt, dann ist es ihr Großvater Senegos.\n\nDie Azurschwingenrast befindet sich im Nordosten. Ich treffe Euch dann dort, $n.', '', '', '', '', '', '', 22522),
(37449, 'deDE', 'Finstere Offenbarungen', 'Tötet Nachtgleve die Verräterin.', 'Damit wird Cordana Teufelsang nicht davonkommen. Ich werde nicht zulassen, dass diese Wächterhexe mein Volk gegen uns aufbringt.\n\nUnd was diese Nachtgleve betrifft... Verrätern gewähre ich KEINERLEI Gnade.\n\nWerdet Ihr mir helfen, $n?', '', '', '', '', '', '', 22522),
(44140, 'deDE', 'Von innen heraus', 'Sucht nach Kor''vas Blutdorn auf Faronaar am Triumphposten.', 'Unsere Dämonenjäger, Kor''vas Blutdorn und Cyana Nachtgleve, sind mit einer kleinen Streitmacht der Illidari nach Faronaar übergesetzt. Ihre Mission war es, die Legion von innen heraus zu schwächen.\n\nIch habe schon eine Weile nichts von ihnen gehört.\n\nEs wäre das Beste, wenn Ihr sie finden und zum Erfolg führen könntet. Einverstanden?', '', '', '', '', '', '', 22522),
(37450, 'deDE', 'Stellagosas Rettung', 'Beschafft Lykills Schlüssel und befreit Stellagosa.', 'Mitten im Kampfgetümmel entdeckte ich plötzlich einen blauen Drachen, der sich auf die Dämonen stürzte. Ich konnte meinen Augen kaum glauben, denn ich hatte gehört, der blaue Drachenschwarm sei ausgestorben.\n\nDie Legion konnte den Drachen zwar überwältigen, aber zuvor hatte er jede Menge ihrer Kämpfer getötet. Ein wundervolles Geschöpf. Ich sah dann, wie sie den Drachen in Richtung ihres südlichen Lagers davonschleppten.\n\nWir sollten nachschauen, ob der Drache noch lebt. Er könnte ein wichtiger Verbündeter für uns werden.', '', '', '', '', '', '', 22522),
(37656, 'deDE', 'Teuflische Machenschaften', 'Befreit $1oa Dämonenjäger aus den Seelenerntern.', 'Meine Truppen haben den Vorposten der Dämonen hier ausgelöscht, aber eine Wichtelmutter und ihre Brut haben meine überlebenden Dämonenjäger einschließlich Cyana Nachtgleve als Geiseln genommen.\n\nWir müssen die Illidari befreien und die Brennende Legion vernichten.', '', '', '', '', '', '', 22522),
(36811, 'deDE', 'Rückeroberung von Faronaar', '', '', '', '', '', '', '', '', 22522),
(36920, 'deDE', 'Von innen heraus', 'Sucht nach Kor''vas Blutdorn auf Faronaar am Triumphposten.', 'Einige unserer Dämonenjäger, Kor''vas Blutdorn und Cyana Nachtgleve, sind mit einer kleinen Streitmacht der Illidari nach Faronaar übergesetzt. Ihre Mission war es, die Legion von innen heraus zu schwächen.\n\nIch habe schon eine Weile nichts von ihnen gehört.\n\nEs wäre das Beste, wenn Ihr sie finden und zum Erfolg führen könntet. Einverstanden?', '', '', '', '', '', '', 22522),
(37660, 'deDE', 'Die Seelensense', 'Arbeitet mit Allari der Seelenfresserin zusammen, um die Informationen von den Dämonenseelen zu bekommen.', 'Gehen wir auf die andere Seite der Tribüne. Wir werden die Dämonenseelen eine nach der anderen freilassen und dann die Antworten bezüglich Fürst Illidan bekommen.\n\nDafür wird die Seelensense schon sorgen.', '', '', '', '', '', '', 22522),
(38797, 'deDE', 'Probe lebenden Teufelsschiefers', 'Bringt die Probe lebenden Teufelsschiefers zu Mama Buddel in Dalaran.', 'Ihr entwickelt langsam einen echten Bergarbeiterinstinkt. Die Brocken der Höllenbestie scheinen nutzbar zu sein.$b$bAber es muss einen Weg geben, noch mehr Erz aus den Dämonenleichen zu gewinnen. Vielleicht weiß Eure Bergbaulehrerin in Dalaran etwas darüber.', '', '', '', '', '', '', 22522),
(40014, 'deDE', 'Sterilisierung mit dem Spaten', 'Beschafft einen makellosen Blütenstempel und ein unangetastetes Staubgefäß.', 'Stängel, Blätter und Blüten sind sicher faszinierend... aber die Staubfäden und Reproduktionsorgane in der Mitte der Blüte sind wirklich einzigartig. Sie enthalten mehr Energie als der gesamte Rest der Pflanze.$b$bIch denke, wir können Aethril verantwortungsvoller und effizienter sammeln, wenn wir uns auf diese Teile konzentrieren.$b$bDiese Blüte scheint allerdings beschädigt zu sein. Um sicherzugehen, brauche ich unbeschädigte Teile.', '', '', '', '', '', '', 22522),
(37658, 'deDE', 'Wiederentzündung der Zauberschutze', 'Zündet 3 Dämonenzauberschutze neu an.', 'Die Dämonen schwächen meine Verteidigung. Sie scheinen viel mächtiger zu sein als jene, denen wir zuvor begegnet sind.\n\nKönnt Ihr mir helfen, die Zauberschutze neu zu entzünden und zu verhindern, dass die Legion unsere Stellung hier überrennt?', '', '', '', '', '', '', 22522),
(38842, 'deDE', 'Feenaufruhr', '', '', '', '', '', '', '', '', 22522),
(42431, 'deDE', 'Nithoggs Brut', '', '', '', '', '', '', '', '', 22522),
(40316, 'deDE', 'Der Kreischende Felsen', '', '', '', '', '', '', '', '', 22522),
(39371, 'deDE', 'Himmelshexen', '', '', '', '', '', '', '', '', 22522),
(40050, 'deDE', 'Die Schiffbruchgrotte', '', '', '', '', '', '', '', '', 22522),
(37779, 'deDE', 'Gut gegen Schuppen', '', '', '', '', '', '', '', '', 22522),
(37965, 'deDE', 'Ein verderbter Jahrgang', '', '', '', '', '', '', '', '', 22522),
(40132, 'deDE', 'In einem Stück', 'Sammelt 20 unzerteilte Bälge, indem Ihr die Tiere der Verheerten Inseln fachgerecht häutet.', '<Euer Lehrer schaut sich den Balg an.>$b$bDieser Balg lässt sich gut verwenden, aber leider ist er sehr klein. Ihr zollt dem Tier zu wenig Respekt, wenn Ihr es so zerhackt, $n. Ihr macht zu wenig aus diesem Geschenk der Natur.$b$bZieht noch einmal los in die Wildnis. Zieht den Tieren unter uns die Häute in einem Stück ab und bringt sie mir dann.', '', '', '', '', '', '', 22522),
(40131, 'deDE', 'Steinbalglederprobe', 'Bringt die Steinbalglederprobe und $1oa weitere Stücke Steinbalgleder zu Kondal Jägerschwur in Dalaran.', 'Dieser Lederfetzen scheint Aufschluss über die Qualität der Bälge von Tieren auf den Verheerten Inseln zu geben.$b$bEuer Kürschnerlehrer in Dalaran hätte sicherlich Interesse daran.', '', '', '', '', '', '', 22522),
(37653, 'deDE', 'Dämonenseelen', 'Setzt $1oa Dämonenseelen an der Faronaarfront fest.', 'Ich muss diesen widerwärtigen Kreaturen Informationen über die Brennende Legion entlocken. Aber im lebendigen Zustand lassen die Dämonen sich kaum brechen.\n\nGenau wie Ihr bin jedoch auch ich im Besitz eines mächtigen Artefakts, der Seelensense.\n\nNehmt einen meiner Seelenedelsteine. Wenn Ihr ihn neben die Leiche eines Dämons legt, wird er ihm die "Seele" herausreißen. Und dann werden wir herausfinden, ob die Sense ihn zum Sprechen bringt.', '', '', '', '', '', '', 22522),
(38834, 'deDE', 'Auf ins Getümmel', 'Sprecht mit dem kommandierenden Dämonenjäger am Verteidigungsposten der Illidari.', 'Die Brennende Legion hat hier einen Sieg errungen, aber die Illidari auch. Anscheinend können die Dämonenjäger dem Ansturm standhalten... wenn auch nur mit Müh und Not.\n\nKommt, setzen wir Eure Waffe sinnvoll ein. Ich werde unsere nächsten Schritte planen, während Ihr die Legion niederkämpft.', '', '', '', '', '', '', 22522),
(41220, 'deDE', 'Nach Azsuna', 'Sprecht mit Erzmagier Khadgar, um nach Azsuna zu fliegen.', '$n, wir müssen einen Weg finden, um das Grabmal des Sargeras wieder zu versiegeln.\n\nIch habe Informationen zu einem uralten Relikt in Erfahrung gebracht. Dieses Relikt ist vielleicht der Schlüssel, um die Legion aufzuhalten.\n\nUnsere Suche beginnt auf der uralten Elfeninsel Azsuna. Vor langer Zeit haben dort die Hochgeborenen von Suramar ihre mächtigsten Magien perfektioniert. Jetzt findet sich dort nur noch eine verfluchte Ruine.\n\nDie Dämonenjäger der Illidari haben ein Lager auf der Insel errichtet. Da wir sonst niemanden in diesem Gebiet kennen, werden wir dort anfangen.\n\nSeid Ihr bereit für einen Flug?', '', '', '', '', '', '', 22522),
(39733, 'deDE', 'Der einsame Berg', 'Sprecht mit Kriegerheld Oro an Krasus'' Landeplatz in Dalaran.', 'Unsere Quellen haben uns berichtet, dass ein Abgesandter der Hochbergtauren vor Kurzem in Dalaran eingetroffen ist. Seine Absichten sind noch nicht bekannt, aber die Tauren könnten Verbündete von unschätzbarem Wert in dieser Gegend sein.$B$BKhadgar wird den Abgesandten bei Krasus'' Landeplatz in Dalaran empfangen. Ihr solltet Euch auch dort einfinden.$B$BDie Magier der Kirin Tor haben uns ein permanentes Portal eingerichtet, mit dem Ihr schnell in die Stadt reisen könnt.', '', '', 'Lord Grayson Schattenbruch', '', '', '', 22522),
(39864, 'deDE', 'Sturmheim', 'Sprecht mit Nathanos Pestrufer in der Violetten Zitadelle in Dalaran.', 'Nathanos Pestrufer, der Champion der Bansheekönigin höchstpersönlich, will, dass Ihr nach Dalaran kommt. Es klang dringend.$B$BDie Kirin Tor haben ein permanentes Portal nach Dalaran eingerichtet, damit Ihr schnell dorthineilen könnt. Er erwartet Euch in der Violetten Zitadelle.', '', '', 'Lord Grayson Schattenbruch', '', '', '', 22522),
(39731, 'deDE', 'Der friedvolle Forst', 'Sprecht mit Erzmagier Khadgar auf Krasus'' Landeplatz in Dalaran.', 'Erzmagier Khadgar hat uns vor Kurzem Informationen über Val''sharah zukommen lassen. Es scheint gute Neuigkeiten zu geben.$B$BDie Kirin Tor haben gleich dort drüben ein permanentes Portal nach Dalaran eingerichtet. Er möchte, dass Ihr ihn auf Krasus'' Landeplatz aufsucht.', '', '', '', '', '', '', 22522),
(39718, 'deDE', 'Verlorenes Paradies', 'Sprecht mit Erzmagier Khadgar bei Krasus'' Landeplatz in Dalaran.', 'Erzmagier Khadgar plant eine Expedition nach Azsuna. Ihr solltet ihn unverzüglich an Krasus'' Landeplatz in Dalaran aufsuchen.', '', '', 'Lord Grayson Schattenbruch', '', '', '', 22522),
(40653, 'deDE', 'Spuren legen', 'Wählt das erste Operationsgebiet Eures Ordens aus.', 'Die Brennende Legion bedroht die physische Existenz von Azeroth, aber der Alptraum hat es auf die Seele unserer Welt selbst abgesehen. Wir müssen beide Bedrohungen ausschalten, wenn wir überleben wollen.$b$bMithilfe dieser Karte können wir alle Entwicklungen auf den Verheerten Inseln nachvollziehen und zeitnah auf Bedrohungen reagieren. Aufgrund der begrenzten Anzahl unserer Truppen sollten wir sie zunächst da einsetzen, wo sie am meisten ausrichten können.$b$bNehmt selbst die Lage in Augenschein und entscheidet dann, wo wir beginnen sollen.', '', '', '', '', '', '', 22522),
(40652, 'deDE', 'Himmlische Informationen', 'Sprecht mit Himmelsfürst Omnuron.', 'Es ist an der Zeit, dass wir uns der Situation auf den Verheerten Inseln zuwenden.$B$BHimmelsfürst Omnuron hat Informationen über alle Vorgänge auf den Inseln zusammengetragen. Wenn sich eine günstige Gelegenheit bieten sollte, die Legion anzugreifen, wird er davon wissen.$b$bSucht ihn auf und hört Euch an, was er zu berichten hat.', '', '', '', '', '', '', 22522),
(41332, 'deDE', 'Aufstieg im erlauchten Kreis', 'Begleitet Erzdruide Großhuf und sprecht dann mit Malfurion Sturmgrimm.', 'Jetzt zählt Ihr zum Kreis der mächtigsten Druiden aller Zeiten, $n!$b$bIch spüre, dass Malfurion eingetroffen ist. Wir sollten ihm von Euren Erfolgen berichten. Aber begleitet mich bitte zunächst ein Stück.', '', '', '', '', '', '', 22522),
(40651, 'deDE', 'Die Saat der Zeitalter', 'Erfüllt Eure Waffe mit zusätzlicher Macht, indem Ihr einen Artefaktpunkt zuweist.', 'Diese Waffen wurden mithilfe uralter Magie erschaffen. Die wahre Macht Eures Artefakts liegt in seiner Fähigkeit, immer stärker zu werden, je mehr Ihr es verwendet.$B$BNachdem Ihr Eure Waffe nun auf die Saat der Zeitalter eingestimmt habt, könnt Ihr mithilfe ihrer Magie zusätzliche Attribute für Eure Waffe freischalten.$B$BVerwendet sie jetzt, um das Potenzial Eurer Waffe zu entfalten.', '', '', '', '', '', '', 22522),
(41255, 'deDE', 'Wer sät, der wird ernten', 'Pflanzt die Saat der Zeitalter in die weiche Erde im Hain der Träume.', 'Eure Waffe ist uralt und mächtig, aber sie birgt noch sehr viel mehr Potenzial.$b$bNehmt diesen Samen. Er ist alles, was von einem riesigen Wald übrig blieb, der zu Anbeginn dieser Welt gedieh. Pflanzt ihn im Hain westlich von hier ein und lasst ihn Wurzeln schlagen.$b$bVon seinen Kräften werden sowohl der Hain als auch Eure Waffe erheblich profitieren.', '', '', '', '', '', '', 22522),
(41689, 'deDE', 'Reinigung des Mutterbaums', 'Nutzt den Traumpfad, um zum Hyjal zu reisen, und läutert G''Hanir in den heiligen Wassern unter Nordrassil.', 'Wir müssen den Mutterbaum retten, koste es, was es wolle.$B$BIch glaube, ich weiß auch schon wie.$B$BDer Weltenbaum Nordrassil auf dem Hyjal entstand aus einer Eichel von G''Hanir. Wir können das heilige Wasser an dessen Wurzeln nutzen, um die Verderbnis zu läutern.$B$BWenn wir dem Traumpfad zum Gipfel des Hyjal folgen, sollte uns genug Zeit bleiben, um das Läuterungsritual durchzuführen, bevor G''Hanir gänzlich der Dunkelheit erliegt.', '', '', '', '', '', '', 22522),
(40900, 'deDE', 'Eine schwere Bürde', 'Kehrt zu Erzdruide Großhuf im Hain der Träume zurück.', 'Durch Eure Hilfe im Kampf gegen die Dunklen Reiter habt Ihr dem Licht Ehre erwiesen. Wir hängen vielleicht nicht demselben Glauben an, aber wir haben ein gemeinsames Ziel.\n\nAuf jeden Fall bin ich Euch dankbar, dass Euer Handeln mir ermöglicht, die Artefakte ihren rechtmäßigen Besitzern zurückzugeben. Und ich bin überzeugt, dass die Sichel von Elune bei Euch in den denkbar besten Händen ist.\n\nMöge sie Euch im Kampf gegen die Legion gute Dienste leisten, $n.', '', '', '', '', '', '', 22522),
(40838, 'deDE', 'Die Dunklen Reiter', 'Besiegt die Dunklen Reiter und beansprucht die Sichel von Elune für Euch.', 'Dieses Kanalisationsgitter führt zu den Katakomben unterhalb von Karazhan. Ich habe ihm nie große Beachtung geschenkt, aber das scheint genau der Ort zu sein, zu dem die Nachtheuler sich hingezogen fühlen.\n\nVielleicht haben sich die Dunklen Reiter ja die ganze Zeit direkt unter unseren Füßen aufgehalten! Kommt, lasst uns schauen, wo uns das hinführt.', '', '', '', '', '', '', 22522),
(40837, 'deDE', 'Die Jagd der Totenwinde', 'Folgt der Spur der Worgen der Nachtheuler.', 'Die Nachtheuler! Wenn sie sich am Gebirgspass der Totenwinde befinden, werden sie ebenfalls nach der Sichel suchen.\n\nWir sollten uns trennen und sie verfolgen. Mal schauen, wohin die Spur uns führen wird...', '', '', '', '', '', '', 22522),
(40835, 'deDE', 'Unberührte Zeugen', 'Sucht Aridens Lager nach Hinweisen auf den Aufenthaltsort der Dunklen Reiter ab.', 'Hier hatte der Händler Ariden sein Lager aufgeschlagen, als er einst versuchte, seine dubiosen Waren dem Zauberer Medivh zu verkaufen. Seltsamerweise ist es über all diese Zeit hinweg relativ unberührt geblieben. Deswegen glaube ich, dass immer noch eine Verbindung zum vormaligen Besitzer bestehen könnte.\n\nHelft mir, das Lager zu durchsuchen. Vielleicht finden wir hier etwas, das uns zu den Dunklen Reitern führt.', '', '', '', '', '', '', 22522),
(40834, 'deDE', 'Auf den Spuren des Fluchs', 'Begleitet Revil Kost vom Anwesen der Dunstmantels im Dämmerwald zu Aridens Lager am Gebirgspass der Totenwinde.', 'Bei meiner letzten Konfrontation mit Euren Feinden konnte ich einen Blick in ihre Vergangenheit erhaschen. Dank des Segens, den mir der Umhang der Reinheit gewährte, sah ich, wer die Dunklen Reiter früher waren.\n\nKommt mit mir zu Aridens Lager am Gebirgspass der Totenwinde. Ich werde Euch auf dem Weg alles Wichtige erklären.', '', '', '', '', '', '', 22522),
(40785, 'deDE', 'Ein Feind im Dunkeln', 'Beginnt Eure Suche nach der Sichel von Elune beim Anwesen der Dunstmantels im Dämmerwald.', 'Ich kann nicht fassen, dass die Dunklen Reiter uns hier gefunden haben. Schon lange haben sie nach der Sichel gesucht, denn sie begehren all die Artefakte von Azeroth. Wir dachten, im Hain wären wir sicher...\n\nUnd jetzt ist Valorn tot...\n\nIhr müsst $gzum Jäger:zur Jägerin; werden, $n. Sucht die Dunklen Reiter und holt die Sichel zurück. Zuletzt haben wir sie am Anwesen der Dunstmantels im Dämmerwald gesehen. Beginnt dort mit Eurer Suche.\n\nUnd $n... Lasst für Valorn Gerechtigkeit walten. Der Reiter muss büßen.', '', '', '', '', '', '', 22522),
(40784, 'deDE', 'Ihr rechtmäßiger Platz', 'Lasst Euch von Valorn die Sichel von Elune aushändigen.', 'Auch wenn ich nur wider besseres Wissen Folge leiste, wünscht Tyrande, dass die Sichel von Elune erneut gegen die Bedrohung durch die Legion in den Kampf getragen wird. Doch es beruhigt mich, dass die Waffe von einem Bewahrer des Gleichgewichts wie Euch selbst geführt werden wird.\n\nVergesst nicht, dass der letzte Kampf mit der Sichel zu einer großen Katastrophe geführt hat. Ihr müsst aufpassen, dass der in ihr wohnende bestialische Zorn nicht entfesselt wird.\n\nMit diesen Worten überlasse ich Euch nun die Sichel, auf dass Ihr unsere Feinde ihre Wut spüren lasst. Möge das Licht von Elune Euch behüten.', '', '', '', '', '', '', 22522),
(40783, 'deDE', 'Die Sichel von Elune', 'Trefft Euch mit Valorn Stillzweig im Zwielichtshain des Dämmerwalds.', 'Ich habe erfahren, dass Tyrande selbst die Freigabe eines bedeutenden Artefakts genehmigt hat. Die Sichel von Elune soll unserer Sache dienen. Valorn Stillzweig hat zugestimmt, uns die Waffe auszuhändigen, die er als Teil seiner Nachforschungen über die verfluchten Worgen des Dämmerwalds verwahrt hatte.\n\nIch glaube, dass nur bei Euch die Hoffnung besteht, die Macht der Waffe nutzen zu können, ohne der tierischen Wut zu erliegen, die in ihr wogt. Begebt Euch zum Dämmerwald und beschafft die Sichel für den Kampf gegen unsere Feinde!', '', '', '', '', '', '', 22522),
(40646, 'deDE', 'Legendäre Waffen', 'Wählt eine Artefaktwaffe aus, nach der Ihr suchen wollt.', 'Ich stimme mit Bewahrer Remulos überein. Um die Legion bekämpfen und besiegen zu können, benötigen wir Waffen mit legendären Kräften.$B$BIn der Geschichte der Druiden fanden vier solcher Waffen besondere Erwähnung.$B$BAlso sagt mir, nach welcher Waffe wollt Ihr zuerst suchen?', '', '', '', '', '', '', 22522),
(40645, 'deDE', 'Zum Hain der Träume', 'Geht durch das Portal zum Hain der Träume.', 'Das Portal zum Traumpfad steht offen und hier werden sich jetzt unsere Wege trennen. Die Mondlichtung ist weiterhin anfällig für die Angriffe der Brennenden Legion und ich muss dafür sorgen, dass sie gut geschützt wird.$b$bIch vertraue Euch die Mission in Val''sharah an, $n. Erzdruide Großhuf ist der dortige Anführer der Druiden. Er verfügt über uralte Weisheit und wird uns eine große Hilfe sein.$b$bDer Traum kann gefährlich sein, weswegen ich Bewahrer Remulos anweisen werde, Euch zu begleiten. Ihr trefft ihn auf der anderen Seite.', '', '', '', '', '', '', 22522),
(40644, 'deDE', 'Der Traumpfad', 'Helft bei der Durchführung des Rituals.', 'Die Vorbereitungen für das Ritual sind jetzt abgeschlossen.$b$bKommt, $n, ich werde Euch jetzt zur Zeremonie geleiten.', '', '', '', '', '', '', 22522),
(41106, 'deDE', 'Ruf der Wildnis', 'Macht Zen''tabra und Naralex ausfindig und sorgt dafür, dass sie sich an der Ritualstätte einfinden.', 'Um die Legion zu besiegen, müssen wir schnell und unerwartet zuschlagen.$b$bWir haben einen Traumpfad entdeckt, mit dessen Hilfe wir durch den Smaragdgrünen Traum selbst reisen können. Im Traum ist es nur eine kurze Strecke, aber auf Azeroth legen wir dabei riesige Entfernungen zurück und haben sogar Zutritt nach Val''sharah, dem uralten Zuhause aller Druiden.$b$bMalfurion bereitet gerade das Ritual zur Öffnung des Traumpfads vor. Ich möchte, dass Ihr ihm Zen''tabra und Naralex zur Seite stellt.$b$bWir werden nur dann erfolgreich sein, wenn wir all unsere Kräfte bündeln.', '', '', '', '', '', '', 22522),
(39958, 'deDE', 'Harthäutig', 'Sammelt $1oa Stücke Steinbalgleder und $2oa Sturmschuppen. Wenn Ihr $gkein Kürschner:keine Kürschnerin; seid, könnt Ihr diese Dinge im Auktionshaus oder von anderen Spielercharakteren erwerben.', 'Ich heiße Euch willkommen, $n. Es ist mir eine Ehre, mit jemandem Eures Ranges zusammenzuarbeiten.$b$bUm beginnen zu können, müssen wir aber zunächst ein paar Materialien sammeln.$b$bAuf den Verheerten Inseln gibt viele seltsame, exotische Tiere, mit deren Bälgen man zweifelsohne ein paar schöne Experimente durchführen kann. Und wir benötigen auch Schuppen für Kettenrüstungen.$b$bZieht bitte los und holt uns ein paar Materialien, damit wir anfangen können.', '', '', '', '', '', '', 22522),
(39261, 'deDE', 'Der Ruf der Illidari', 'Sprecht mit Kayn Sonnenzorn an Krasus'' Landeplatz.', '$n, Kayn möchte Euch sofort an Krasus'' Landeplatz sprechen.$B$B<Kor''vas beugt sich zu Euch und flüstert eindringlich.>$B$BIhr müsst Euch beeilen. Die Legion hat ihre Augen überall.', '', '', '', '', '', '', 22522),
(40643, 'deDE', 'Einbestellt zur Mondlichtung', 'Trefft Hamuul Runentotem auf der Mondlichtung.', 'Eure umgehende Anwesenheit auf der Mondlichtung wird erbeten, $n.$b$bWir bereiten ein äußerst wichtiges Ritual vor und benötigen Eure Unterstützung.$b$bIch werde Euch dort erwarten, also eilt Euch bitte!', '', '', '', '', '', '', 22522),
(40040, 'deDE', 'Teufelswurzprobe', 'Bringt die Teufelswurzprobe zu Kuhuine Sanftschreiter in Dalaran.', 'Dieses Kraut scheint bis in die Blattspitzen mit verderbter Energie erfüllt zu sein. Es fühlt sich kalt, glatt und trocken an, doch ist die Berührung ziemlich schmerzhaft.$b$bEure Kräuterkundelehrerin in Dalaran möchte sicher einen Blick darauf werfen.', '', '', '', '', '', '', 22522),
(40034, 'deDE', 'Sternlichtrosenstaub', 'Bringt den Sternlichtrosenstaub zu Kuhuine Sanftschreiter in Dalaran.', 'Trotz Eurer guten Ausbildung löst sich die Rose einfach in Eurer Hand auf.$b$bEure Kräuterkundelehrerin in Dalaran möchte sicher einen Blick darauf werfen.', '', '', '', '', '', '', 22522),
(40029, 'deDE', 'Fjarnskagglprobe', 'Bringt $1oa Proben Fjarnskaggl zu Kuhuine Sanftschreiter in Dalaran.', 'Wer sich mit der Kräuterkunde nicht sonderlich auskennt, könnte diesen Dornenbusch als komplett nutzlos erachten. Durch Eure Ausbildung wisst Ihr aber, dass es sich um eine sehr spezielle Pflanze handelt.$b$bEure Kräuterkundelehrerin in Dalaran wäre sicherlich an ein paar weiteren Proben interessiert.', '', '', '', '', '', '', 22522),
(40024, 'deDE', 'Fuchsblumenprobe', 'Sammelt $1oa Fuchsblumenproben und bringt sie dann zu Kuhuine Sanftschreiter in Dalaran.', 'Irgendetwas an diesem Kraut ist besonders... sagt Euch Eure Nase.$b$bEure Kräuterkundelehrerin in Dalaran wäre sicherlich an ein paar Proben dieser außergewöhnlichen neuen Pflanze interessiert.', '', '', '', '', '', '', 22522),
(40018, 'deDE', 'Traumlaubprobe', 'Bringt die Traumlaubprobe zu Kuhuine Sanftschreiter in Dalaran.', 'Dieses Kraut ist strahlend grün, papierdünn und etwas klebrig.$b$bEure Kräuterkundelehrerin in Dalaran hat sicherlich Interesse daran.', '', '', '', '', '', '', 22522),
(38795, 'deDE', 'Probe eines Teufelsschiefervorkommens', 'Bringt die Probe eines Teufelsschiefervorkommens zu Mama Buddel in Dalaran.', 'Das ist ein ziemlich finster aussehender Stein.$b$bIhr solltet ihn Eurer Lehrerin in Dalaran zeigen.', '', '', '', '', '', '', 22522),
(37654, 'deDE', 'Gesetze des Meeres', 'Sucht nach Looper Allen in der Nähe des Schiffes, das an der Ostküste von Mak''rana auf Grund gelaufen ist.', '$gWerter Herr:Werte Dame;, bitte gewährt mir einen Augenblick Eurer kostbaren Zeit. Meine Bergungskollegen und ich haben Euer Schiff bemerkt, das unglücklicherweise letzte Nacht im Sturm auf Grund gelaufen ist.\n\nTut mir sehr leid um die Crew.\n\n<Herr Fessel unterstreicht dies mit einer mitfühlenden Geste.>\n\nAber Gesetz ist Gesetz. Das Seeverkehrsrecht ist hier absolut eindeutig. Wenn Ihr trotzdem noch ein paar Sachen bergen wollt, wäret Ihr dann vielleicht so liebenswürdig, nach meinem Akquise-Scout Looper Ausschau zu halten? Er hätte schon vor einer Weile pünktlich zum Nachmittagstee zurück sein sollen.', '', '', '', '', '', '', 22522),
(38407, 'deDE', 'Flaschenpost', 'Bringt Okunas Nachricht zu Lady Irisse.', '<Ihr habt bei der Leiche eine Flaschenpost gefunden. Es ist ein Hilferuf von jemandem namens Okuna Langhauer. Okuna berichtet, dass er zusammen mit seinen Schiffskameraden von Seeriesen gefangen gehalten wird.\n\nVielleicht kann Euch ja Lady Irisse am Zerfallenen Palast zeigen, wo das ist, wenn Ihr ihr diesen Brief zeigt?>', '', '', '', '', '', '', 22522),
(44414, 'deDE', 'Teufelsbrut von Lothros', 'Bezwingt Lothros.', 'Diese Teufelsbrut nimmt einfach kein Ende! Ich kann sie nicht schnell genug niedermähen, um an den Schreckenslord im Mausoleum zu gelangen.\n\nMeister Illidan, werdet Ihr mich begleiten, damit wir Lothros gemeinsam zur Strecke bringen können?', '', '', 'Lothros', '', '', '', 22522),
(42985, 'deDE', 'Eine königliche Audienz', 'Meldet Euch bei Kriegshäuptling Sylvanas Windläufer in Unterstadt.', '$n,$B$Bwer nicht auf der Seite der Horde steht, stellt sich gegen sie. Als Champion habt Ihr solche Feinde zuhauf vernichtet.$B$BIch bewundere Eure Hingabe. Dank Euch werden wir zu ungeahnter Macht aufsteigen und die Allianz endgültig in die Schranken weisen.$B$BKommt so schnell wie möglich zu mir nach Unterstadt. Eure Siege verdienen eine angemessene Belohnung.$B$B- Kriegshäuptling Sylvanas Windläufer', '', '', 'Sylvanas Windläufer', '', '', '', 22522),
(38643, 'deDE', 'Ein Dorf in Gefahr', 'Sprecht mit Bürgermeister Heathrow in Bradenbach.', 'Der Leichnam von Darcy Heathrow liegt vor Euch, von Pfeilen durchbohrt und mit Stichwunden übersät.$b$bIn einer Hand umklammert sie eine Notiz, die sie mit letzter Kraft verfasst hat:$b$b"Die Toten marschieren nach Bradenbach.$b$bBitte helft uns!"', '', '', '', '', '', '', 22522),
(40122, 'deDE', 'Cenarius, Hüter des Hains', 'Sprecht mit Malfurion Sturmgrimm in Lorlathil in Val''sharah.', 'Ahh, Val''sharah.$b$bEs ist schon lange her, dass ich einen Fuß in diese heiligen Wälder gesetzt habe. Viel zu lange...$b$bUnsere Aufgabe hier ist einfach, $C. Wir müssen meinen Mentor, Cenarius, rufen und herausfinden, wo er die Tränen von Elune verwahrt. Sie werden Euch unserem Ziel näherbringen, Sargeras'' Grab zu versiegeln und die Legion aufzuhalten.$b$bAuf Euch warten schwere Prüfungen. Euer Aufenthalt hier ist lediglich eine Atempause von kurzer Dauer.$b$bKommt, das wird nicht viel Zeit in Anspruch nehmen.', '', '', '', '', '', '', 22522),
(40013, 'deDE', 'Aethrilprobe', 'Bringt die Aethrilprobe zu Kuhuine Sanftschreiter in Dalaran.', 'Dieses Kraut hat eindeutig die Magie des Landes absorbiert. Es lassen sich Spuren von Leyenergie von der Wurzel bis zum Blatt feststellen.$b$bEure Kräuterkundelehrerin in Dalaran hat sicherlich Interesse daran.', '', '', '', '', '', '', 22522),
(44701, 'deDE', 'Sturmheim', 'Sprecht mit Nathanos Pestrufer in der Violetten Zitadelle in Dalaran.', 'Gut, dass wir Euch gefunden haben, $n!\n\nNathanos Pestrufer, Champion der Bansheekönigin selbst, wünscht, Euch in Dalaran zu sprechen. Es klang dringend. Er wartet in der Violetten Zitadelle auf Euch.\n\nBeeilt Euch!', '', '', 'Lord Grayson Schattenbruch', '', '', '', 22522),
(39595, 'deDE', 'Blut und Gold', 'Sammelt 7 Tribute der Herausforderer im Tal des Schwertes.', 'Die im Tal des Schwertes wartenden Herausforderer haben Geschenke für den Wächter der Kammer mitgebracht. \n\nEs ist vielleicht eine gute Idee, einige davon an Euch zu nehmen, um Yotnar damit zu beeindrucken.', '', '', '', '', '', '', 22522),
(38777, 'deDE', 'Probe eines Leysteinvorkommens', 'Bringt die Probe eines Leysteinvorkommens zu Mama Buddel in Dalaran.', 'So eine Art Erz habt Ihr noch nie gesehen.$b$bDurch das Metall hindurch wandert ein mystischer Schimmer in aufwendigen, nicht nachvollziehbaren Mustern. Eure Lehrerin in Dalaran wäre daran sicher sehr interessiert.', '', '', '', '', '', '', 22522),
(40329, 'deDE', 'Kampfhaustierzähmer: Warlords', 'Besiegt fünf Kampfhaustiertrainer in Draenor.', 'Ihr habt bewiesen, dass ihr ein Meister eures Fachs seid, doch die Wildnis von Draenor verspeist Meister zum Frühstück. Zeigt, aus welchem Holz ihr wirklich geschnitzt seid und besiegt diese Haustierzähmer: Cymre Leuchtklinge in Gorgrond, Vesharr in den Spitzen von Arak, Tarr den Schrecklichen in Nagrand, Taralune in Talador, Gargra im Frostfeuergrat oder Ashlei im Schattenmondtal. Ihr könnt Euch auch Besuchern Eurer Garnison stellen: Erris der Sammlerin oder Kura Donnerhuf.$B$BEin Sieg über sie, wird Eure Überlegenheit in Draenor beweisen.', '', '', '', '', '', '', 22522),
(39532, 'deDE', 'Zwingt sie in die Knie', 'Tötet $oa Orcs der Eisernen Horde im Tanaandschungel.', 'Seit dem Fall der Schwarzfelsgießerei ist Gul''dan damit beschäftigt, die Eiserne Horde in etwas... schlimmeres zu verwandeln. Die Orcs, die sich bisher entweder aus Furcht oder Rachsucht um Grommash geschart hatten, geben sich jetzt der Dämonenmagie hin.$b$bTretet ihnen auf dem Schlachtfeld von Tanaan entgegen und dezimiert Gul''dans Truppen. Ob Orc oder Teufelsorc ist einerlei. Gebt der Eisernen Horde den Gnadenstoß!', '', '', '', '', '', '', 22522),
(39433, 'deDE', 'Unsichtbare Fänge', 'Erhaltet $oa teufelsverderbte Apexisfragmente aus einem Schatz, von einer der seltenen Kreaturen oder einer der seltenen Elitekreaturen im Tanaandschungel.', 'Die Verderbnis der Apexis beginnt, in meinem Volk um sich zu greifen. Ihre Energie ist uns so vertraut, dass täglich mehr Arakkoa ihrem Flüstern verfallen. Ihr müsst mir mehr Proben bringen, damit ich mein Volk davor bewahren kann, einem weiteren Fluch zum Opfer zu fallen.', '', '', '', '', '', '', 22522),
(39231, 'deDE', 'Die Höhe des Zerstörers', 'Vernichtet Mannoroth in der Höllenfeuerzitadelle.', 'Wir dachten, dass wir Mannoroth ausgeschaltet hätten – zumindest für eine ganze Weile. Aber Gul''dan ist mächtiger, als wir angenommen haben.\n\nMannoroth ist fast schon wieder zurück in dieser Welt. Seine Knochen wurden im Innern der Zitadelle gesichtet und er muss vernichtet werden, bevor er zu voller Stärke gelangen kann.', '', '', 'Mannoroth', '', '', '', 22522),
(44184, 'deDE', 'In einem flüchtigen Augenblick', 'Werdet Zeuge, wie Dalaran zu den Verheerten Inseln teleportiert wird.', 'Unser Ziel sind die Verheerten Inseln, $n.$b$bDer Zauber, der Dalaran innerhalb eines Lidschlags an seinen neuen Standort versetzt, ist nicht einfach. Ich brauche die volle Energie und Aufmerksamkeit dieses Rats.$b$bDa Jaina... abwesend ist, hat Kalecgos vom blauen Drachenschwarm uns seine Hilfe angeboten.$b$bDie Legion wird alles versuchen, um uns aufzuhalten, aber wir werden nicht klein beigeben. Es ist Zeit, anzufangen!', '', '', '', '', '', '', 22522),
(39565, 'deDE', 'Im Dschungel ist die Hölle los', 'Besiegt die drei Arenachampions von Fangri''la.', 'Diesen Katzenmenschen muss mal jemand zeigen, wer hier die Hosen anhat. Murkst all ihre Champions ab und alles was im Dschungel kreucht und fleucht wird Euch Respekt zollen.\n\nIhr werdet die Totems hier brauchen, um sie hervorzulocken.', '', '', '', '', '', '', 22522);
