INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(41176, 'deDE', 'Suche in den Trümmern', 'Sucht die verschiedenen Gegenden um Suramar herum ab, bis Ihr die $1oa verschwundenen Edelsteine der Krone gefunden habt.', 'Dann ist wohl vollkommen klar, was wir jetzt tun müssen: nämlich diese Edelsteine finden.$b$bSie müssen noch irgendwo hier in Suramar sein. Wir sollten gleich mit der Suche anfangen.$b$bIch werde die Suche von hier aus koordinieren, während Ihr und meine Assistenten Ausschau nach ihnen halten.', '', '', '', '', '', '', 22810),
(43521, 'deDE', 'Die Essenz der Macht', 'Sammelt $1oa verderbte Essenzen aus dem Smaragdgrünen Alptraum.', 'Jetzt, da wir die Möglichkeiten des Herzens besser verstehen, wird es Zeit, uns sein wahres Potenzial zunutze zu machen.$B$BIm Smaragdgrünen Alptraum findet Ihr Kreaturen, die es gar nicht geben dürfte... Monster, die nichts mit dem zu tun haben, wofür der Traum steht.$B$BSie widerstreben einfach allem, was dem grünen Schwarm heilig ist.$B$BJeder dieser Unholde trägt eine verderbte Essenz in sich, so wie auch Eichenherz es tat... nur noch stärker. Sammelt alle, derer Ihr habhaft werden könnt, und wir werden ihre eigene Verderbnis gegen sie wenden.', '', '', '', '', '', '', 22810),
(43520, 'deDE', 'In den Alpträumen', 'Beschafft eine Todesblickiris und das Horn des Alptraumfürsten aus dem Smaragdgrünen Alptraum.', 'Wir müssen alles Mögliche tun, damit der Alptraum wirklich nie wieder im Smaragdgrünen Traum schwärt.$B$BDie Entartung namens Il''gynoth befindet sich im Herzen der Verderbnis, eine lebendige, sich windende Manifestation der Schrecken, denen Ihr auf Eurer Reise an jeder Ecke begegnen werdet.$B$BIch bitte Euch, ihn ein für alle Mal zu zerstören... und den Alptraumfürsten Xavius gleich mit.$B$BSobald es vollbracht ist, bringt mir ein kleines Stück von beiden. Wir können sie untersuchen, um herauszufinden, warum der Alptraum so schnell gewachsen ist.', '', '', '', '', '', '', 22810),
(43362, 'deDE', 'Der Stoff der Träume', 'Sammelt geläuterte Lebensessenz während eines Schlachtzugs im Smaragdgrünen Alptraum.', 'Dieser Arcan''dor gehört zu einer uralten Art. Sie wurden nicht gezüchtet, sondern erschaffen. In ihnen verband sich das Arkane mit der primitiven Macht der Druiden zu etwas, das größer war als beide zusammen.\n\nDas Arkane wird ihn bald vollständig verschlungen haben.\n\nWir können das Gleichgewicht wiederherstellen, aber dazu bedarf es wilder Magie. Magie, die in einem anderen Reich gefangen ist.\n\nIhr müsst Euch in den Smaragdgrünen Traum begeben, in die unberührte und reine Vision von Azeroth, und die Essenz des Lebens hierher zurückbringen.\n\nNur dann wird sich der Arcan''dor wieder stabilisieren und erneut erblühen.', '', '', '', '', '', '', 22810),
(43926, 'deDE', 'Die Rückkehr der Legion', 'Meldet Euch bei Holgar Sturmaxt an der Blockade von Dranosh''ar.', 'Krieger der Horde, schleift Eure Äxte und schärft Eure Speere. Die Armeen der Brennenden Legion sind in diese Welt zurückgekehrt.\n\nWir laufen sofort aus der Messerbucht aus. Die ganze Macht der Horde wird die Legion wie ein Hammer treffen. Da kriegt sogar Sargeras Kopfschmerzen.\n\nAb auf die Schiffe, wir haben ''ne Menge Dämonenblut zu vergießen.', '', '', '', '', '', '', 22810),
(44563, 'deDE', 'Erlösung für die Gefallenen', 'Erreicht einen ehrfürchtigen Ruf bei den Nachtsüchtigen.', 'Die Zeit der Veränderung ist nahe, und die Nachtsüchtigen werden ihr Schicksal selbst in die Hand nehmen.$B$BIhr habt ihnen Hoffnung geschenkt und sie auf den Pfad der Erlösung geführt. Sie brauchen Euch nun mehr als je zuvor.$B$BSo geht es uns allen.', '', '', '', '', '', '', 22810),
(43517, 'deDE', 'Die Macht des Gefallenen', 'Beschafft eine verderbte Essenz von Eichenherz und besiegt den Schemen von Xavius im Finsterherzdickicht auf dem Schwierigkeitsgrad ''Mythisch''.', 'Sobald wir die richtigen Instrumente haben, können wir das Herz dazu verwenden, Euer Artefakt mit großer Macht zu tränken. Wir benötigen nur eine Quelle.$B$BTief im Finsterherzdickicht liegt der uralte Eichenherz, der einst ein großartiger und mächtiger Beschützer von Shaladrassil war. Bedauerlicherweise ist er dem Alptraum anheimgefallen, wie so viele seiner Art.$B$BAls die Dunkelheit sich in ihm festgesetzt hat, manifestierte sich eine Essenz der Verderbnis. Ich bitte Euch, diese Essenz zu beschaffen, damit wir von ihrer Macht profitieren und den edlen Eichenherz zur Ruhe betten können.', '', '', '', '', '', '', 22810),
(43314, 'deDE', 'Fürchtet den Zorn eines geduldigen Elfen', 'Betretet den Dungeon "Der Hof der Sterne" und tötet Berater Melandrus. Nehmt ihm seinen Zauberstein ab und bringt ihn Thalyssra.', 'Es hat sich eine Gelegenheit ergeben, $n.\n\nLy''leth hat dafür gesorgt, dass Ihr Zutritt zu einem Ball am Hof der Sterne erhaltet. Ich weiß nicht, ob uns dieses Unterfangen weiterhelfen wird, aber ich möchte Euch mit einer Aufgabe betrauen.\n\nBerater Melandrus und ich waren früher Verbündete. Zusammen hatten wir Elisandes Sturz geplant, aber als der Zeitpunkt gekommen war, hat er mich verraten.\n\nDie Wunde, die er mir mit seinem Dolch zugefügt hat, schmerzt mich noch immer. Ich selbst kann Suramar nicht betreten, um Rache zu nehmen, aber Ihr.\n\nRichtet ihn wie einen Verräter hin und bringt mir seinen Zauberstein.', '', '', '', '', '', '', 22810),
(43179, 'deDE', 'Die Kirin Tor von Dalaran', 'Schließt 4 Weltquests der Kirin Tor ab.', 'Unterstützt die Kirin Tor von Dalaran, indem Ihr 4 Weltquests abschließt.', '', '', '', '', '', 'Kehrt zu Kriegsmagier Silva in Dalaran zurück.', 22810),
(42491, 'deDE', 'Lang begrabenes Wissen', 'Besorgt die Leylinienkarte von Suramar aus dem Arkus.', 'Unterhalb von Suramar gibt es ein uraltes Netzwerk von Tunneln, das wir den Arkus nennen.\n\nDie Tunnel wurden vor einiger Zeit verlassen, als Teile der Höhle einzustürzen drohten. Niemand weiß, was seitdem dort unten geschieht.\n\nAber unabhängig davon, was sich dort unten noch so befindet, gibt es dort etwas, das uns langfristig den Erfolg sichern kann, wie ich glaube.\n\nVor langer Zeit hat mein Volk alle Leylinien in und um Suramar herum kartografiert. Diese Aufzeichnungen müssten sich noch immer in der Bibliothek des Arkus befinden.\n\nBitte sucht für mich nach dieser Karte.', '', '', '', '', '', '', 22810),
(42490, 'deDE', 'Den Arkus öffnen', 'Tötet Berater Vandros im Arkus.', 'Tief unter Suramar befinden sich einige stillgelegte Tunnel, die als der Arkus bezeichnet werden. Sie wurden aufgegeben, als eine Katastrophe den Betrieb der dortigen Manasammler in Mitleidenschaft zog.\n\nIch weiß nicht, was Euch dort unten erwartet, doch eins weiß ich.\n\nWir sind nicht die Einzigen, die an diesem Ort interessiert sind. Wenn Ihr genug Lärm macht, wird sicher einer der Berater erscheinen, an deren Seite ich Elisande diente.\n\nEr heißt Vandros. Tötet ihn, wenn Ihr könnt.\n\nSein Tod wird ein Triumph für uns sein, denn damit berauben wir Elisande um eine der Waffen aus ihrem Arsenal.', '', '', '', '', '', '', 22810),
(42446, 'deDE', 'Angesengte Federn', 'Tötet 8 tobende Sturmjäger im Sturmgepeitschten Vorgebirge.', 'Krah! Krah-krah!\n\n<Der Häuptling schlägt mit den Flügeln und zeigt nach draußen, wo die Sturmgroßdrachen die Rabenbären seines Stammes angreifen.\n\nEs ist offensichtlich, dass er Euch bittet, seinem Schwarm zu helfen.>', '', '', '', '', '', '', 22810),
(42445, 'deDE', 'Nithoggs Tribut', 'Sammelt 8 intakte Großhirschgeweihe und 16 makellose Adlerschwanzfedern im Sturmgepeitschten Vorgebirge.', 'Die Rabenbären scheinen damit begonnen zu haben, Materialien für eine Art Tributritual zu sammeln, aber die Sturmdrachen halten sie davon ab.\n\nOffensichtlich benötigen sie vollständig intakte Geweihe von den Großhirschen und lange Schwanzfedern von den Adlern in der Nähe.\n\nEs sollte nicht allzu lange dauern, die restlichen Materialien herbeizuschaffen.', '', '', '', '', '', '', 22810),
(42234, 'deDE', 'Die Valarjar', 'Schließt 4 beliebige Weltquests in Sturmheim ab.', 'Unterstützt die Vrykul von Sturmheim, indem Ihr 4 Weltquests abschließt.', '', '', '', '', '', 'Kehrt zu Valdemar Sturmsucher in Sturmheim zurück.', 22810),
(42170, 'deDE', 'Die Traumweber', 'Schließt beliebige 4 Weltquests in Val''sharah ab.', 'Unterstützt die Druiden von Val''sharah, indem Ihr 4 Weltquests abschließt.', '', '', '', '', '', 'Kehrt zu Sylvio Hirschhorn in Val''sharah zurück.', 22810),
(40991, 'deDE', 'Küchenexperimente', 'Holt 5 von Nomis Silbermakrelen und bringt sie ihm, damit er damit experimentieren kann.', 'Ich kann zwar kochen, aber ich kann nicht wirklich jagen... und angeln schon mal gar nicht.$b$bAber wenn Ihr mir die Zutaten bringt, werde ich sie verwenden, um neue Rezepte auszuprobieren.$b$bWir können ja mit den paar Fischen anfangen, die ich mir zusammenschnorren konnte. Ihr findet sie in dem Fass an der Rückseite der Küche.', '', '', '', '', '', '', 22810),
(38009, 'deDE', 'Angriff auf die Ruinen von Kra''nak', '', '', '', '', '', '', '', '', 22810),
(43806, 'deDE', 'Die Schlacht um die Verheerte Küste', 'Trefft Euch mit der dritten Flotte.', 'Die dritte Flotte ist bereits vor uns ausgelaufen, $n. Dieser Greif wird Euch dorthin bringen.\n\nViel Glück da draußen. Die Legion wird es uns bestimmt nicht leicht machen. Hoffen wir nur, dass König Varian die Lage im Griff hat.', '', '', '', '', '', '', 22810),
(44009, 'deDE', 'Eine Sternschnuppe', 'Findet das mysteriöse lichtgebundene Objekt.', 'Danke, dass Ihr gekommen seid, $ct $n. Es gibt da eine dringende Angelegenheit, die Eurer speziellen Talente bedarf.$B$BDer Orden der Silbernen Hand hat gemeldet, dass ein außergewöhnlich leuchtendes Objekt aus dem Teufelssturm aufgetaucht ist und im Moment auf die Küste von Suramar herabstürzt. Am meisten hat mich bei dem Bericht verblüfft, dass dieses Objekt "eine Schneise durch den Teufelssturm" selbst geschnitten hat.$B$BWürdet Ihr Euch das bitte ansehen? Ein Greif erwartet Euch bereits an Krasus'' Landeplatz.', '', '', '', '', '', '', 22810),
(38213, 'deDE', 'Suche nach Hinweisen', 'Durchsucht die Ruinen von Kra''nak nach Hinweisen.', 'Aktar hat erwähnt, dass er in den Ruinen Sethekk, Oger und sogar Dämonen gesehen hat, aber kein Zeichen von Iskar.$b$bWir werden dort reingehen müssen, wenn wir irgendeinen Hinweis finden wollen, und mir wäre es am liebsten, wenn jemand mit Euren Fähigkeiten die Führung übernehmen würde.$b$bNun, was meint Ihr?', '', '', '', '', '', '', 22810),
(39454, 'deDE', 'Angriff auf Kil''jaedens Thron', 'Greift die Truppen des Schattenrats auf Kil''jaedens Thron im Tanaandschungel an.', 'Der Schattenrat hat seine Dämonenmeister auf einem Berg zusammengerufen, den sie "Kil''jaedens Thron" nennen. Wir wissen, welche Verheerungen die Legion anrichten kann, wenn sie nicht rechtzeitig aufgehalten wird.\n\nBegebt Euch zu Kil''jaedens Thron und macht dieser Invasion ein Ende, bevor sie überhaupt richtig beginnt.', '', '', '', '', '', '', 22810),
(39448, 'deDE', 'Angriff auf die Ruinen von Kra''nak', '', '', '', '', '', '', '', '', 22810),
(38588, 'deDE', 'Angriff auf Kil''jaedens Thron', 'Greift die Truppen des Schattenrats auf Kil''jaedens Thron im Tanaandschungel an.', 'Der Schattenrat hat seine Dämonenmeister auf einem Berg zusammengerufen, den sie "Kil''jaedens Thron" nennen. Wir wissen, welche Verheerungen die Legion anrichten kann, wenn sie nicht rechtzeitig aufgehalten wird.\n\nBegebt Euch zu Kil''jaedens Thron und macht dieser Invasion ein Ende, bevor sie überhaupt richtig beginnt.', '', '', '', '', '', '', 22810),
(38569, 'deDE', 'Säuberung der Docks', 'Tötet $1oa Orcs der Eisernen Horde und $2oa Ogron.', 'Wir müssen diese Situation unter Kontrolle bekommen. Wir dachten, diese Docks wären vollkommen verlassen. Tatsächlich aber hat Gul''dan der Eisernen Horde befohlen, sich nach Tanaan zurückzuziehen. Und viele von ihnen nutzen diese Hafen, um von hier zu entkommen.$b$bWir müssen unserem Bergungsteam den Weg freimachen. Tötet sie alle! Alle Gegner, die wir hier abschlachten, stehen dann Gul''dan nicht mehr für seine widerwärtige Dämonenarmee zur Verfügung.', '', '', '', '', '', '', 22810),
(42193, 'deDE', 'Das Gjallarhorn', 'Sucht Svergan Sturmmantel.', 'Ihr habt Euch ein ums andere Mal als würdig erwiesen, $n. Ihr seid ohne Zweifel $gder:die; Richtige, um Odyns Armeen in der letzten Schlacht anzuführen.$B$BAber zunächst müsst Ihr das Gjallarhorn in Euren Besitz bringen, um damit unseren Verbündeten mitzuteilen, dass das Ende der Zeit naht.$B$BKönig Svergan Sturmmantel war der letzte lebende Hüter des Horns.$B$BSucht seinen Geist auf dem Feld der Gefallenen Könige in Sturmheim auf und fragt ihn nach dem Horn.', '', '', '', '', '', '', 22810),
(39500, 'deDE', 'Brunnen der Seelen', 'Sammelt 4 Seelenrückstände von Blutschatten in der heroischen Version der Höllenfeuerzitadelle.', 'Je mehr Diener Gul''dans Ihr besiegt, desto schwächer wird seine Kontrolle über diese dämonische Zitadelle und so kann ich Euch und Eure Verbündeten tiefer in diese dunklen Hallen teleportieren. Wenn ich nur ein paar Seelenrückstände hätte – vier, zum Beispiel – wäre das alles nicht nötig und ich könnte Euch einfach mit ihrer Macht direkt in die oberen Bereiche der Zitadelle schicken.', '', '', '', '', '', '', 22810),
(39278, 'deDE', 'Das verschollene Manifest', 'Sucht nach Herrn Matoms verschollener Warenlieferung.', 'Der überwiegende Teil des wasserfesten Schreibens ist durch einen Code geschützt. In der Ecke seht Ihr eine hastig hingekritzelte Notiz für einen gewissen Herrn Matom: "Wir wurden angegriffen... Wir konnten entkommen, aber das Schiff geht unter. Kommt und helft uns, bevor sie uns finden."$b$bDanach folgen ein paar Koordinaten zum gesunkenen Schiff. Herr Matom gehört definitiv zu den Leuten, bei denen man einen Gefallen gut haben sollte – und nicht anders herum.', '', '', '', '', '', '', 22810),
(39228, 'deDE', 'Tempel der Tyrannei', 'Vernichtet Tyrannin Velhari in der Höllenfeuerzitadelle.', 'Tyrannin Velhari war einst eine viel versprechende Verteidigerin, aber aus Gründen, die nur sie selbst kennt, hat sie sich den Schatten zugewandt.\n\nLöscht diese Eredar der Finsternis aus.', '', '', 'Tyrannin Velhari', '', '', '', 22810),
(39090, 'deDE', 'Tötet sie alle!', 'Tötet 200 gegnerische Spielercharaktere.', 'Findet sie, jagt sie, tötet sie. Erteilt unseren Feinden eine Lektion, die sie niemals vergessen werden.', '', '', '', '', '', '', 22810),
(38923, 'deDE', 'Vorherrschaft in Ashran', 'Besiegt die Allianz an den Ereignisstandorten in Ashran und besiegt Großmarschall Tremblade.', 'Die Allianz weigert sich, in Ashran nachzugeben. Zeigt ihr, dass die Horde nicht einfach tatenlos zusieht, während sie die Macht des alten Ogerreichs für ihre Zwecke an sich reißt.', '', 'Großmarschall Tremblade ist der Anführer der Allianztruppen in Ashran.', 'Großmarschall Tremblade', '', '', '', 22810),
(38499, 'deDE', 'Seltsame neue Erze', 'Sammelt 10 Brocken Leysteinerz.', 'Könnt Ihr das auch riechen, $gSchmied:Schmiedin;? Ahhhh... Das ist der Geruch einer Gelegenheit.$b$bDalaran hat uns an ganz neue Gestade voller mysteriöser Metalle gebracht. Ich kann hier nicht aus meinem Laden raus, aber Ihr... Ihr habt die Freiheit, so viel zu erkunden und abzubauen, wie Ihr wollt.$b$bWenn Ihr mir etwas Interessantes mitbringt, werde ich sehen, was wir daraus machen können.', '', '', '', '', '', '', 22810);
