INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(45160, 'deDE', 'Kubische Ströme', 'Beschafft 300 uraltes Mana aus Suramar.', 'Um den Einsatzzweck dieses Würfels herauszufinden, müssen wir ihn mit Energie versorgen. Hm, ich habe da eine Idee...$b$bDie Nachtgeborenen schätzen eine Substanz, die wir uraltes Mana nennen und die Ihr sicherlich kennt. Für solch ein altes Relikt ist es wohl genau das Richtige. Besorgt eine ausreichende Menge davon, damit wir versuchen können, ihn zum Laufen zu bringen.$b$bAber passt gut auf Euch auf in Suramar! Es wäre ja eine Schande, wenn dieser kuriose Würfel verloren ginge und erneut nur eine blasse Erinnerung bliebe.', '', '', '', '', '', '', 23360),
(43595, 'deDE', 'Ehrung der Gefallenen', 'Sammelt 8 uralte Klingen der Wellenskorn von den Kvaldir im Höllenschlundmorast.', 'Seelen sind nicht das Einzige, wonach Helya trachtet, und die Kvaldir sind jederzeit bereit, ihr zu dienen.\n\nUnter ihrem Raubgut befinden sich auch die ehrwürdigen Klingen der gefallenen Helden, die den Kampf gegen Helyas Schergen wagten. Die Klingen gehören an einen ehrenwerten Ort, nicht in die Hände der Feinde.\n\nHolt diese Klingen zurück. Für die Ehre der Wellenskorn!', '', '', '', '', '', '', 23360),
(40046, 'deDE', 'Die Untiefen plündern', 'Durchsucht die Schiffswracktrümmer im Höllenschlundmorast nach Havis Hinweis.', 'Die Kvaldir leben, um Odyn das Leben schwer zu machen. Hier verrichten sie ihr gehässiges Werk. Sie stehlen die Seelen der Lebenden, die bisher nicht gerichteten Seelen, und berauben Odyn so seiner Champions.\n\nSie bringen sie hierher, um sie zu Helya zu schicken, doch die Nebel verhüllen jeden Hinweis drauf, wie dies vonstattengeht.\n\nIhr scheint gut mit Nebel zurechtzukommen. Vielleicht könnt Ihr die Wracks in der Bucht untersuchen und die Antwort finden. Ihr Fremden scheint ein Talent dafür zu haben, auch den verborgensten Geheimnissen auf die Spur zu kommen.', '', '', '', '', '', '', 23360),
(40044, 'deDE', 'Schatten im Nebel', 'Besiegt Kapitän Sten, Sigrid, Kjell und Halvir im Höllenschlundmorast.', 'Viele der Kvaldir hier in der Bucht sind frühere Bewohner des Hafens, die von der Seehexe Helya verflucht wurden und ihr für alle Zeit zu Willen sein müssen.\n\nDie, von denen Ihr lest, wandeln immer noch durch das träge Wasser. Eine gütige Seele könnte ihnen mit der Spitze einer Klinge eine Atempause verschaffen.\n\nSagt, wie gütig ist Eure Seele?', '', '', '', '', '', '', 23360),
(39786, 'deDE', 'Keine zahnlose Wette', 'Erbeutet Steinzahns Kiefer von Steinzahn in der Steinnarbenschlucht.', 'Das zweite Ziel ist ein Basilisk namens Steinzahn. Er lebt am Ufer des nahe gelegenen Flusses.\n\nIch habe einen größeren Betrag auf ihn gesetzt. Wenn Ihr ihn also erlegen könnt, winkt Euch ein schöner Anteil.\n\nZum Beweis müsst Ihr mir seinen Kiefer bringen. Und beeilt Euch, bevor die Tauren ihn kriegen!', '', 'Ein gewaltiger Basilisk, der die Vrykul von Sturmheim terrorisiert.', 'Steinzahn', '', '', '', 23360),
(39341, 'deDE', 'Das Verlies der Wächterinnen: Dämonengalle', 'Stellt einen Skaggldrynk her, geht dann zum Verlies der Wächterinnen und taucht den Tiegel in Grimoiras Galle ein.', 'Um die Beschichtung des Tiegels zu veredeln, muss er mit der widerwärtigsten aller Teufelsenergien behandelt werden. Ich hasse es, das sagen zu müssen, aber die effektivste Methode dafür ist, ihn der Galle eines Dämons auszusetzen.$b$bJa, ich sagte Galle eines Dämons. Also, seid Ihr bereit?$b$bIm Verlies der Wächterinnen gibt es solch einen Dämon. Es ist eine Wichtelmutter namens Grimoira. Die Tür zu ihrem Zimmer ist mit einem unsichtbaren Schloss gesichert.$b$bNehmt am besten etwas Skaggldrynk mit und greift darauf zurück, wenn Ihr an der Tür angekommen seid.', '', 'Bringt auf jeden Fall etwas Skaggldrynk mit, um in ihr Zimmer zu gelangen.', 'Grimoira', '', '', '', 23360),
(40863, 'deDE', 'Mehr Saft', 'Sucht nach einer geeigneten Energiequelle für Didis Entwurf und bringt sie ihr. Testet dann, ob die neue Batterie auch funktioniert.', 'So, ich habe jetzt ein paar Sachen optimiert, indem ich ein paar neue Schrauben und Muttern verbaut und ein neues Gehäuse für den Kraftkern eingesetzt habe.$b$bDas Problem ist nur, dass keine meiner Batterien zu funktionieren scheint. Offensichtlich müssen wir uns eine eigene Lösung einfallen lassen.$b$bIch werde Eure Hilfe brauchen, $n. Als Erstes sollten wir einen aufgeladenen Edelstein aus dem Juwelierskunstladen die Straße runter ausprobieren.', '', '', '', '', '', '', 23360),
(40860, 'deDE', 'Nachschub für die Front', 'Bringt die Munitionskiste zu Fargo Flintlocke in Val''sharah.', '$n, ich möchte, dass Ihr etwas für mich ausliefert.$b$bFlintlocke hat aus Val''sharah gemeldet, dass ihnen dort die Kugeln ausgehen.$b$bIch selbst habe ihn mit genug Munition dort hingeschickt, um die halbe Welt in Stücke zu schießen. Aber Flintlocke scheint Munition zu brauchen wie andere... Luft zum Atmen. Also los, helft ihm aus der Patsche!', '', '', '', '', '', '', 23360),
(40370, 'deDE', 'Alles ins Reine bringen', 'Besiegt die Verderbnis des Azursturzwächters.', 'Spürt Ihr es auch, $R? Es ist jetzt so weit, dass wir alles wiedergutmachen können.$b$bWährend Ihr beschäftigt wart, habe ich den alten Elementar aufgespürt, der über diese Gewässer herrscht. Er heißt Azursturzwächter und lebt in den Kanälen von Tel''anor.$b$bNehmt den Ritualfokus, er wird Euch im Kampf nützlich sein.', 'Besiegt die Verderbnis des Azursturzwächters.', 'Verwendet den Reinigungsritualfokus, um die Quelle der Verderbnis zu finden.', 'Azursturzwächter', '', '', '', 23360),
(43361, 'deDE', 'Bruchstücke der Katastrophe', 'Sammelt 5 Stücke kristallisierte Rinde.', 'In der nächsten Halle ruhen die Überreste des gefallenen Arcan''dor.\n\nIch werde einige Fragmente des kristallisierten Baumes an mich nehmen und sie studieren. Wir müssen herausfinden, was hinter dieser Zerstörung steckt.\n\nNur so können wir vielleicht verhindern, dass sich diese Katastrophe wiederholt.', '', '', '', '', '', '', 23360),
(43360, 'deDE', 'Die Splittermaiden', 'Tötet 6 Splittermaiden im Epizentrum.', 'Ich durchstreife schon lange dieses Land und habe viele Arten der Verderbnis gesehen.\n\nDie Kreaturen in dieser Halle wurden von der Magie des gefallenen Arcan''dor verderbt. Die Dryaden sind vom Weg abgekommen und können nicht gerettet werden.\n\nIhr müsst sie vernichten und ihr Leiden beenden.', '', '', '', '', '', '', 23360),
(40041, 'deDE', 'Teufelswurzanalyse', 'Sammelt Ryno Blumenfelds, Dani Gründaums und Lohors Analysen ein.', 'Ich habe Teufelswurzproben an einige Kräuterkundekollegen geschickt, doch noch keine Nachricht von ihnen erhalten. Würdet Ihr sie aufsuchen und mir ihre Analysen bringen?$b$bRyno Blumenfeld arbeitet zurzeit im Krater von Un''Goro.$b$bDani Gründaum erforscht gerade Freyas Einfluss im Sholazarbecken.$b$bUnd Lohor war in der überwucherten Gegend von Gorgrond beschäftigt.', '', '', '', '', '', '', 23360),
(40037, 'deDE', 'Das Spatenblatt', 'Findet das zerbrochene Spatenblatt eines Kräuterkundigen.', 'Habt Ihr die Inschrift auf dem Stiel gesehen, $n? Da steht "Kräuterkundiger Tharillon" in der Schrift der Nachtgeborenen.$b$bMit dem Stiel können wir nicht viel anfangen, aber vielleicht können wir mithilfe des Blattes lernen, die Sternlichtrose erfolgreicher abzuernten.$b$bIch würde mit der Suche in Suramar beginnen.', '', '', '', '', '', '', 23360),
(45417, 'deDE', 'Die Nachtfestung: Herr des Schattenrats', 'Besiegt Gul''dan in der Nachtfestung.', 'Gul''dan missbraucht in diesem Augenblick die Macht des Nachtbrunnens, um Sargeras in unsere Welt zu holen.\n\nVersammelt Eure Gefährten und kämpft Euch zur Spitze der Nachtfestung durch.\n\nIch werde das Herz des Lichts vorbereiten und zu Euch stoßen, wenn die richtige Zeit gekommen ist.\n\nZusammen werden wir Gul''dan ein für alle Mal zur Strecke bringen.', '', '', 'Gul''dan', '', '', '', 23360),
(45352, 'deDE', 'Von Titanen berührt', 'Bringt $1oa Essenzen von Aman''thul zu Arkanomant Vridiel.', 'Meine Güte! Ihr habt es geschafft, Euch einen legendären Gegenstand für Eure nächsten Abenteuer zu beschaffen. Ich hatte schon lange die Theorie, dass solche Gegenstände ihre Macht direkt von den Titanen beziehen. Das hier könnte sich als Gelegenheit erweisen, diese Spekulationen einem Test zu unterziehen!$b$bEs heißt, dass der Nachtbrunnen seine Macht aus dem Auge von Aman''thul bezieht. Ich glaube, dass die Macht der Titanen nun nach Jahrhunderten in unmittelbarer Nähe den Bewohnern der Nachtfestung innewohnt. Bringt mir Überreste dieser Energie, dann werde ich Eure Ausrüstung mit Macht erfüllen.', '', '', '', '', '', '', 23360),
(43548, 'deDE', 'In den Alptraum: Il''gynoth', 'Bezwingt Il''gynoth im Smaragdgrünen Alptraum.', 'Unsere Champions haben die Quelle der Verderbtheit entdeckt. Die Legion ist in den Smaragdgrünen Traum eingefallen, und wenn wir unsere Bemühungen nicht darauf konzentrieren, sie aufzuhalten, wird das nur der Anfang der Verderbnis sein. Sie muss jetzt aufgehalten werden.', '', '', '', '', '', '', 23360),
(42639, 'deDE', 'Ein blutiger Stein', 'Holt Bruchzahns Rubinamulett von Kapitän Bruchzahn auf den Schiffen nahe Dreyrgrot.', 'Leider haben wir vielleicht noch ein weiteres Problem. Der aus den Ruinen aufsteigende Nebel scheint die plündernden Piraten befallen und sie gehörig in den Wahnsinn getrieben zu haben.\n\nNur einer von ihnen ist offensichtlich nicht davon betroffen: ein Kerl mit einem schneidigen Hut, bei dem es sich um einen Kapitän zu handeln scheint. Um den Hals trägt er ein Rubinamulett, das ihn meiner Meinung nach vor dem Nebel schützt. Wenn Ihr die Grabstätte betreten wollt, braucht Ihr dieses Amulett.\n\nAber er wird es wohl nicht freiwillig herausrücken.', '', '', '', '', '', '', 23360),
(42635, 'deDE', 'Das Geheimnis von Dreyrgrot', 'Beschafft 4 uralte Schrifttafeln von Dreyrgrot von den Piratenschiffen außerhalb von Dreyrgrot.', 'Seid gegrüßt!\n\nIch war auf dem Weg, um die örtlichen Murlocs zu studieren, als ich über ein paar sehr seltsame Ruinen gestolpert bin. Allerdings sind diese Piraten dort hinten vor uns eingetroffen und haben augenscheinlich irgendein uraltes böses Wesen freigelassen.\n\nEin solch bösartiges Geschöpf darf nicht frei herumlaufen, aber vielleicht sollten wir erst etwas darüber in Erfahrung bringen, damit wir nicht wie diese Kerle enden.\n\nIch habe beobachtet, wie die Crew ein paar Tafeln aus den Ruinen geholt hat. Wenn Ihr mir diese Tafeln bringen könnt, werde ich versuchen, dieses bösartige Wesen zu identifizieren.', '', '', '', '', '', '', 23360),
(42517, 'deDE', 'Ritual der Verdammnis', 'Führt das Ritual der Verdammnis auf der Beschwörungsplattform in der Schreckensnarbe durch.', 'Eure Nachforschungen haben Euch Zugang zu einem sehr mächtigen Ritual verschafft. Aber wenn Ihr damit einen Dämon beschwören wollt, braucht Ihr die Hilfe einiger Eurer Freunde.\n\nDas Ritual muss oben auf der Beschwörungsplattform durchgeführt werden.\n\nVergesst nicht, dass man für diese Art von Macht stets einen Preis bezahlt. Nicht alle, die an dem Ritual teilnehmen, werden auch überleben. Bereitet Euch gut vor.', '', '', '', '', '', '', 23360),
(42444, 'deDE', 'Die Notlage der Schwarzfedern', 'Folgt dem verängstigten Rabenbären zum Häuptling der Schwarzfedern.', '<Der Rabenbär gestikuliert aufgeregt und zeigt den Pfad hinunter.\n\nEr möchte offenbar, dass Ihr ihm folgt.>', '', '', '', '', '', '', 23360),
(40613, 'deDE', 'Der Seelenschlund: Die Rettung des Svalngards', 'Beschafft den Svalngard und besiegt Helya im Seelenschlund auf dem Schwierigkeitsgrad ''Mythisch''.', 'Einst war der Svalngard Odyns Schild. Es wird behauptet, dass er die Hallen der Tapferkeit vor der endlosen Dunkelheit unter seinem Reich geschützt hat.$B$BHelya, die vor langer Zeit aus den glänzenden Hallen vertrieben wurde, stahl den Svalngard und verbarg ihn in ihrem verfluchten Schiff, der Naglfar.$B$BIhr müsst Euch an Bord der Naglfar begeben und Helya besiegen, um den Svalngard wiederzubeschaffen. Er wird einen würdigen Tribut für Odyn abgeben.', '', '', '', '', '', '', 23360),
(40173, 'deDE', 'Das instabile Prisma', 'Bringt das kalte zeitverzerrte Prisma zu einem interessierten Magier in Dalaran.', 'Das Prisma scheint sich nicht in einem einzelnen Zeitstrom stabilisieren zu können, wodurch es eine schattenhafte Kälte verströmt, wie Ihr sie seit Nordend nicht mehr gespürt habt. Für einen Magier aus Dalaran wäre dieses Relikt sicher von höchstem Interesse.', '', '', '', '', '', '', 23360),
(39843, 'deDE', 'Das Herumschmutzen endet hier', 'Beschafft 20 Phiolen Zauberschlacke.$b$bZauberschlacke kann bei den machtvolleren Kreaturen der Schattenseite gefunden werden, die erscheinen, wenn die Kanalwächter von Dalaran im Dienst sind.', 'Für meinen nächsten Arbeitsschritt, die Komponenten der Schmiede zusammenzufügen, benötige ich einen magischen Klebstoff. Und ich habe herausgefunden, dass die angestaute Schlacke aus der Kanalisation von Dalaran geradezu perfekt dafür geeignet ist.$b$bSie setzt sich an den Füßen, Stiefeln und schlängelnden Bäuchen der langlebigeren Bewohner von Dalaran ab. Kratzt etwas davon ab und bringt sie her.', '', '', '', '', '', '', 23360),
(39842, 'deDE', 'Des einen Magiers Leid...', 'Findet eine Phiole mit alchemistischer Flamme, eine Rolle bezauberter Isolierung und eine stillgelegte Heizung.$b$bDiese Gegenstände können nur beschafft werden, wenn die Kanalwächter von Dalaran außer Dienst sind.', 'Ich kann Euch helfen, Eure Schmiede zu bauen. Ich kann sogar das Metall beisteuern. Jedoch benötige ich einige... schwieriger zu beschaffende Komponenten.$b$bIch brauche eine alchemistische Wärmequelle, eine Art Isolierung und eine Heizung. Ich bin mir sicher, dass all das hier in der Schattenseite aufgetrieben werden kann... wenn die Wachen gerade nicht hinsehen.', '', '', '', '', '', '', 23360),
(43598, 'deDE', 'Angriff auf den Turm der Wächterinnen: Das Schwarzfalkenbollwerk', '', '', '', '', '', '', '', '', 23360);
