INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(42104, 'esES', 'Viene el Infrarrey', 'Evacua Recodo del Río.', 'No soy lo bastante veloz para poner a salvo a estos rezagados. Toma mi cuerno y da la señal de evacuación.$B$BTenemos que sacar a la gente de aquí antes de que lleguen los drógbar. Los guerreros bravos se encargarán del resto.$B$BPor ahora limítate a enviárselos al caminaespíritus Cuerno de Ébano. Ese insensato puede cuidar de ellos hasta que pase el peligro.', '', '', '', '', '', '', 22566),
(39498, 'esES', 'Marea alta', 'Habla con Jale Crin de Río en Recodo del Río.', '¡Recodo del Río! ¿Eso dijo el Infrarrey?$B$BEs mi hogar y el sitio donde almacenamos nuestras provisiones para el invierno. Debemos ir allí a toda prisa y avisarlos.$B$BNos vemos allí.', '', '', '', '', '', 'Vuelve con: Jale Crin de Río. Zona: Recodo del Río.', 22566),
(39487, 'esES', 'Furia de cristal', 'Mata a Gelmogg.', 'Un taumaturgo de piedra drógbar conocido como Gelmogg entró en nuestras cavernas de agua sagrada y pretende extraer su poder para sí.\n\nDebemos destruirlo antes de que se vuelva demasiado poderoso.\n\nSe encuentra en las cavernas superiores.', '', '', 'Gelmogg', '', '', '', 22566),
(39489, 'esES', 'Espeleólogos invasores', 'Mata a $1oa drógbar sedientos de maná o comerriscos violentos.', 'Los drógbar y sus furias terráneas han invadido la caverna. Están pervirtiendo la magia de su interior y destruyendo a los elementales que han habitado aquí desde siempre.\n\nColaboremos para sacarlos de esta cueva sagrada.', '', '', '', '', '', '', 22566),
(39488, 'esES', 'El equilibrio de los elementos', 'Recupera $1oa cristales potenciados.', 'Los drógbar colocan sus cristales malignos en nuestra cueva para absorber la magia de dentro, y luego los recogen cuando están saturados de poder.\n\nDebemos hacernos con los cristales para que la magia pueda disiparse y bañe de nuevo las aguas; si no, los drógbar encontrarán formas de corromper la magia para sus propios intereses.', '', '', '', '', '', '', 22566),
(39661, 'esES', 'Caverna Brote de Vida', 'Reúnete con Jale Crin de Río en la Caverna Brote de Vida.', 'Los drógbar han tomado mi caverna. Es el lugar donde los Crin de Río practicamos nuestra magia de agua y obtenemos gran parte del agua sanadora más pura de Monte Alto.\n\nReúnete conmigo allí y limpiaremos de habitantes de la oscuridad ese lugar sagrado.', '', '', '', '', '', '', 22566),
(39572, 'esES', 'La aventura de Mugi', 'Lleva a Mugi con Sella Agua Sabia, en Playa Aguablanca.', '<Mugi flota río abajo y debe reunirse con su madre.>', '', '', '', '', '', '', 22566),
(39323, 'esES', 'Estadía de Mugi', 'Encuentra a Mugi río abajo en Playa Aguablanca.', '¡Mi hijo! ¡Se lo han llevado! Mi mujer y yo nos separamos para buscarlo. Ella fue río arriba y yo río abajo.\n\nDéjame, por favor, y encuentra a Mugi... Puede que a mí no me pase nada... Si das con Mugi, llévaselo a mi esposa río arriba.', '', '', '', '', '', '', 22566),
(39274, 'esES', 'Defender las riberas', '', '', '', '', '', '', '', '', 22566),
(38567, 'esES', 'Campaña de la ciudadela: Consejo de guerra', 'Habla con el Jefe de Guerra Vol''jin en el concejo de tu ciudadela.', 'Tu ciudadela está alborotada. El mismísimo Jefe de Guerra Vol''jin está en tu fortaleza para tratar tu plan de invasión de la Selva de Tanaan.$b$bEl Jefe de Guerra está preparado para recibirte en el concejo. No sería conveniente hacerle esperar...', '', 'El Jefe de Guerra espera.', '', '', '', 'Habla con: Jefe de Guerra Vol''jin. Zona: Concejo, en tu ciudadela.', 22566),
(43182, 'esES', 'El recipiente perdido', 'Interactúa con el mapa de exploración y completa las misiones de "El recipiente perdido" para reunir los fragmentos de vasija destrozada.', 'Mientras Hakkar siga libre, ningún mago estará a salvo.$b$bMalífica Tormenta de Maná creó un recipiente que succiona la esencia de los magos y vincula su magia al objeto. Se ideó como un arma, pero podemos usarla como cebo.$b$bSi canalizo mi esencia al recipiente, se convertirá en un objeto de magia pura.$b$bCuando la capturaron, Malífica rompió el recipiente y permitió que cayera en manos de nuestros enemigos.$b$bEncuentra los pedazos y vuelve conmigo. ¡Diseñaremos una trampa a la que Hakkar no podrá resistirse!', '', '', '', '', '', '', 22566),
(42655, 'esES', 'Mena bajo el mar', 'Explota los depósitos marítimos frente a las costas de Suramar y recoge mena azur.', 'Si queremos derrotar a Hakkar, mis forestales han de hacerse con flechas y municiones capaces de perforar la piel gruesa y resistente a la magia de sus canes.$b$bHay un versátil herrero en Dalaran que hace un trabajo increíble tanto con arcos como con hojas.$b$bPara sus creaciones emplea mena azur, que solo se encuentra en las profundidades marinas.$b$bDebido a su gran parecido con la piedra Ley, suele pasar desapercibida. Sin embargo, en manos de un herrero experto, puede fundirse para convertirla en un material casi indestructible.', '', '', '', '', '', '', 22566),
