(42169, 'esES', 'Dado por muerto', '', '', '', '', '', '', '', '', 22566),
(42795, 'esES', 'SE BUSCA: Sanaar', '', '', '', '', '', '', '', '', 22566),
(43325, 'esES', 'Raza Ley', '', '', '', '', '', '', '', '', 22566),
